INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1049, 1, '2', 'Articolul 2', NULL, NULL, NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '├Än sensul prezentei legi, termenii ╚Öi expresiile de mai jos au urm─âtoarele semnifica╚¢ii:a)aprobare de dezvoltare a centralei electrice eoliene offshore - act asimilat, din punctul de vedere al efectelor juridice, autoriza╚¢iei de construire, reprezent├ónd autoriza╚¢ia de construire/desfiin╚¢are pentru lucr─ârile efectuate ├«ntr-un perimetru offshore determinat, precum ╚Öi pentru cele efectuate pe uscat, p├ón─â la punctul de racordare cu re╚¢eaua de transport al energiei electrice, acordat─â de c─âtre Autoritatea Competent─â de Reglementare a Opera╚¢iunilor Petroliere Offshore la Marea Neagr─â concesionarului unui perimetru eolian offshore;...b)autoritate competent─â pentru concesionarea perimetrelor offshore - Ministerul Energiei;...c)autoritate competent─â pentru emiterea aprob─ârii de dezvoltare/desfiin╚¢are a centralelor electrice eoliene offshore -Autoritatea Competent─â de Reglementare a Opera╚¢iunilor Petroliere Offshore la Marea Neagr─â, denumit─â ├«n continuare ACROPO;...d)autoritate competent─â pentru emiterea autoriza╚¢iei de ├«nfiin╚¢are ╚Öi a licen╚¢ei de producere a energiei electrice pentru activit─â╚¢ile supuse regimului autoriz─ârii - Autoritatea Na╚¢ional─â de Reglementare ├«n Domeniul Energiei, denumit─â ├«n continuare ANRE, conform prevederilor art. 8 din Legea energiei electrice ╚Öi a gazelor naturale nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare;...e)autoriza╚¢ie de ├«nfiin╚¢are - autoriza╚¢ie emis─â de c─âtre ANRE pentru realizarea unor capacit─â╚¢i energetice de producere a energiei electrice, ├«n conformitate cu prevederile art. 8-10 din Legea nr. 123/2012,  cu modific─ârile ╚Öi complet─ârile ulterioare;...f)cablu subacvatic - cablu instalat pe fundul m─ârii, utilizat pentru transportul energiei electrice produse ├«n centralele electrice eoliene offshore;...g)central─â electric─â eolian─â offshore - capacitate de producere a energiei electrice situat─â ├«ntr-o zon─â a perimetrului eolian offshore concesionat, care grupeaz─â mai multe turbine eoliene ╚Öi instala╚¢ii electrice aferente, cu scopul de a transforma energia eolian─â primar─â ├«n energie electric─â;...h)concesionar - operatorul economic care a ├«ncheiat cu Ministerul Energiei un contract de concesiune pentru explorarea perimetrului eolian offshore ╚Öi exploatarea ulterioar─â a acestui perimetru prin construirea ╚Öi exploatarea unei centrale electrice eoliene offshore;...i)contract de concesiune - contract ├«ncheiat ├«ntre Ministerul Energiei ╚Öi concesionar ├«n vederea desf─â╚Öur─ârii activit─â╚¢ilor specifice de explorare a perimetrului eolian offshore, respectiv de exploatare a acestui perimetru prin construirea ╚Öi exploatarea centralelor electrice eoliene offshore;...j)desfiin╚¢are - retragerea din exploatare a centralei electrice eoliene offshore, ├«n baza autoriza╚¢iei ACROPO, concretizat─â prin lucr─âri privind demontarea construc╚¢iilor ce o alc─âtuiesc, gestionarea de╚Öeurilor rezultate ╚Öi repunerea perimetrului ├«n situa╚¢ia anterioar─â exploat─ârii ╚Öi construirii centralei electrice eoliene offshore;...k)dezvoltatorul centralei electrice eoliene offshore - operatorul economic care a ├«ncheiat contract de concesiune pentru explorarea unui perimetru eolian offshore ╚Öi pentru exploatarea acestui perimetru prin construirea ╚Öi exploatarea centralei electrice eoliene offshore, acesta reprezent├ónd gestionarul instala╚¢iei de producere a energiei electrice, ├«n conformitate cu prevederile art. 2 pct. 7 din Regulamentul (UE) 2016/631 al Comisiei din 14 aprilie 2016 de instituire a unui cod de re╚¢ea privind cerin╚¢ele pentru racordarea la re╚¢ea a instala╚¢iilor de generare;...l)licen╚¢─â de producere a energiei electrice - licen╚¢─â acordat─â de c─âtre ANRE unui operator economic pentru exploatarea comercial─â a capacit─â╚¢ilor de producere a energiei electrice ╚Öi, dup─â caz, a instala╚¢iilor de stocare a energiei ad─âugate capacit─â╚¢ilor de producere respective;...m)offshore - situat ├«n apele maritime interioare, marea teritorial─â, ├«n zona contigu─â ╚Öi ├«n zona economic─â exclusiv─â sau pe platoul continental al M─ârii Negre, ├«n sensul dispozi╚¢iilor Conven╚¢iei Na╚¢iunilor Unite asupra dreptului m─ârii, ├«ncheiat─â la Montego Bay (Jamaica) la 10 decembrie 1982, ratificat─â prin Legea nr. 110/1996, potrivit dispozi╚¢iilor art. 2 pct. 24 din Legea nr. 165/2016 privind siguran╚¢a opera╚¢iunilor petroliere offshore, cu modific─ârile ╚Öi complet─ârile ulterioare;...n)operator economic - persoan─â juridic─â de na╚¢ionalitate rom├ón─â sau str─âin─â care particip─â la procedurile organizate de autoritatea competent─â pentru ob╚¢inerea concesiunii, autoriza╚¢iilor, licen╚¢elor ├«n vederea explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore ╚Öi care este ├«nfiin╚¢at─â ╚Öi func╚¢ioneaz─â ├«n condi╚¢iile Legii societ─â╚¢ilor nr. 31/1990, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, ori asocierea unor astfel de persoane juridice; documentul de asociere trebuie s─â cuprind─â, ├«n mod obligatoriu, urm─âtoarele elemente: capacitatea tehnic─â ╚Öi financiar─â a celui cu care se ├«ncheie asocierea, obiectul asocierii, modul de delimitare a drepturilor ╚Öi obliga╚¢iilor asocia╚¢ilor;...o)organism de verificare independent─â - entitate care nu se afl─â sub controlul direct sau indirect al concesionarului perimetrului eolian offshore ╚Öi care execut─â o evaluare sau o validare a documenta╚¢iei tehnice aferente proiectului de construire a centralei electrice eoliene offshore, at├ót pentru faza de proiectare, c├ót ╚Öi pentru faza 