INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1073, 1, '26', 'Articolul 26', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, '├Än cadrul planurilor de dezvoltare RET, elaborate ├«ncep├ónd cu data de 1 ianuarie 2025, operatorul de transport ╚Öi sistem asigur─â elaborarea de studii de sistem pentru a analiza poten╚¢ialul impact al unor mari cantit─â╚¢i de energie eolian─â offshore asupra sistemului energetic na╚¢ional, ├«n conformitate cu cele mai bune practici interna╚¢ionale, ╚Öi analizeaz─â impactul pe care cerin╚¢ele de racordare a centralelor eoliene offshore ├«l au asupra sistemului energetic na╚¢ional.', NULL, 26, '2025-11-14 21:57:25.34042', '2025-11-14 21:57:25.34042', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1074, 1, '27', 'Articolul 27', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, '(1)...Termenii ╚Öi condi╚¢iile pentru racordarea la re╚¢ea sunt stabili╚¢i ├«n cadrul avizului tehnic de racordare emis de operatorul de transport ╚Öi sistem....(2)...Operatorul de transport ╚Öi sistem analizeaz─â ╚Öi avizeaz─â ├«ndeplinirea condi╚¢iilor tehnice de racordare la re╚¢elele electrice de transport ├«n conformitate cu prevederile reglement─ârilor ├«n vigoare....(3)...Sub condi╚¢ia respect─ârii cerin╚¢elor de racordare, operatorul de transport ╚Öi sistem este obligat s─â conecteze centrala electric─â eolian─â la re╚¢ea ├«n conformitate cu prevederile legale ├«n vigoare....(4)...Obliga╚¢iile dezvoltatorului centralei electrice eoliene offshore ├«n leg─âtur─â cu instala╚¢iile de transport al energiei electrice c─âtre punctul de racordare la re╚¢eaua de transport al energiei electrice trebuie s─â respecte specifica╚¢iile anun╚¢ului de concesionare a perimetrului eolian offshore.', NULL, 27, '2025-11-14 21:57:25.34042', '2025-11-14 21:57:25.34042', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1075, 1, '28', 'Articolul 28', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, '(1)...Dezvoltatorul centralei electrice eoliene offshore trebuie s─â dezvolte, pe cheltuial─â proprie, re╚¢eaua intern─â a centralei electrice eoliene offshore, a sta╚¢iilor de conversie, de transformare ╚Öi de conexiune, precum ╚Öi cablurile sau liniile de transport al curentului continuu ├«ntre sta╚¢iile de conversie, dup─â caz, p├ón─â la punctul de racordare la re╚¢eaua de transport al energiei electrice, care poate fi offshore sau onshore....(2)...Dezvoltatorul centralei electrice eoliene offshore este responsabil pentru exploatarea instala╚¢iilor situate p├ón─â la punctul de racordare la re╚¢eaua de transport al energiei electrice....(3)...Prin certificatul de racordare emis de c─âtre operatorul de transport ╚Öi sistem ╚Öi prin conven╚¢ia de exploatare ├«ncheiat─â de c─âtre acesta cu dezvoltatorul centralei electrice eoliene offshore se stabilesc, ├«n func╚¢ie de punctul de racordare, delimit─ârile instala╚¢iilor, precum ╚Öi condi╚¢iile de exploatare ╚Öi ├«ntre╚¢inere a acestora.', NULL, 28, '2025-11-14 21:57:25.34042', '2025-11-14 21:57:25.34042', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1076, 1, '29', 'Articolul 29', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, '(1)...├Än cazul ├«n care concesionarul, dezvoltator al centralei electrice eoliene offshore, nu reu╚Öe╚Öte s─â construiasc─â ╚Öi s─â conecteze centrala la re╚¢ea, ├«n conformitate cu licen╚¢ele/autoriza╚¢iile acordate, este considerat responsabil pentru daunele suferite de operatorul de transport ╚Öi sistem ca urmare a ac╚¢iunilor sale. O astfel de r─âspundere nu va fi incident─â dac─â proprietarul centralei electrice eoliene offshore dovede╚Öte c─â ├«nt├órzierile nu s-au produs din culpa sa....(2)...Operatorul de transport ╚Öi sistem r─âspunde pentru daunele suferite de concesionar, dezvoltator al centralei electrice eoliene offshore, dac─â nu respect─â termenul-limit─â pentru racordarea la re╚¢ea ├«n situa╚¢ia ├«n care se dovede╚Öte c─â ├«nt├órzierile s-au produs din culpa sa....(3)...P├ón─â la data de 31 decembrie 2024, ANRE emite modele de aviz tehnic de racordare ╚Öi contracte de racordare la re╚¢ea, precum ╚Öi certificate de racordare specifice centralelor electrice eoliene offshore ╚Öi modific─â ├«n mod corespunz─âtor reglement─ârile necesare....(4)...Redispecerizarea de c─âtre operatorul de transport ╚Öi sistem a centralelor electrice eoliene offshore se face ├«n condi╚¢iile prevederilor art. 13 din Regulamentul (UE) 2019/943 al Parlamentului European ╚Öi al Consiliului din 5 iunie 2019 privind pia╚¢a intern─â de energie electric─â ╚Öi ale reglement─ârilor de pu