INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1102, 6, '2', 'Articolul 2', NULL, 'Energia electric─â', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, 'Obiectivele activit─â╚¢ilor din domeniul energiei electrice ╚Öi al energiei termice produse ├«n cogenerare...Activit─â╚¢ile din domeniul energiei electrice ╚Öi al energiei termice produse ├«n cogenerare trebuie s─â se desf─â╚Öoare pentru realizarea urm─âtoarelor obiective de baz─â:a)asigurarea dezvolt─ârii durabile a economiei na╚¢ionale;...b)diversificarea bazei de resurse energetice primare;...c)crearea ╚Öi asigurarea func╚¢ion─ârii pie╚¢elor concuren╚¢iale de energie electric─â;...d)asigurarea accesului nediscriminatoriu ╚Öi reglementat al tuturor participan╚¢ilor la pia╚¢a de energie electric─â ╚Öi la re╚¢elele electrice de interes public;...e)transparen╚¢a tarifelor, pre╚¢urilor ╚Öi taxelor la energie electric─â ├«n cadrul unei politici de tarifare, urm─ârind cre╚Öterea eficien╚¢ei energetice pe ciclul de producere, transport, distribu╚¢ie ╚Öi utilizare a energiei electrice;...f)constituirea stocurilor de siguran╚¢─â la combustibilii necesari pentru producerea energiei electrice, precum ╚Öi a energiei termice produse ├«n cogenerare;...g)asigurarea func╚¢ion─ârii interconectate a SEN cu sistemele electroenergetice ale ╚¢─ârilor vecine ╚Öi cu sistemele electroenergetice din Re╚¢eaua European─â a Operatorilor de Transport ╚Öi Sistem de Energie Electric─â - ENTSO-E, denumit─â ├«n continuare ENTSO-E;...h)├«mbun─ât─â╚¢irea competitivit─â╚¢ii pie╚¢ei interne de energie electric─â ╚Öi participarea activ─â la formarea at├ót a pie╚¢ei regionale, c├ót ╚Öi a pie╚¢ei interne de energie a Uniunii Europene ╚Öi la dezvoltarea schimburilor transfrontaliere;...i)promovarea utiliz─ârii surselor noi ╚Öi regenerabile de energie;...j)asigurarea protec╚¢iei mediului la nivel local ╚Öi global, ├«n concordan╚¢─â cu reglement─ârile legale ├«n vigoare;...k)asigurarea m─âsurilor de securitate ├«n vederea prevenirii ╚Öi combaterii actelor de terorism ╚Öi sabotaj asupra infrastructurii SEN;...l)asigurarea siguran╚¢ei ├«n func╚¢ionare a SEN;...m)asigurarea siguran╚¢ei ├«n alimentarea cu energie electric─â a clien╚¢ilor finali;...n)promovarea produc╚¢iei de energie electric─â realizat─â ├«n sisteme de cogenerare de ├«nalt─â eficien╚¢─â, asociat─â energiei termice livrate pentru acoperirea unui consum economic justificat....', NULL, 2, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1103, 6, '3', 'Articolul 3', NULL, 'Energia electric─â', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '├Än╚¢elesul unor termeni ╚Öi expresii...├Än ├«n╚¢elesul prezentului titlu, termenii ╚Öi expresiile de mai jos au urm─âtoarele semnifica╚¢ii:...1. acces la re╚¢eaua electric─â de interes public - ansamblul de reguli prin care un ter╚¢ ├«╚Öi exercit─â dreptul de a se racorda ╚Öi de a folosi, ├«n condi╚¢iile legii, re╚¢elele electrice de transport ╚Öi distribu╚¢ie;...2. autoritate competent─â - Autoritatea Na╚¢ional─â de Reglementare ├«n Domeniul Energiei, denumit─â ├«n continuare ANRE;...3. acces garantat la re╚¢ele electrice - ansamblul de reguli ╚Öi condi╚¢ii tehnice ╚Öi comerciale prin care unor categorii de produc─âtori aflate sub inciden╚¢a unei scheme de sprijin adoptate la nivel na╚¢ional li se garanteaz─â preluarea ├«n re╚¢eaua electric─â a unor cantit─â╚¢i de energie electric─â ce au fost contractate pe pia╚¢a concuren╚¢ial─â sau pe baza unor obliga╚¢ii de achizi╚¢ie stabilite ├«n baza prezentei legi;...4. acces prioritar la re╚¢ele electrice - ansamblul de reguli ╚Öi condi╚¢ii tehnice ╚Öi comerciale prin care unor categorii de produc─âtori li se asigur─â posibilitatea prelu─ârii ├«n orice moment ╚Öi a v├ónz─ârii ├«ntregii cantit─â╚¢i de energie electrice livrat─â ├«n re╚¢ea, ├«n func╚¢ie de capacitatea racordului la re╚¢ea ╚Öi de disponibilitatea unit─â╚¢ilor/resurselor eligibile;...5. avarie - evenimentul care se manifest─â prin abateri periculoase de la parametrii func╚¢ionali prev─âzu╚¢i de normele tehnice ├«n vigoare;...6. capacitate de interconexiune - o linie de transport de energie electric─â ce traverseaz─â sau trece peste grani╚¢a dintre dou─â state ╚Öi care face leg─âtura ├«ntre sistemele de transport na╚¢ionale ale acelor state;...7. capacitate energetic─â - instala╚¢iile de producere a energiei electrice ╚Öi/sau energiei termice ├«n cogenerare, re╚¢ele electrice ╚Öi alte echipamente electroenergetice;...8. central─â electric─â - ansamblul de instala╚¢ii, construc╚¢ii ╚Öi echipamente necesare pentru producerea de energie electric─â; o central─â electric─â cuprinde una sau mai multe unit─â╚¢i (grupuri) de producere;...9. central─â electric─â de cogenerare (termoficare) - ansamblul de instala╚¢ii, construc╚¢ii ╚Öi echipamente necesare pentru producerea combinat─â de energie electric─â ╚Öi termic─â;...10. client - clientul angro sau final de energie electric─â;...11. client angro - persoana fizic─â sau juridic─â ce cump─âr─â energie electric─â ├«n vederea rev├ónz─ârii ├«n interiorul sau ├«n exteriorul sistemului ├«n cadrul c─âruia este stabilit─â;...12. client eligibil - clientul care este liber s─â ├«╚Öi aleag─â furnizorul de energie electric─â de la care cump─âr─â energie electric─â, ├«n sensul art. 33 din Directiva 2009/72/CE a Parlamentului European ╚Öi a Consiliului din 13 iulie 2009 privind normele comune pentru pia╚¢a intern─â a energiei electrice ╚Öi de abrogare a Directivei 2003/54/CE ;...13. client final - orice persoan─â fizic─â sau juridic─â ce cump─âr─â energie electric─â pentru consum propriu; ├«n cuprinsul legii no╚¢iunile de "client final" ╚Öi "consumator" sunt echivalente;...14. client noncasnic - orice persoan─â fizic─â sau juridic─â ce cump─âr─â energie electric─â ce nu este pentru propriul consum casnic; aceast─â categorie include ╚Öi produc─âtori de energie electric─â, operatori de re╚¢ea ╚Öi clien╚¢i angro;...15. client casnic - clientul care cump─âr─â energie electric─â pentru propriul consum casnic, excluz├ónd consumul pentru activit─â╚¢i comerciale sau profesionale;...16. client vulnerabil - clientul final apar╚¢in├ónd unei categorii de clien╚¢i casn