INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1111, 6, '11', 'Articolul 11', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Suspendarea ╚Öi retragerea autoriza╚¢iilor ╚Öi licen╚¢elor...(1)...├Än caz de ne├«ndeplinire de c─âtre titularul autoriza╚¢iei de ├«nfiin╚¢are, respectiv al licen╚¢ei a obliga╚¢iilor sale legale, precum ╚Öi ├«n caz de nerespectare a condi╚¢ion─ârilor, limit─ârilor, restric╚¢iilor, interdic╚¢iilor sau a sarcinilor stabilite prin autoriza╚¢ie, respectiv prin licen╚¢─â, constatate de autoritatea competent─â din oficiu ori la sesizarea unor ter╚¢i sau la notificarea de c─âtre titular, autoritatea competent─â va proceda dup─â cum urmeaz─â:a)├«n cazul ├«n care ne├«ndeplinirea sau nerespectarea obliga╚¢iilor nu este imputabil─â titularului, va dispune:ΓÇôun termen de conformare, dac─â situa╚¢ia creat─â este remediabil─â;...ΓÇôretragerea autoriza╚¢iei, respectiv a licen╚¢ei, dac─â situa╚¢ia creat─â este iremediabil─â;......b)├«n cazul ├«n care ne├«ndeplinirea sau nerespectarea obliga╚¢iilor este imputabil─â titularului, va dispune:ΓÇôsuspendarea autoriza╚¢iei, respectiv a licen╚¢ei, pe o perioad─â fixat─â pentru remedierea situa╚¢iei create ╚Öi pentru conformare cu autoriza╚¢ia, respectiv cu licen╚¢a, dac─â situa╚¢ia creat─â este remediabil─â;...ΓÇôretragerea autoriza╚¢iei, respectiv a licen╚¢ei, dac─â situa╚¢ia creat─â este iremediabil─â..........(2)...Autoritatea competent─â va retrage autoriza╚¢ia de ├«nfiin╚¢are, respectiv licen╚¢a, ├«n cazul dec─âderii, incapacit─â╚¢ii sau al falimentului titularului, precum ╚Öi la ├«ncetarea concesiunii ori a loca╚¢iunii capacit─â╚¢ii energetice sau la v├ónzarea capacit─â╚¢ii de c─âtre titular.', NULL, 11, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1112, 6, '12', 'Articolul 12', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Drepturile ╚Öi obliga╚¢iile ce decurg din autoriza╚¢ia de ├«nfiin╚¢are ╚Öi din licen╚¢e...(1)...Lucr─ârile de realizare ╚Öi retehnologizare ale capacit─â╚¢ilor energetice pentru care se acord─â autoriza╚¢ii, precum ╚Öi activit─â╚¢ile ╚Öi serviciile pentru care se acord─â licen╚¢e, dup─â caz, sunt de interes public, cu excep╚¢ia celor care sunt destinate exclusiv satisfacerii consumului propriu al titularului autoriza╚¢iei sau licen╚¢ei....(2)...Asupra terenurilor ╚Öi bunurilor proprietate public─â sau privat─â a altor persoane fizice ori juridice ╚Öi asupra activit─â╚¢ilor desf─â╚Öurate de persoane fizice sau juridice ├«n vecin─âtatea capacit─â╚¢ii energetice se instituie limit─âri ale dreptului de proprietate ├«n favoarea titularilor autoriza╚¢iilor de ├«nfiin╚¢are ╚Öi de licen╚¢e care beneficiaz─â de:a)dreptul de uz pentru executarea lucr─ârilor necesare realiz─ârii, reloc─ârii, retehnologiz─ârii sau desfiin╚¢─ârii capacit─â╚¢ii energetice, obiect al autoriza╚¢iei;...b)dreptul de uz pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii, obiect al autoriza╚¢iei de ├«nfiin╚¢are, pentru reviziile, repara╚¢iile ╚Öi interven╚¢iile necesare;...c)servitutea de trecere subteran─â, de suprafa╚¢─â sau aerian─â pentru instalarea/desfiin╚¢area de re╚¢ele electrice sau alte echipamente aferente capacit─â╚¢ii energetice ╚Öi pentru acces la locul de amplasare a acestora, ├«n condi╚¢iile legii;...d)dreptul de a ob╚¢ine restr├óngerea sau ├«ncetarea unor activit─â╚¢i care ar putea pune ├«n pericol persoane ╚Öi bunuri;...e)dreptul de acces la utilit─â╚¢ile publice.......(3)...Drepturile de uz ╚Öi de servitute au ca obiect utilitatea public─â, au caracter legal, iar con╚¢inutul acestora este prev─âzut la art. 14 ╚Öi se exercit─â f─âr─â ├«nscriere ├«n Cartea funciar─â pe toat─â durata existentei capacit─â╚¢ii energetice sau, temporar, cu ocazia retehnologiz─ârii unei capacit─â╚¢i ├«n func╚¢iune, repara╚¢iei, reviziei, lucr─ârilor de interven╚¢ie ├«n caz de avarie....(4)...Exercitarea drepturilor de uz ╚Öi servitute asupra propriet─â╚¢ilor statului ╚Öi ale unit─â╚¢ilor administrativ-teritoriale afectate de capacit─â╚¢ile energetice se realizeaz─â cu titlu gratuit, pe toat─â durata existen╚¢ei acestora....(5)...Exercitarea drepturilor de uz ╚Öi de servitute asupra propriet─â╚¢ilor private afectate de capacit─â╚¢ile energetice, care se vor realiza dup─â intrarea ├«n vigoare a prezen╚¢ei legi, se face ├«n conformitate cu regulile procedurale privind condi╚¢iile ╚Öi termenii referitori la durata, con╚¢inutul ╚Öi limitele de exercitare a acestor drepturi, prev─âzute ├«ntr-o conven╚¢ie-cadru, precum ╚Öi pentru determinarea cuantumului indemniza╚¢iilor, a desp─âgubirilor ╚Öi a modului de plat─â a acestora, care se aprob─â, ├«mpreun─â cu conven╚¢ia-cadru, prin hot─âr├óre a Guvernului, la propunerea ministerului de resort....(6)...Proprietarii terenurilor afectate de exercitarea drepturilor de uz ╚Öi de servitute de c─âtre titularii de licen╚¢e ╚Öi autoriza╚¢ii pot solicita ├«ncheierea de conven╚¢ii, conform prevederilor alin. (5)....(7)...Beneficiaz─â de indemniza╚¢ii ╚Öi, respectiv, desp─âgubiri ╚Öi proprietarii de terenuri afectate de exercitarea drepturilor de uz ╚Öi de servitute de c─âtre titularii de licen╚¢e ╚Öi autoriza╚¢ii care la data intr─ârii ├«n vigoare a prezentei legi au ├«n derulare conven╚¢ii privind exercitarea acestor drepturi ├«ncheiate ├«n condi╚¢iile legii....(8)...Titularii de licen╚¢e ╚Öi autoriza╚¢ii sunt obliga╚¢i s─â procedeze la ├«ncheierea conven╚¢iilor-cadru prev─âzute la alin. (5), ├«n termen de maximum 30 de zile de la solicitarea proprietarilor afecta╚¢i....(9)...Dac─â, cu ocazia interven╚¢iei pentru retehnologiz─âri, repara╚¢ii, revizii sau avarii, se produc pagube proprietarilor din vecin─âtatea capacit─â╚¢ilor energetice, titularii de licen╚¢─â au obliga╚¢ia s─â pl─âteasc─â desp─âgubiri, ├«n condi╚¢iile prezentei legi....(10)...Proprietarii terenurilor ╚Öi titularii activit─â╚¢ilor afecta╚¢i de exercitarea de c─âtre titularii de licen╚¢─â ╚Öi autoriza╚¢ii a drepturilor prev─âzute la alin. (2) vor fi desp─âgubi╚¢i pentru prejudiciile cauzate acestora. La calculul desp─âgubirilor vor fi avute ├«n vedere urm─âtoarele criterii:ΓÇôsuprafa╚¢a de teren afectat─â cu ocazia efectu─ârii lucr─ârilor;...ΓÇôtipurile de culturi ╚Öi planta╚¢ii, precum ╚Öi amenaj─ârile afectate de lucr─âri;...ΓÇôactivit─â╚¢ile restr├ónse cu ocazia lucr─ârilor.......Cuantumul desp─âgubirii se stabile╚Öte prin acordul p─âr╚¢ilor sau, ├«n cazul ├«n care p─âr╚¢ile nu se ├«n╚¢eleg, prin hot─âr├óre judec─âtoreasc─â....(11)...Dreptul de uz ╚Öi de servitute asupra terenurilor proprietate privat─â, restr├óngerea sau ├«ncetarea unor activit─â╚¢i prev─âzute la alin. (2) se stabilesc ╚Öi se exercit─â cu respectarea principiului echit─â╚¢ii, a dreptului de proprietate ╚Öi a minimei afect─âri a acestuia....(12)...Titularii de autoriza╚¢ii ╚Öi licen╚¢e sunt ├«n drept s─â efectueze lucr─ârile de defri╚Öare a vegeta╚¢iei sau t─âierile de modelare pentru crearea ╚Öi men╚¢inerea distan╚¢ei de apropiere fa╚¢─â de re╚¢elele electrice cu personal specializat, respect├ónd prevederile legale ├«n vigoare....(13)...Titularii de autoriza╚¢ii ╚Öi licen╚¢e beneficiari ai drepturilor de uz ╚Öi de servitute asupra propriet─â╚¢ii publice sau private a statului ╚Öi a unit─â╚¢ilor administrativ-teritoriale sunt scuti╚¢i de plat─â de taxe, impozite ╚Öi alte obliga╚¢ii de plat─â instituite de autorit─â╚¢ile administra╚¢iei publice centrale ╚Öi locale.', NULL, 12, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1113, 6, '13', 'Articolul 13', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Obliga╚¢iile decurg├ónd din autoriza╚¢iile de ├«nfiin╚¢are ╚Öi din licen╚¢e...(1)...Titularul autoriza╚¢iei de ├«nfiin╚¢are are urm─âtoarele obliga╚¢ii:a)s─â stabileasc─â ╚Öi s─â aplice, pe ├«ntreaga durat─â a execut─ârii lucr─ârilor, m─âsurile de protec╚¢ie a persoanelor, bunurilor ╚Öi mediului;...b)s─â ob╚¢in─â toate avizele, acordurile ╚Öi atestatele prev─âzute de lege pentru realizarea obiectivului autorizat.......(2)...Pe durata de valabilitate a licen╚¢elor, titularii de licen╚¢e sunt obliga╚¢i:a)s─â respecte condi╚¢iile care ├«nso╚¢esc licen╚¢a;...b)s─â ╚¢in─â situa╚¢ii contabile separate pentru fiecare activitate care face obiectul licen╚¢ei acordate de autoritatea competent─â, ├«n cazul operatorului economic ├«n domeniul energiei electrice integrat, pe vertical─â sau pe orizontal─â, s─â procedeze la fel ca ├«n cazul ├«n care activitatea respectiv─â ar fi efectuat─â de operatori economici separa╚¢i ╚Öi s─â ├«ntocmeasc─â rapoarte financiare ├«n formatul solicitat de autoritatea competent─â;...c)s─â constituie ╚Öi s─â men╚¢in─â garan╚¢ii financiare care s─â le permit─â desf─â╚Öurarea activit─â╚¢ii ╚Öi asigurarea continuit─â╚¢ii serviciului;...d)s─â pun─â la dispozi╚¢ia autorit─â╚¢ii competente informa╚¢iile necesare pentru desf─â╚Öurarea ├«n bune condi╚¢ii a activit─â╚¢ii acesteia;...e)s─â ├«ntocmeasc─â, s─â supun─â spre auditare ╚Öi s─â publice eviden╚¢ele lor contabile anuale la nivel de societate comercial─â, f─âr─â a cuprinde ├«n mod distinct sediile secundare f─âr─â personalitate juridic─â, conform legisla╚¢iei specifice, adoptate ├«n conformitate cu cea de-a Patra Directiv─â 78/660/CEE a Consiliului din 25 iulie 1978.......(3)...Operatorii economici care nu au obliga╚¢ia de a public─â conturile anuale p─âstreaz─â la sediul lor un exemplar din acestea la dispozi╚¢ia publicului....(4)...Activitatea de audit prev─âzut─â la alin. (2) lit. e) const─â, ├«n special, ├«n verificarea respect─ârii obliga╚¢iei de a evita discrimin─ârile ╚Öi subven╚¢iile ├«ncruci╚Öate ├«ntre activit─â╚¢ile desf─â╚Öurate de operatorul economic auditat.', NULL, 13, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1114, 6, '14', 'Articolul 14', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Drepturile ╚Öi obliga╚¢iile titularilor de autoriza╚¢ii de ├«nfiin╚¢are ╚Öi de licen╚¢─â asupra propriet─â╚¢ii ter╚¢ilor...(1)...Dreptul de uz asupra terenului pentru executarea lucr─ârilor necesare realiz─ârii/reloc─ârii/desfiin╚¢─ârii sau retehnologiz─ârii de capacit─â╚¢i energetice se ├«ntinde pe durata necesar─â execut─ârii lucr─ârilor. ├Än exercitarea acestui drept de uz, titularul autoriza╚¢iei de ├«nfiin╚¢are/relocare/desfiin╚¢are sau retehnologizare, dup─â caz, cu respectarea prevederilor legale, poate:a)s─â depoziteze, pe terenurile necesare execut─ârii lucr─ârilor, materiale, echipamente, utilaje, instala╚¢ii;...b)s─â desfiin╚¢eze culturi sau planta╚¢ii, construc╚¢ii sau alte amenaj─âri existente ori numai s─â le restr├óng─â, ├«n m─âsura strict necesar─â execut─ârii lucr─ârilor pentru capacitatea autorizat─â, ├«n condi╚¢iile legii;...c)s─â ├«ndep─ârteze materiale, s─â capteze ap─â, ├«n condi╚¢iile prev─âzute de legisla╚¢ia ├«n vigoare;...d)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea, s─â amplaseze birouri ╚Öi locuin╚¢e de ╚Öantier, cu acordul prealabil al proprietarului;...e)s─â opreasc─â ori s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura strict necesar─â execut─ârii lucr─ârilor pentru capacitatea autorizat─â, cu respectarea prevederilor legale ├«n vigoare.......(2)...Dreptul de uz prev─âzut la alin. (1) ├«nceteaz─â ├«naintea expir─ârii duratei stabilite pentru executarea lucr─ârilor sau ├«naintea acestui termen, la data termin─ârii anticipate a lucr─ârilor sau la data sist─ârii acestora ╚Öi renun╚¢─ârii la autoriza╚¢ii. Oricare dintre aceste situa╚¢ii trebuie notificat─â de ├«ndat─â proprietarului....(3)...Dreptul de uz asupra terenului pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii energetice se ├«ntinde pe toat─â durata func╚¢ion─ârii capacit─â╚¢ii, iar exercitarea lui se face ori de c├óte ori este necesar pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii. ├Än exercitarea acestui drept titularul licen╚¢ei poate:a)s─â depoziteze materiale, echipamente, utilaje, instala╚¢ii pentru ├«ntre╚¢inere, revizii, repara╚¢ii ╚Öi interven╚¢ii necesare pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii;...b)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea;...c)s─â desfiin╚¢eze sau s─â reduc─â culturi, planta╚¢ii ori alte amenaj─âri existente ╚Öi s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura ╚Öi pe durata strict necesare execut─ârii opera╚¢iilor de ├«ntre╚¢inere, repara╚¢ii, revizii sau interven╚¢ii pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii, cu respectarea legisla╚¢iei ├«n vigoare.......(4)...Titularul licen╚¢ei este obligat s─â ├«n╚Ötiin╚¢eze ├«n scris proprietarul bunurilor sau prestatorul activit─â╚¢ilor care vor fi afectate ca urmare a lucr─ârilor la capacit─â╚¢ile energetice, cu excep╚¢ia cazurilor de avarii, situa╚¢ie ├«n care proprietarii sunt ├«n╚Ötiin╚¢a╚¢i ├«n termenul cel mai scurt....(5)...Titularul licen╚¢ei este obligat s─â pl─âteasc─â proprietarilor desp─âgubirea cuvenit─â pentru pagubele produse, s─â degajeze terenul ╚Öi s─â-l repun─â ├«n situa╚¢ia anterioar─â, ├«n cel mai scurt timp posibil....(6)...Servitutea de trecere subteran─â, de suprafa╚¢─â sau aerian─â cuprinde dreptul de acces ╚Öi de executare a lucr─ârilor la locul de amplasare a capacit─â╚¢ilor energetice cu ocazia interven╚¢iei pentru retehnologiz─âri, repara╚¢ii, revizii ╚Öi avarii....(7)...Pentru a evita punerea ├«n pericol a persoanelor, a bunurilor sau a unor activit─â╚¢i desf─â╚Öurate ├«n zona de executare a lucr─ârilor de realizare ori retehnologizare de capacit─â╚¢i energetice, precum ╚Öi a opera╚¢iilor de revizie sau repara╚¢ie la capacitatea ├«n func╚¢iune, titularul autoriza╚¢iei sau al licen╚¢ei are dreptul de a ob╚¢ine restr├óngerea ori sistarea, pe toat─â durata lucr─ârilor, a activit─â╚¢ilor desf─â╚Öurate ├«n vecin─âtate de alte persoane. ├Än acest caz, persoanele afectate vor fi ├«n╚Ötiin╚¢ate, ├«n scris, despre data ├«nceperii, respectiv a finaliz─ârii lucr─ârilor....(8)...La ├«ncetarea exercit─ârii drepturilor prev─âzute la art. 12 alin. (2), titularul autoriza╚¢iei de ├«nfiin╚¢are, respectiv titularul licen╚¢ei este obligat s─â asigure degajarea terenului ╚Öi repunerea lui ├«n situa╚¢ia ini╚¢ial─â....(9)...Dreptul de acces la utilit─â╚¢ile publice, prev─âzut la art. 12 alin. (2) lit. e), trebuie exercitat de titularul autoriza╚¢iei sau al licen╚¢ei cu bun─â-credin╚¢─â ╚Öi ├«n mod rezonabil, f─âr─â a prejudicia accesul altor persoane la respectivele utilit─â╚¢i publice.', NULL, 14, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1115, 6, '15', 'Articolul 15', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Zonele de protec╚¢ie ╚Öi zonele de siguran╚¢─â...(1)...Pentru protec╚¢ia ╚Öi func╚¢ionarea normal─â a capacit─â╚¢ilor energetice ╚Öi a anexelor acestora, precum ╚Öi pentru evitarea punerii ├«n pericol a persoanelor, bunurilor ╚Öi mediului se instituie zone de protec╚¢ie ╚Öi de siguran╚¢─â....(2)...Zonele de protec╚¢ie ╚Öi de siguran╚¢─â se determin─â pentru fiecare capacitate, ├«n conformitate cu normele tehnice elaborate de autoritatea competent─â....(3)...Asupra terenurilor aflate ├«n proprietatea ter╚¢ilor, cuprinse ├«n zonele de protec╚¢ie ╚Öi de siguran╚¢─â, se stabile╚Öte drept de servitute legal─â.', NULL, 15, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1116, 6, '16', 'Articolul 16', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Obliga╚¢iile de serviciu public...(1)...Titularii de autoriza╚¢ii de ├«nfiin╚¢are ╚Öi de licen╚¢e au obliga╚¢ia de a-╚Öi desf─â╚Öura activit─â╚¢ile cu respectarea obliga╚¢iilor de serviciu public referitoare la siguran╚¢─â, calitatea ╚Öi pre╚¢ul energiei electrice furnizate, continuitatea aprovizion─ârii, eficien╚¢a energetic─â, cu respectarea normelor de securitate ╚Öi s─ân─âtate a muncii ╚Öi de protec╚¢ie a mediului, precum ╚Öi a prevederilor din contractele directe cu consumatorii....(2)...Obliga╚¢iile de serviciu public se pot stabili prin hot─âr├óre a Guvernului sau prin reglement─âri ale ANRE, caz ├«n care se notific─â Comisiei Europene de c─âtre ANRE, prin licen╚¢e sau autoriza╚¢ii de ├«nfiin╚¢are, pentru fiecare activitate din sectorul energiei electrice.', NULL, 16, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1117, 6, '17', 'Articolul 17', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Exproprierea...(1)...Terenul necesar ├«nfiin╚¢─ârii ╚Öi func╚¢ion─ârii capacit─â╚¢ii energetice este fie proprietate privat─â a unui ter╚¢ sau a titularului autoriza╚¢iei, fie proprietate public─â....(2)...Dac─â terenul necesar pentru ├«nfiin╚¢area ╚Öi func╚¢ionarea capacit─â╚¢ii energetice este proprietate privat─â a unui ter╚¢, solicitantul autoriza╚¢iei de ├«nfiin╚¢are are ca prim─â op╚¢iune cump─ârarea terenului de la proprietar sau s─â ini╚¢ieze procedura legal─â de expropriere a terenului pentru cauz─â de utilitate public─â, cu desp─âgubirea proprietarului, ├«n condi╚¢iile legii, ╚Öi s─â ob╚¢in─â concesiunea acestuia, pe durata existen╚¢ei capacit─â╚¢ii energetice.', NULL, 17, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1118, 6, '18', 'Articolul 18', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Proceduri pentru noi capacit─â╚¢i energetice de produc╚¢ie', NULL, NULL, 'Procedura de licita╚¢ie ╚Öi alte proceduri...(1)...├Än cazul ├«n care, ├«n urma procedurii de autorizare, capacit─â╚¢ile de produc╚¢ie care se construiesc sau m─âsurile luate pe linia managementului orientat c─âtre eficien╚¢a energetic─â ╚Öi satisfacerea cererii nu sunt suficiente pentru a garanta siguran╚¢a aliment─ârii pentru consumul intern, ministerul de resort poate ini╚¢ia o procedur─â de licita╚¢ie sau orice alt─â procedur─â similar─â pentru atribuirea unui contract, ├«n condi╚¢ii de transparen╚¢─â ╚Öi nediscriminare, pe baza unor criterii publicate, prin care s─â invite noi operatori economici sau titulari de licen╚¢─â de producere preexisten╚¢i s─â oferteze pentru realizarea de noi capacit─â╚¢i de producere a energiei electrice....(2)...├Än condi╚¢iile alin. (1) ╚Öi ├«n scopul asigur─ârii protec╚¢iei mediului ╚Öi al promov─ârii noilor tehnologii, ministerul de resort poate ini╚¢ia o procedur─â de licita╚¢ie privind realizarea de noi capacit─â╚¢i de producere a energiei electrice. Aceast─â procedur─â se poate referi la noi capacit─â╚¢i sau la resursele de management orientat c─âtre eficien╚¢─â energetic─â ╚Öi satisfacerea cererii....(3)...Ofertantului declarat c├ó╚Ötig─âtor i se va acorda autoriza╚¢ie de ├«nfiin╚¢are ╚Öi licen╚¢─â, potrivit dispozi╚¢iilor sec╚¢iunii 1 din prezentul capitol....(4)...Procedura de desf─â╚Öurare a licita╚¢iei pentru capacit─â╚¢i de producere noi, precum ╚Öi lista de criterii care stau la baza selec╚¢iei ofertelor ╚Öi atribuirea contractului se aprob─â prin hot─âr├óre a Guvernului, la propunerea ministerului de resort....(5)...Procedura prev─âzut─â la alin. (4) se va elabora cu respectarea urm─âtoarelor principii:a)informa╚¢iile privind procedura de ofertare pentru capacit─â╚¢ile de producere ╚Öi m─âsurile de eficien╚¢─â energetic─â ╚Öi satisfacerea cererii se public─â ├«n Jurnalul Oficial al Uniunii Europene, cu cel pu╚¢in 6 luni ├«nainte de termenul final pentru depunerea ofertelor;...b)caietul de sarcini se pune la dispozi╚¢ia oric─ârui operator economic interesat, la solicitarea acestuia;...c)caietul de sarcini con╚¢ine descrierea detaliat─â a specifica╚¢iilor contractului ╚Öi a procedurii care trebuie urmat─â de c─âtre to╚¢i ofertan╚¢ii, precum ╚Öi lista complet─â a criteriilor care determin─â selec╚¢ia ofertan╚¢ilor ╚Öi atribuirea contractului, inclusiv stimulentele, cum ar fi subven╚¢iile, care sunt acoperite de ofert─â;...d)pentru invita╚¢iile de participare la procedura de ofertare care se refer─â la capacitatea de producere previzionat─â a fi necesar─â, trebuie luate ├«n considerare ╚Öi ofertele de furnizare de energie electric─â cu garan╚¢ii pe termen lung, propuse de unit─â╚¢ile de produc╚¢ie existente, cu condi╚¢ia ca acestea s─â permit─â acoperirea nevoilor suplimentare.......(6)...Organizarea, monitorizarea ╚Öi controlul procedurii de ofertare se realizeaz─â de operatorul de transport ╚Öi de sistem.', NULL, 18, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1119, 6, '19', 'Articolul 19', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Proceduri pentru noi capacit─â╚¢i energetice de produc╚¢ie', NULL, NULL, 'Concesionarea. Obiectul concesiunii ╚Öi autoritatea contractant─â...(1)...Pot face obiectul unei concesiuni energetice bunurile proprietate public─â sau privat─â a statului, activit─â╚¢ile ╚Öi serviciile publice de interes na╚¢ional din domeniul energiei electrice....(2)...Calitatea de autoritate contractant─â o are ministerul de resort pentru bunurile proprietate public─â sau privat─â a statului ori pentru activit─â╚¢ile sau serviciile publice de interes na╚¢ional....(3)...Cadrul general privind regimul juridic al contractelor de concesiune, procedurile pentru acordarea concesiunilor, precum ╚Öi con╚¢inutul-cadru al caietului de sarcini sunt elaborate de autoritatea contractant─â, ├«n conformitate cu prevederile legii, ╚Öi se aprob─â prin hot─âr├óre a Guvernului....(4)...Contractul de concesiune intr─â ├«n vigoare la data public─ârii acestuia ├«n Monitorul Oficial al Rom├óniei, Partea I....(5)...Condi╚¢iile de acordare, men╚¢inere, suspendare ╚Öi retragere a concesiunii se stabilesc prin contractul de concesiune ├«ncheiat de autoritatea contractant─â ╚Öi aprobat ├«n conformitate cu prevederile prezentei legi.', NULL, 19, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1120, 6, '20', 'Articolul 20', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Pia╚¢a de energie electric─â...(1)...Pia╚¢a de energie electric─â este compus─â din pia╚¢a reglementat─â ╚Öi pia╚¢a concuren╚¢ial─â, iar tranzac╚¢iile cu energie se fac angro sau cu am─ânuntul....(2)...Cre╚Öterea ponderii pie╚¢ei concuren╚¢iale se realizeaz─â gradat, prin asigurarea accesului pe aceast─â pia╚¢─â pentru c├ót mai mul╚¢i participan╚¢i, produc─âtori, furnizori ╚Öi clien╚¢i finali, ├«n conformitate cu prevederile art. 23....(3)...Se ├«nfiin╚¢eaz─â Comisia de solu╚¢ionare a disputelor, ca organism care solu╚¢ioneaz─â disputele pe pia╚¢a angro ╚Öi cu am─ânuntul ap─ârute ├«ntre participan╚¢ii la pia╚¢a de energie electric─â....(4)...Comisia de solu╚¢ionare a disputelor este format─â din 5 membri, care sunt numi╚¢i prin decizie a pre╚Öedintelui ANRE pe o perioad─â de 3 ani, dintre speciali╚Ötii ANRE cu vechime de cel pu╚¢in 8 ani ├«n sectorul energiei electrice....(5)...Comisia de solu╚¢ionare a disputelor ├«╚Öi desf─â╚Öoar─â activitatea pe baza unui regulament de organizare ╚Öi func╚¢ionare, aprobat prin decizie a pre╚Öedintelui ANRE, dup─â consultare public─â.', NULL, 20, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1121, 6, '21', 'Articolul 21', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Participan╚¢ii la pia╚¢a de energie electric─â...(1)...Participan╚¢ii la pia╚¢a de energie electric─â trebuie s─â respecte regulile de func╚¢ionare a acesteia, prev─âzute prin ordine, decizii ╚Öi avize favorabile emise de autoritatea competent─â ╚Öi s─â efectueze pl─â╚¢ile pentru energia electric─â ╚Öi serviciile de care beneficiaz─â, rezultate din tranzac╚¢iile efectuate ├«n conformitate cu aceste reguli, la termenele scadente prev─âzute ├«n contractele ├«ncheiate ├«ntre p─âr╚¢i....(2)...Participan╚¢ii la pia╚¢a de energie electric─â ╚Öi structurile opera╚¢ionale asociate sunt: produc─âtorul, operatorul de transport ╚Öi de sistem, operatorul pie╚¢ei de energie electric─â, operatorul de distribu╚¢ie, furnizorul ╚Öi clientul....(3)...Participan╚¢ii la pia╚¢─â sunt obliga╚¢i s─â-╚Öi asume responsabilitatea financiar─â pentru plata dezechilibrelor pe care le genereaz─â pe pia╚¢a de energie electric─â, cu excep╚¢ia dezechilibrelor pentru care se aplic─â reguli de socializare/redistribuire stabilite de autoritatea competent─â sau prev─âzute explicit de legisla╚¢ia ├«n vigoare, s─â respecte prevederile licen╚¢elor ╚Öi ale reglement─ârilor emise de autoritatea competent─â. Obliga╚¢iile de plat─â vor fi achitate ├«n termen de maximum o lun─â de la ├«ncheierea lunii de tranzac╚¢ionare, pe baza cantit─â╚¢ilor de energie electric─â tranzac╚¢ionate m─âsurate....(4)...Clien╚¢ii au obliga╚¢ia pl─â╚¢ii contravalorii facturilor pentru energia electric─â ╚Öi pentru serviciile de care beneficiaz─â, la termenele scadente prev─âzute ├«n contractele ├«ncheiate ├«ntre p─âr╚¢i, precum ╚Öi constituirea de garan╚¢ii financiare ├«n vederea evit─ârii riscurilor de neplat─â pe pia╚¢a de energie electric─â....(5)...Participan╚¢ii la pia╚¢a de energie electric─â trebuie s─â notifice operatorului de transport ╚Öi de sistem importurile, exporturile ╚Öi tranzitele pe intervale de tranzac╚¢ionare, cu partenerii externi, pe fiecare grani╚¢─â....(6)...Participan╚¢ii la pia╚¢a de energie electric─â trebuie s─â notifice operatorului de transport ╚Öi de sistem toate cantit─â╚¢ile contractate nete reciproce....(7)...Participan╚¢ii la pia╚¢a de energie electric─â au obliga╚¢ia s─â transmit─â operatoruluipie╚¢ei de energie electric─â ╚Öi de echilibrare informa╚¢ii privind cantit─â╚¢ile de energie electric─â tranzac╚¢ionate aferente contractelor de v├ónzare-cump─ârare de energie electric─â ├«ncheiate.', NULL, 21, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1122, 6, '22', 'Articolul 22', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Func╚¢ionarea pie╚¢ei reglementate de energie electric─â...(1)...Furnizarea de energie electric─â ├«n condi╚¢iile reglementate se realizeaz─â pentru asigurarea aliment─ârii cu energie electric─â a clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) p├ón─â la data de 31 decembrie 2013 pentru clien╚¢ii noncasnici, respectiv p├ón─â la data de 31 decembrie 2017 pentru clien╚¢ii casnici....(2)...Pe pia╚¢a reglementat─â, autoritatea competent─â are dreptul:a)s─â impun─â obliga╚¢ii de serviciu public produc─âtorilor, operatorului de transport ╚Öi de sistem, operatorilor de distribu╚¢ie ╚Öi furnizorilor de ultim─â instan╚¢─â pentru asigurarea aliment─ârii cu energie electric─â a clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1), conform art. 16;...b)s─â impun─â furnizorilor de ultim─â instan╚¢─â proceduri transparente de achizi╚¢ie a energiei electrice de pe pia╚¢a concuren╚¢ial─â destinat─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1);...c)s─â aprobe ╚Öi s─â publice pre╚¢urile ╚Öi cantit─â╚¢ile din contractele ├«ncheiate ├«ntre produc─âtori ╚Öi furnizorii clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1);...d)s─â aprobe ╚Öi s─â publice tarifele aplicate de furnizorii de ultim─â instan╚¢─â de energie electric─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) p├ón─â la eliminarea tarifelor reglementate conform alin. (1);...e)s─â aprobe metodologii de monitorizare a cheltuielilor cu achizi╚¢ia energiei electrice conform lit. b);...f)s─â avizeze ╚Öi s─â publice pre╚¢urile propuse de furnizorii de ultim─â instan╚¢─â pentru energia electric─â furnizat─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1), dup─â eliminarea tarifelor reglementate.......(3)...Pentru asigurarea siguran╚¢ei ├«n func╚¢ionare a SEN, autoritatea competent─â poate s─â aprobe pre╚¢urile ╚Öi cantit─â╚¢ile din contractele de achizi╚¢ie ╚Öi livrare a serviciilor tehnologice de sistem....(4)...Furnizarea energiei electrice clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) ├«n condi╚¢iile alin. (1) se face pe baz─â de contracte reglementate....(5)...Autoritatea competent─â va monitoriza continuu efectul pie╚¢ei reglementate asupra pie╚¢ei concuren╚¢iale de energie electric─â ╚Öi va lua m─âsurile necesare pentru evitarea eventualelor distorsiuni ╚Öi pentru cre╚Öterea gradului de transparen╚¢─â a tranzac╚¢iilor comerciale....(6)...Autoritatea competent─â va organiza, ├«n cadrul ac╚¢iunii de monitorizare, un proces de evaluare anual─â detaliat─â a func╚¢ion─ârii pie╚¢ei de energie electric─â, ├«n condi╚¢iile renun╚¢─ârii treptate la aplicarea tarifelor reglementate pentru clien╚¢ii noncasnici prev─âzu╚¢i la art. 55 alin. (1) lit. a), ├«n care vor fi utiliza╚¢i, cel pu╚¢in, urm─âtorii indicatori generali:a)num─ârul de furnizori activi ├«n pia╚¢a de energie electric─â ├«n fiecare an;...b)cota de pia╚¢─â a fiec─âruia dintre furnizorii activi;...c)capabilitatea economico-financiar─â a furnizorilor activi ╚Öi comportamentul acestora ├«n pia╚¢─â;...d)evolu╚¢ia num─ârului anual de schimb─âri ale furnizorului de energie pe categorii de clien╚¢i;...e)nivelul ╚Öi evolu╚¢ia pre╚¢urilor ├«n pia╚¢─â;...f)gradul de informare a clien╚¢ilor finali privind pia╚¢a de energie electric─â;...g)facilit─â╚¢ile acordate clien╚¢ilor vulnerabili ╚Öi num─ârul acestora;...h)num─ârul clien╚¢ilor care beneficiaz─â de serviciul universal ├«n condi╚¢iile prezentei legi;...i)gradul de transparen╚¢─â a informa╚¢iilor ├«n pia╚¢a de energie.......(7)...├Än baza procesului de monitorizare prev─âzut la alin. (5) ╚Öi (6), ANRE ├«ntocme╚Öte un raport o dat─â la 2 ani, referitor la pre╚¢urile reglementate ca obliga╚¢ii de serviciu public ╚Öi impactul acestora asupra pie╚¢ei de energie electric─â....(8)...Calendarul de eliminare treptat─â a pre╚¢urilor reglementate pentru clien╚¢ii finali, cu ├«ncepere la data de 1 septembrie 2012 pentru clien╚¢ii noncasnici, respectiv la data de 1 iulie 2013 pentru clien╚¢ii casnici, se stabile╚Öte ╚¢in├ónd seama de posibilele efecte negative ale elimin─ârii pre╚¢urilor reglementate, astfel ├«nc├ót acestea s─â fie c├ót mai pu╚¢in resim╚¢ite de clien╚¢i.', NULL, 22, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1123, 6, '23', 'Articolul 23', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Func╚¢ionarea pie╚¢ei concuren╚¢iale...(1)...Tranzac╚¢iile cu energie electric─â se desf─â╚Öoar─â pe pia╚¢a concuren╚¢ial─â, ├«n mod transparent, public, centralizat ╚Öi nediscriminatoriu....(2)...Pe pia╚¢a concuren╚¢ial─â, tranzac╚¢iile comerciale se fac angro sau cu am─ânuntul, conform reglement─ârilor ANRE, iar pre╚¢urile se formeaz─â ├«n baza cererii ╚Öi ofertei, ca rezultat al unor mecanisme concuren╚¢iale....(3)...Pe pia╚¢a concuren╚¢ial─â de energie electric─â, operatorul de transport ╚Öi de sistem poate achizi╚¢iona servicii tehnologice de sistem....(4)...Pe pia╚¢a concuren╚¢ial─â cu am─ânuntul, furnizorii v├ónd energie electric─â clien╚¢ilor finali prin contracte bilaterale, la pre╚¢uri negociate sau stabilite prin oferte-tip....(5)...Datele relevante cum sunt durata, regulile privind livrarea ╚Öi decontarea, cantitatea, termenele de executare, pre╚¢urile tranzac╚¢iei, mijloacele de identificare a clientului angro, cu privire la toate tranzac╚¢iile din cadrul contractelor de furnizare de energie electric─â ╚Öi al instrumentelor derivate din domeniul energiei electrice ├«ncheiate cu clien╚¢i angro ╚Öi cu operatori de transport ╚Öi de sistem se p─âstreaz─â de furnizori cel pu╚¢in 5 ani ╚Öi se pun la dispozi╚¢ia ANRE, a Comisiei Europene ╚Öi a celorlalte autorit─â╚¢i na╚¢ionale competente, la cererea acestora....(6)...Obliga╚¢ia de p─âstrare a datelor privitoare la tranzac╚¢iile din cadrul instrumentelor financiare derivate se aplic─â ├«n conformitate cu prevederile liniilor directoare publicate de c─âtre Comisia European─â