INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1152, 6, '52', 'Articolul 52', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Furnizarea energiei electrice...(1)...Furnizarea energiei electrice este activitatea prin care persoana juridic─â titular─â de licen╚¢─â comercializeaz─â energie electric─â clien╚¢ilor. Pentru furnizarea energiei electrice la clien╚¢i se ├«ncheie un contract, ├«n conformitate cu prevederile legale ├«n vigoare....(2)...Energia electric─â furnizat─â clientului se factureaz─â de c─âtre furnizor, conform reglement─ârilor ANRE ╚Öi prevederilor legale ├«n vigoare....(3)...Sunt interzise consumul ╚Öi furnizarea de energie electric─â f─âr─â ├«ncheierea contractului de furnizare, cu excep╚¢ia situa╚¢iilor reglementate ├«n mod distinct de c─âtre autoritatea competent─â.', NULL, 52, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1124, 6, '24', 'Articolul 24', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'M─âsuri de salvgardare...(1)...├Än cazul unor situa╚¢ii nea╚Öteptate de criz─â pe pia╚¢a de energie ╚Öi ├«n cazul ├«n care este amenin╚¢at─â siguran╚¢a fizic─â ori securitatea persoanelor, a aparatelor sau a instala╚¢iilor ori integritatea sistemului, operatorul de transport ╚Öi de sistem propune ANRE ╚Öi ministerului de resort adoptarea unor m─âsuri de siguran╚¢─â....(2)...M─âsurile men╚¢ionate la alin. (1) trebuie s─â afecteze c├ót mai pu╚¢in buna func╚¢ionare a pie╚¢ei interne europene ╚Öi s─â se rezume strict la remedierea situa╚¢iei de criz─â care le-a generat....(3)...Punerea ├«n aplicare a m─âsurilor prev─âzute la alin. (1) se face prin hot─âr├óre a Guvernului, ini╚¢iat─â de ministerul de resort....(4)...Ministerul de resort notific─â, ├«n regim de urgen╚¢─â, celorlalte state membre ale Uniunii Europene, precum ╚Öi Comisiei Europene m─âsurile de siguran╚¢─â adoptate ├«n fiecare caz....(5)...├Än termen de 3 luni de la intrarea ├«n vigoare a prezentei legi, operatorul de transport ╚Öi de sistem elaboreaz─â ╚Öi propune spre aprobare, prin ordin al conduc─âtorului autorit─â╚¢ii competente, un set de reglement─âri tehnice ╚Öi comerciale cu privire la m─âsurile prev─âzute la alin. (1).', NULL, 24, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1125, 6, '25', 'Articolul 25', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Accesul la re╚¢eaua electric─â...(1)...De╚¢in─âtorul de licen╚¢─â ╚Öi clientul final au acces reglementat la re╚¢elele electrice de interes public. Accesul la re╚¢elele electrice de interes public reprezint─â un serviciu obligatoriu, ├«n condi╚¢ii reglementate, pe care trebuie s─â-l ├«ndeplineasc─â operatorul de transport ╚Öi de sistem, precum ╚Öi operatorul de distribu╚¢ie....(2)...Accesul la re╚¢ea poate fi restric╚¢ionat numai dac─â racordarea afecteaz─â siguran╚¢a SEN, prin nerespectarea normelor tehnice ╚Öi a standardelor de performan╚¢─â prev─âzute ├«n reglement─ârile tehnice ├«n vigoare sau ├«n cazul ├«n care operatorul de transport ori de distribu╚¢ie nu dispune de capacit─â╚¢ile necesare. Refuzul trebuie motivat ╚Öi justificat ├«n mod corespunz─âtor pe criterii obiective, din punct de vedere tehnic ╚Öi economic, conform reglement─ârilor emise de autoritatea competent─â....(3)...Disputele privind accesul la re╚¢eaua electric─â se solu╚¢ioneaz─â de autoritatea competent─â prin emiterea unei decizii obligatorii pentru p─âr╚¢i, ├«n termen de dou─â luni de la primirea sesiz─ârii sau pl├óngerii....(4)...Realizarea de linii electrice directe ╚Öi accesul la acestea se reglementeaz─â de autoritatea competent─â....(5)...Tariful pentru accesul la re╚¢eaua electric─â de interes public este reglementat....(6)...Produc─âtorii ╚Öi furnizorii de energie electric─â ├«╚Öi pot alimenta sediile proprii, filialele sau clien╚¢ii eligibili prin intermediul unor linii directe....(7)...Alimentarea prin intermediul unei linii directe conform alin. (6) este condi╚¢ionat─â de absen╚¢a unei oferte rezonabile economic ╚Öi tehnic pentru accesul la re╚¢eaua electric─â de interes public....(8)...Realizarea liniilor electrice directe se face cu respectarea normelor tehnice ╚Öi de siguran╚¢─â cuprinse ├«n reglement─ârile tehnice ├«n vigoare....(9)...Alimentarea printr-o linie direct─â trebuie s─â asigure ├«ndeplinirea condi╚¢iilor privind protec╚¢ia consumatorilor prev─âzute de prezenta lege....(10)...De╚¢in─âtorul unei linii electrice directe are obliga╚¢ia s─â asigure accesul la linia direct─â, conform reglement─ârilor autorit─â╚¢ii competente....(11)...Capacit─â╚¢ile de interconexiune finan╚¢ate prin investi╚¢ii comerciale private se realizeaz─â f─âr─â a aduce atingere prevederilor Regulamentului (CE) nr. 714/2009 ....(12)...Deciziile privind realizarea investi╚¢iilor aferente capacit─â╚¢ilor de interconexiune se iau ├«n urma unei analize comune efectuate de c─âtre operatorii de transport ╚Öi de sistem ├«n cauz─â....(13)...Etapele ╚Öi procedurile necesare pentru racordarea utilizatorilor la re╚¢elele de transport ╚Öi distribu╚¢ie se stabilesc prin regulamentul de racordare a utilizatorilor la re╚¢ele de interes public, aprobat de ANRE.', NULL, 25, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1126, 6, '26', 'Articolul 26', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Contractul de racordare...(1)...La cererea scris─â a unui utilizator de re╚¢ea, nou sau preexistent, operatorul de transport ╚Öi de sistem sau operatorul de distribu╚¢ie, dup─â caz, este obligat s─â comunice, ├«n scris, ├«n termen de 30 de zile, condi╚¢iile tehnico-economice de racordare la re╚¢ea ╚Öi s─â colaboreze cu solicitantul pentru alegerea celei mai avantajoase solu╚¢ii de racordare....(2)...Contractul de racordare se ├«ncheie conform reglement─ârilor emise de autoritatea competent─â....(3)...Operatorul de transport ╚Öi de sistem ori operatorul de distribu╚¢ie are dreptul de a refuza unui solicitant actualizarea avizului de racordare ╚Öi/sau ├«ncheierea unui nou contract de transport ori distribu╚¢ie pentru un loc de consum preexistent, ├«n situa╚¢ia ├«n care pentru acel loc de consum exist─â un contract de furnizare a energiei electrice ├«n derulare ├«ncheiat cu un alt client final....(4)...La stabilirea punctului de delimitare patrimonial─â a instala╚¢iilor operatorului de re╚¢ea ╚Öi utilizatorul acesteia se vor avea ├«n vedere:a)tipul instala╚¢iei: de transport, de distribu╚¢ie sau de utilizare;...b)limita de proprietate asupra terenurilor, astfel ├«nc├ót re╚¢elele electrice amplasate pe domeniul public s─â fie de╚¢inute, de regul─â, de operatorul de re╚¢ea ╚Öi s─â se evite c├ót mai mult posibil amplasarea instala╚¢iilor operatorului de re╚¢ea pe proprietatea utilizatorului;...c)racordarea, ├«n perspectiv─â, de noi utilizatori la instala╚¢ia de racordare....', NULL, 26, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:2