INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1127, 6, '27', 'Articolul 27', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Producerea de energie electric─â...(1)...Producerea de energie electric─â ╚Öi producerea de energie electric─â ╚Öi termic─â ├«n cogenerare se realizeaz─â de c─âtre operatorii economici care de╚¢in unit─â╚¢i de producere a energiei electrice, ├«n condi╚¢iile prezentei legi....(2)...Produc─âtorii de energie electric─â pot beneficia de accesul garantat sau de accesul prioritar la re╚¢elele electrice, conform reglement─ârilor ANRE ├«n vigoare.', NULL, 27, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1128, 6, '28', 'Articolul 28', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile produc─âtorilor...Produc─âtorii au, ├«n principal, urm─âtoarele obliga╚¢ii:a)s─â asigure livr─ârile de energie electric─â ╚Öi serviciile tehnologice de sistem, cu respectarea condi╚¢iilor impuse prin licen╚¢e, clauze contractuale ╚Öi reglement─âri ├«n vigoare;...b)├«n cazul unit─â╚¢ilor dispecerizabile s─â oferteze ├«ntreaga putere electric─â disponibil─â pe pia╚¢a de echilibrare, definit─â conform reglement─ârilor emise de autoritatea competent─â;...c)s─â oferteze public ╚Öi nediscriminatoriu pe pia╚¢a concuren╚¢ial─â ├«ntreaga energie electric─â disponibil─â;...d)s─â oferteze nediscriminatoriu serviciile tehnologice de sistem;...e)s─â nu transmit─â la operatorul de transport ╚Öi de sistem notific─âri fizice ├«n dezechilibru negativ fa╚¢─â de contractele pe care le au ├«ncheiate, cu excep╚¢ia produc─âtorilor care beneficiaz─â de scheme de sprijin, conform prevederilor prezentului titlu;...f)s─â men╚¢in─â o rezerv─â de combustibil la un nivel suficient sau, dup─â caz, o rezerv─â suficient─â de ap─â, pentru ├«ndeplinirea obliga╚¢iilor de produc╚¢ie ╚Öi furnizare continu─â a energiei electrice, prev─âzute de reglement─ârile ├«n vigoare;...g)s─â se conformeze, din punct de vedere operativ, cerin╚¢elor operatorului de transport ╚Öi de sistem ╚Öi s─â ├«nfiin╚¢eze, dup─â caz, trepte proprii de conducere operativ─â;...h)s─â transmit─â autorit─â╚¢ii competente un raport anual de activitate, conform reglement─ârilor ├«n vigoare, chiar ├«n condi╚¢iile ├«n care nu de╚¢ine licen╚¢a de producere sau capacit─â╚¢ile sunt transferate altui operator economic....', NULL, 28, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1129, 6, '29', 'Articolul 29', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Drepturile produc─âtorilor...(1)...Produc─âtorii de energie electric─â au, ├«n principal, urm─âtoarele drepturi:a)s─â aib─â acces la re╚¢elele electrice de interes public, ├«n condi╚¢iile prezentului titlu;...b)s─â ob╚¢in─â, ├«n condi╚¢iile legii, culoar de trecere pentru liniile electrice proprii;...c)s─â tranzac╚¢ioneze energia electric─â ╚Öi serviciile tehnologice de sistem pe pia╚¢a reglementat─â ╚Öi concuren╚¢ial─â ├«n mod transparent ╚Öi nediscriminatoriu;...d)s─â ├«nfiin╚¢eze ╚Öi s─â men╚¢in─â un sistem propriu de telecomunica╚¢ii pentru leg─âtura cu capacit─â╚¢ile sale de produc╚¢ie, cu clien╚¢ii sau cu treptele de conducere operativ─â;...e)s─â comercializeze energia termic─â produs─â ├«n cogenerare;...f)s─â acceseze schemele de sprijin instituite la nivel na╚¢ional.......(2)...F─âr─â a periclita siguran╚¢a ├«n func╚¢ionare a SEN, produc─âtorii de energie electric─â pot oferta pe pia╚¢a intern─â european─â servicii tehnologice de sistem conform reglement─ârilor ANRE, cu avizul operatorului de transport ╚Öi de sistem.', NULL, 29, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1131, 6, '31', 'Articolul 31', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Certificarea operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem se organizeaz─â ╚Öi func╚¢ioneaz─â dup─â modelul "operator de sistem independent" ╚Öi este persoana juridic─â certificat─â de autoritatea competent─â ├«n ceea ce prive╚Öte ├«ndeplinirea condi╚¢iilor prev─âzute la art. 34....(2)...Pentru a respecta condi╚¢iile prev─âzute la art. 34, prin hot─âr├óre a Guvernului, elaborat─â la propunerea ministerului de resort, se desemneaz─â entitatea public─â ce de╚¢ine calitatea de ac╚¢ionar majoritar la operatorii economici care desf─â╚Öoar─â activit─â╚¢i de producere ╚Öi furnizare, pe de o parte, precum ╚Öi entitatea public─â ce de╚¢ine calitatea de ac╚¢ionar majoritar la operatorul de transport ╚Öi de sistem, pe de alt─â parte....(3)...├Än termen de 15 zile de la adoptarea m─âsurilor prev─âzute la alin. (2), operatorul de transport ╚Öi de sistem transmite ANRE, cu avizul proprietarului re╚¢elei de transport, cererea de certificare ├«nso╚¢it─â de documenta╚¢ia justificativ─â privind ├«ndeplinirea cerin╚¢elor prev─âzute la art. 34....(4)...ANRE emite o decizie de certificare preliminar─â ├«n termen de maximum 4 luni de la data ├«nregistr─ârii cererii operatorului de transport ╚Öi de sistem care se notific─â Comisiei Europene, ├«nso╚¢it─â de documenta╚¢ia aferent─â....(5)...Procedura de certificare a operatorului de transport ╚Öi de sistem se finalizeaz─â conform prevederilor art. 3 din Regulamentul (CE) nr. 714/2009 ....(6)...Desemnarea operatorului de transport ╚Öi de sistem ├«n calitate de operator de sistem independent se aprob─â de Comisia European─â ca urmare a comunic─ârii de c─âtre ANRE a operatorului economic certificat, dup─â ├«ncheierea procedurii de certificare conform alin. (5)....(7)...├Ämpreun─â cu decizia de certificare, autoritatea competent─â este obligat─â s─â notifice Comisiei Europene orice solicitare de certificare a proprietarului re╚¢elei de transport sau a operatorului de transport ╚Öi de sistem, care este controlat de o persoan─â ori de persoane din una sau mai multe ╚¢─âri ter╚¢e, precum ╚Öi orice circumstan╚¢e ├«n urma c─ârora o persoan─â sau persoane din una ori mai multe ╚¢─âri ter╚¢e ar dob├óndi controlul asupra re╚¢elei de transport sau asupra operatorului de transport ╚Öi de sistem, dac─â este cazul.', NULL, 31, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
