INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1136, 6, '36', 'Articolul 36', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem presteaz─â serviciul public de transport pentru to╚¢i utilizatorii re╚¢elelor electrice de transport, ├«n condi╚¢ii nediscriminatorii, asigur├ónd accesul la acestea oric─ârui solicitant ├«n condi╚¢iile legii ╚Öi evit├ónd ├«n special discriminarea ├«n favoarea operatorilor economici afilia╚¢i, cu respectarea normelor ╚Öi performan╚¢elor prev─âzute ├«n reglement─ârile tehnice ├«n vigoare....(2)...Operatorul de transport ╚Öi de sistem poate s─â participe la tranzac╚¢ionarea energiei electrice numai ├«n urm─âtoarele situa╚¢ii:a)pentru acoperirea consumului ├«n re╚¢elele electrice proprii ╚Öi ├«n locurile de consum care ├«i apar╚¢in;...b)pentru men╚¢inerea echilibrului produc╚¢ie-consum, prin opera╚¢iuni de v├ónzare-cump─ârare ├«n pia╚¢a de echilibrare sau prin opera╚¢iuni de v├ónzare-cump─ârare cu al╚¢i operatori de transport din ╚¢─ârile vecine, conform reglement─ârilor ├«n vigoare ╚Öi normelor ENTSO-E.......(3)...Operatorul de transport ╚Öi de sistem poate efectua opera╚¢iuni de v├ónzare-cump─ârare de servicii tehnologice de sistem cu operatorii de transport ╚Öi de sistem ai ╚¢─ârilor cu care Rom├ónia a ├«ncheiat acorduri care faciliteaz─â crearea ╚Öi dezvoltarea pie╚¢elor regionale, cu produc─âtorii sau clien╚¢ii, conform reglement─ârilor emise de autoritatea competent─â....(4)...Tranzac╚¢iile cu energie electric─â ╚Öi servicii tehnologice de sistem se realizeaz─â pe baza unor proceduri transparente ╚Öi nediscriminatorii, prin mecanisme concuren╚¢iale, conform reglement─ârilor autorit─â╚¢ii competente....(5)...Operatorul de transport ╚Öi de sistem presteaz─â serviciul de sistem ├«n condi╚¢ii nediscriminatorii pentru to╚¢i utilizatorii SEN, asigur├ónd conducerea operativ─â, ├«n scopul realiz─ârii siguran╚¢ei func╚¢ion─ârii, stabilit─â╚¢ii frecven╚¢ei ╚Öi tensiunii, continuit─â╚¢ii ├«n alimentarea clien╚¢ilor ╚Öi coordon─ârii schimburilor de energie electric─â cu alte sisteme electroenergetice....(6)...Operatorul de transport ╚Öi de sistem poate de╚¢ine ac╚¢iuni la societ─â╚¢i comerciale ├«nfiin╚¢ate pe teritoriul na╚¢ional sau al altor state, av├ónd ca obiect principal de activitate alocarea capacit─â╚¢ilor de interconexiune ╚Öi verificarea siguran╚¢ei re╚¢elei la nivel regional, acoperind teritoriul a dou─â sau mai multe state....(7)...Operatorul de transport ╚Öi de sistem desf─â╚Öoar─â, ├«n principal, urm─âtoarele activit─â╚¢i:a)asigur─â capacitatea pe termen lung a re╚¢elei de transport de a satisface cererile rezonabile de transport de energie electric─â ╚Öi exploateaz─â, ├«ntre╚¢ine, reabiliteaz─â ╚Öi dezvolt─â ├«n condi╚¢ii economice re╚¢eaua de transport pentru a-i asigura siguran╚¢a, fiabilitatea ╚Öi eficien╚¢a, cu respectarea normelor privind protec╚¢ia mediului;...b)garanteaz─â mijloacele adecvate pentru ├«ndeplinirea obliga╚¢iilor de serviciu public;...c)contribuie la realizarea siguran╚¢ei ├«n alimentarea cu energie electric─â, prin asigurarea unor capacit─â╚¢i de transport adecvate ╚Öi prin men╚¢inerea fiabilit─â╚¢ii acestora;...d)asigur─â gestionarea fluxurilor de energie ├«n re╚¢eaua de transport, ╚¢in├ónd seama de schimburile de energie cu alte sisteme interconectate;...e)achizi╚¢ioneaz─â serviciile tehnologice de sistem ╚Öi calific─â produc─âtorii ╚Öi clien╚¢ii dispecerizabili care furnizeaz─â servicii tehnologice de sistem, pe baza unei proceduri proprii, aprobat─â de autoritatea competent─â;...f)realizeaz─â schimbul de informa╚¢ii cu al╚¢i operatori de transport ╚Öi de sistem interconecta╚¢i ╚Öi cu al╚¢i operatori economici din sectorul energiei, cu respectarea reglement─ârilor ENTSO-E privind protocoalele de schimb de informa╚¢ii, rapoartele, structura ╚Öi procedurile de acces la bazele de date;...g)asigur─â alocarea capacit─â╚¢ilor de interconexiune, colecteaz─â venituri rezultate din gestionarea congestiilor ╚Öi efectueaz─â pl─â╚¢i ├«n temeiul mecanismului de compensare ├«ntre operatorii de transport ╚Öi de sistem ├«n conformitate cu art. 13 din Regulamentul (CE) nr. 714/2009, acord├ónd ╚Öi gestion├ónd accesul ter╚¢ilor ╚Öi oferind explica╚¢ii motivate atunci c├ónd refuz─â un astfel de acces, sub supravegherea ANRE;...h)exploateaz─â, ├«ntre╚¢ine ╚Öi dezvolt─â instala╚¢iile de m─âsurare ╚Öi contorizare a transferului de energie electric─â prin re╚¢elele electrice de transport ╚Öi la interfa╚¢a cu utilizatorii re╚¢elelor electrice de transport care ├«i apar╚¢in, instala╚¢iile de informatic─â ╚Öi telecomunica╚¢ii din re╚¢elele electrice de transport aferente SEN;...i)analizeaz─â ╚Öi avizeaz─â ├«ndeplinirea condi╚¢iilor tehnice de racordare de c─âtre utilizatorii re╚¢elelor electrice de transport, ├«n conformitate cu prevederile reglement─ârilor tehnice ├«n vigoare;...j)asigur─â transmiterea rezultatelor m─âsur─ârilor de energie electric─â la operatorul pie╚¢ei de energie electric─â ├«n vederea realiz─ârii decont─ârii tranzac╚¢iilor din pia╚¢a de echilibrare ╚Öi a dezechilibrelor p─âr╚¢ilor responsabile cu echilibrarea, precum ╚Öi accesul beneficiarilor serviciului de transport pentru verificarea grupurilor de m─âsurare;...k)realizeaz─â planificarea opera╚¢ional─â ╚Öi conducerea operativ─â a SEN la nivel central ╚Öi teritorial pe baza prognozei proprii, conform reglement─ârilor legale ├«n vigoare, acord├ónd prioritate instala╚¢iilor de producere care utilizeaz─â surse regenerabile de energie sau care produc energie electric─â ├«n cogenerare de ├«nalt─â eficien╚¢─â, ├«n m─âsura ├«n care func╚¢ionarea sigur─â a re╚¢elei na╚¢ionale de energie electric─â permite acest lucru;...l)autorizeaz─â personalul care realizeaz─â conducerea operativ─â, conform reglement─ârilor ├«n vigoare;...m)culege, ├«nregistreaz─â ╚Öi arhiveaz─â datele statistice privind func╚¢ionarea SEN;...n)elaboreaz─â ╚Öi supune aprob─ârii autorit─â╚¢ii competente normele tehnice ╚Öi reglement─ârile specifice necesare pentru realizarea activit─â╚¢ii de conducere operativ─â, cu consultarea participan╚¢ilor la pia╚¢a de energie electric─â;...o)elaboreaz─â, ├«n condi╚¢iile legii, planul de ap─ârare a SEN ├«mpotriva perturba╚¢iilor majore;...p)elaboreaz─â studii, programe ╚Öi lucr─âri privind dezvoltarea SEN;...q)elaboreaz─â ╚Öi supune aprob─ârii autorit─â╚¢ii competente regulile privind managementul congestiilor, inclusiv pe capacit─â╚¢ile de interconexiune, precum ╚Öi normele de atribuire a capacit─â╚¢ilor de interconexiune;...r)organizeaz─â ╚Öi administreaz─â pia╚¢a de echilibrare a energiei electrice.......(8)...├Än caz de avarie major─â care pericliteaz─â siguran╚¢a func╚¢ion─ârii SEN, operatorul de transport ╚Öi de sistem poate dispune, pe o perioad─â limitat─â, folosirea rezervei de ap─â ├«n afara programelor de exploatare a acumul─ârilor, cu obliga╚¢ia anun╚¢─ârii autorit─â╚¢ilor competente ├«n gospod─ârirea apelor....(9)...Restric╚¢iile ├«n aprovizionarea cu energie electric─â ├«n situa╚¢ii de urgen╚¢─â trebuie s─â respecte criterii predefinite ├«n ceea ce prive╚Öte gestionarea dezechilibrelor de c─âtre operatorul de transport ╚Öi de sistem. Orice m─âsur─â de siguran╚¢─â trebuie luat─â ├«n str├óns─â colaborare ╚Öi cu consultarea altor operatori de transport ╚Öi de sistem implica╚¢i, cu respectarea contractelor bilaterale aplicabile, inclusiv a acordurilor privind schimbul de informa╚¢ii....(10)...Operatorul de transport ╚Öi de sistem ╚Öi proprietarul re╚¢elei de transport public─â informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de re╚¢ea, conform reglement─ârilor ANRE, ├«n vederea asigur─ârii accesului eficient la re╚¢ea, a unei concuren╚¢e efective ╚Öi a func╚¢ion─ârii eficiente a pie╚¢ei de energie, nefiindu-le permis─â divulgarea informa╚¢iilor sensibile din punct de vedere comercial ob╚¢inute pe parcursul activit─â╚¢ilor acestora, inclusiv a celor ob╚¢inute de la ter╚¢i ├«n contextul acord─ârii sau al negocierii accesului la re╚¢ea....(11)...Cheltuielile pentru modificarea instala╚¢iilor de transport al energiei electrice, ca urmare a racord─ârii de noi utilizatori sau a schimb─ârii caracteristicilor energetice ini╚¢iale ale utilizatorilor existen╚¢i, inclusiv pentru eliberarea unor amplasamente, sunt suportate conform reglement─ârilor ├«n vigoare....(12)...Operatorul de transport ╚Öi de sistem aloc─â capacit─â╚¢i de interconexiune pe una sau mai multe grani╚¢e ├«mpreun─â cu al╚¢i operatori economici, inclusiv pentru cei ├«nregistra╚¢i pe teritoriul unui stat membru al Uniunii Europene sau al Tratatului de instituire a Comunit─â╚¢ii Energiei, ├«n condi╚¢iile semn─ârii unui memorandum de ├«n╚¢elegere ├«ntre p─âr╚¢i, avizat de ANRE.', NULL, 36, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1137, 6, '37', 'Articolul 37', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Atribu╚¢iile proprietarului re╚¢elei de transport...(1)...Proprietarul re╚¢elei de transport:a)coopereaz─â cu operatorul de transport ╚Öi de sistem ├«n vederea ├«ndeplinirii atribu╚¢iilor acestuia, furniz├óndu-i toate informa╚¢iile relevante;...b)finan╚¢eaz─â ╚Öi/sau ├«╚Öi d─â acordul asupra modalit─â╚¢ii de finan╚¢are a investi╚¢iilor ├«n re╚¢eaua de transport, stabilite de operatorul de transport ╚Öi de sistem ╚Öi aprobate de ANRE;...c)de╚¢ine r─âspunderea privind activele re╚¢elei de transport, cu excep╚¢ia r─âspunderii privind atribu╚¢iile operatorului de transport ╚Öi de sistem;...d)ofer─â garan╚¢ii pentru facilitarea finan╚¢─ârii eventualelor extinderi ale re╚¢elei, cu excep╚¢ia investi╚¢iilor pentru care ╚Öi-a dat acordul s─â fie finan╚¢ate de c─âtre orice parte interesat─â, inclusiv de c─âtre operatorul de transport ╚Öi de sistem, potrivit prevederilor lit. b).......(2)...Consiliul Concuren╚¢ei, ├«n str├óns─â cooperare cu ANRE, este abilitat cu competen╚¢ele necesare pentru a monitoriza ├«n mod eficient respectarea de c─âtre proprietarul re╚¢elei de transport a obliga╚¢iilor acestuia, ├«n temeiul alin. (1).', NULL, 37, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1168, 6, '68', 'Articolul 68', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Tehnologii de cogenerare...├Än condi╚¢iile prezentului titlu, se definesc urm─âtoarele tehnologii de cogenerare cu:a)turbine cu gaze ├«n ciclu combinat cu recuperare de c─âldur─â ╚Öi turbine cu abur;...b)turbina cu abur cu contrapresiune;...c)turbina cu abur cu condensa╚¢ie ╚Öi prize reglabile;...d)turbine cu gaze ╚Öi cazane recuperatoare;...e)motoare cu ardere intern─â;...f)microturbine;...g)motoare Stirling;...h)celule de combustie;...i)motoare termice;...j)cicluri organice Rankine;...k)orice alt─â instala╚¢ie sau combina╚¢ie de instala╚¢ii prin care se produce simultan energie termic─â ╚Öi electric─â....', NULL, 68, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1138, 6, '38', 'Articolul 38', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Separarea proprietarului re╚¢elei de transport...(1)...├Än cazul ├«n care proprietarul re╚¢elei de transport face parte dintr-un operator economic integrat pe vertical─â, proprietarul re╚¢elei de transport este independent cel pu╚¢in ├«n ceea ce prive╚Öte statutul s─âu juridic, organizarea ╚Öi procesul decizional fa╚¢─â de alte activit─â╚¢i care nu au leg─âtur─â cu transportul energiei electrice....(2)...Pentru a asigura independen╚¢a proprietarului re╚¢elei de transport ├«n condi╚¢iile alin. (1), se aplic─â urm─âtoarele criterii minime:a)persoanele cu func╚¢ie de conducere din cadrul proprietarului re╚¢elei de transport nu pot face parte din structurile operatorului economic integrat din domeniul energiei electrice care r─âspunde, direct sau indirect, de gestionarea zilnic─â a activit─â╚¢ilor de producere, distribu╚¢ie ╚Öi furnizare a energiei electrice;...b)persoanele cu func╚¢ie de conducere din cadrul proprietarului re╚¢elei de transport ac╚¢ioneaz─â independent de orice interes de pia╚¢─â ├«n ├«ndeplinirea atribu╚¢iilor de serviciu;...c)proprietarul re╚¢elei de transport stabile╚Öte un program de conformitate, care con╚¢ine m─âsurile luate pentru a garanta excluderea practicilor discriminatorii ╚Öi stabile╚Öte ╚Öi obliga╚¢iile specifice impuse angaja╚¢ilor pentru realizarea obiectivului de independen╚¢─â;...d)proprietarul re╚¢elei de transport desemneaz─â o persoan─â sau un organism, denumit─â/ denumit agent de conformitate, care s─â asigure monitorizarea adecvat─â a respect─ârii programului de conformitate ╚Öi care depune la ANRE, ├«n luna decembrie a fiec─ârui an, un raport cu privire la m─âsurile luate, ce se va public─â pe site-ul operatorului de transport ╚Öi de sistem.......(3)...Proprietarul re╚¢elei de transport transmite spre aprobare, la ANRE, toate proiectele de contracte ce urmeaz─â a fi ├«ncheiate cu operatorul de transport ╚Öi de sistem, inclusiv cele referitoare la utilizarea bunurilor existente, precum ╚Öi a celor realizate ca urmare a investi╚¢iilor ├«n re╚¢eaua de transport.', NULL, 38, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1139, 6, '39', 'Articolul 39', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Raportarea fluxurilor fizice de energie electric─â...Operatorul de transport ╚Öi de sistem este obligat, conform reglement─ârilor ANRE, s─â ├«ntocmeasc─â, din 3 ├«n 3 luni, un raport cu privire la fluxurile fizice de energie electric─â ce s-au derulat ├«n cadrul importurilor de energie electric─â din ╚¢─âri ter╚¢e Uniunii Europene ├«n cursul ultimelor 3 luni anterioare datei raport─ârii, care se transmite ANRE ╚Öi ministerului de resort, ├«n vederea inform─ârii Comisiei Europene.', NULL, 39, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1140, 6, '40', 'Articolul 40', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii privind de╚¢inerea controlului asupra operatorilor de transport ╚Öi de sistem...Operatorilor economici care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare de energie electric─â le este interzis ca, direct ori indirect, s─â exercite controlul ori s─â exercite vreun drept cu privire la operatorii de transport ╚Öi de sistem separa╚¢i din alte state ale Uniunii Europene care aplic─â dispozi╚¢iile art. 9 alin. (1) din Directiva 2009/72/CE.', NULL, 40, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1141, 6, '41', 'Articolul 41', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii privind personalul operatorului de transport ╚Öi de sistem...Persoanele care exercit─â ├«n cadrul operatorului de transport ╚Öi de sistem func╚¢ii de conducere sau alte func╚¢ii relevante ├«n temeiul c─ârora au avut acces la informa╚¢ii comerciale sensibile nu pot ocupa func╚¢ii similare ├«n cadrul operatorilor economici din domeniul producerii, distribu╚¢iei ╚Öi/sau furniz─ârii de energie electric─â, pe o perioad─â de minimum 2 ani de la data ├«ncet─ârii raporturilor contractuale cu operatorul de transport ╚Öi de sistem, interdic╚¢ie prev─âzut─â ╚Öi reglementat─â ├«n contractul individual de munc─â.', NULL, 41, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1142, 6, '42', 'Articolul 42', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii...Pentru protec╚¢ia instala╚¢iilor de transport se interzice persoanelor fizice sau juridice:a)s─â efectueze construc╚¢ii de orice fel ├«n zona de siguran╚¢─â a instala╚¢iilor, f─âr─â avizul de amplasament al operatorului de transport ╚Öi de sistem;...b)s─â efectueze s─âp─âturi de orice fel sau s─â ├«nfiin╚¢eze planta╚¢ii ├«n zona de siguran╚¢─â a re╚¢elelor electrice de transport, f─âr─â acordul operatorului de transport ╚Öi de sistem;...c)s─â depoziteze materiale pe culoarele de trecere ╚Öi ├«n zonele de protec╚¢ie ╚Öi de siguran╚¢─â a instala╚¢iilor, f─âr─â acordul operatorului de transport ╚Öi de sistem;...d)s─â arunce obiecte de orice fel pe re╚¢elele electrice de transport sau s─â intervin─â ├«n orice alt mod asupra acestora;...e)s─â deterioreze construc╚¢iile, ├«ngr─âdirile sau inscrip╚¢iile de identificare ╚Öi de avertizare aferente instala╚¢iilor de transport;...f)s─â limiteze sau s─â ├«ngr─âdeasc─â, prin execu╚¢ia de ├«mprejmuire, prin construc╚¢ii ori prin orice alt mod, accesul la instala╚¢ii al operatorului de transport ╚Öi de sistem....', NULL, 42, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1143, 6, '43', 'Articolul 43', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Operatorul pie╚¢ei de energie electric─â...(1)...Operatorul pie╚¢ei centralizate de energie electric─â este persoana juridic─â titular─â de licen╚¢─â care asigur─â organizarea ╚Öi administrarea pie╚¢elor centralizate de energie electric─â, cu excep╚¢ia pie╚¢ei de echilibrare, ├«n vederea tranzac╚¢ion─ârii de energie electric─â pe termen scurt, mediu ╚Öi lung, conform reglement─ârilor emise de autoritatea competent─â....(2)...Operatorului pie╚¢ei de energie electric─â nu ├«i este permis─â divulgarea informa╚¢iilor legate de tranzac╚¢iile de energie electric─â pe care le de╚¢ine, ob╚¢inute pe parcursul activit─â╚¢ii sale, altfel dec├ót ├«n condi╚¢iile legii....(3)...Pre╚¢urile stabilite pe pie╚¢ele centralizate de energie electric─â se fac publice conform reglement─ârilor ANRE.', NULL, 43, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1144, 6, '44', 'Articolul 44', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Distribu╚¢ia energiei electrice...(1)...Distribu╚¢ia energiei electrice se realizeaz─â de c─âtre operatorul de distribu╚¢ie, persoan─â juridic─â, titular─â de licen╚¢─â....(2)...Operatorii de distribu╚¢ie presteaz─â servicii pentru to╚¢i utilizatorii re╚¢elelor electrice de distribu╚¢ie, ├«n condi╚¢ii nediscriminatorii, asigur├ónd accesul la acestea oric─ârui solicitant care ├«ndepline╚Öte cerin╚¢ele prezentei legi, cu respectarea normelor ╚Öi standardelor de performan╚¢─â prev─âzute ├«n reglement─ârile tehnice ├«n vigoare....(3)...Re╚¢elele electrice de distribu╚¢ie se delimiteaz─â fa╚¢─â de instala╚¢iile de producere sau de re╚¢elele electrice de transport ╚Öi de cele ale clien╚¢ilor finali ├«n punctele de delimitare patrimonial─â....(4)...Terenurile pe care se situeaz─â re╚¢elele electrice de distribu╚¢ie existente la intrarea ├«n vigoare a prezentei legi sunt ╚Öi r─âm├ón ├«n proprietatea public─â a statului....(5)...Fac excep╚¢ie de la prevederile alin. (4) terenurile pentru care operatorul de distribu╚¢ie, titular de licen╚¢─â, a dob├óndit dreptul de proprietate, ├«n condi╚¢iile legii....(6)...Re╚¢elele electrice de distribu╚¢ie se dezvolt─â pe baza principiilor de eficien╚¢─â economic─â, cu respectarea planurilor de urbanism, a dreptului de proprietate, a protec╚¢iei mediului, a s─ân─ât─â╚¢ii ╚Öi vie╚¢ii persoanelor ╚Öi cu economisirea energiei, conform normelor tehnice ╚Öi de siguran╚¢─â cuprinse ├«n reglement─ârile tehnice ├«n vigoare....(7)...Cheltuielile pentru modificarea instala╚¢iilor de distribu╚¢ie a energiei electrice, ca urmare a racord─ârii de noi utilizatori sau a schimb─ârii caracteristicilor energetice ini╚¢iale ale utilizatorilor existen╚¢i, inclusiv pentru eliberarea unor amplasamente, sunt suportate conform reglement─ârilor emise de autoritatea competent─â, ├«n baza unor criterii obiective....(8)...├Än cazul ├«n care cheltuielile prev─âzute la alin. (7) sunt suportate de c─âtre client, capacit─â╚¢ile energetice rezultate vor fi preluate de c─âtre operatorul de distribu╚¢ie cu desp─âgubire, conform unei metodologii aprobate de ANRE, ├«n 60 de zile de la data intr─ârii ├«n vigoare a prezentei legi....(9)...P├ón─â la data intr─ârii ├«n vigoare a reglement─ârilor emise de autoritatea competent─â, suportarea cheltuielilor de modificare a instala╚¢iilor se afl─â ├«n sarcina celui care a generat modificarea....(10)...Operatorul de distribu╚¢ie este obligat s─â ofere o solu╚¢ie de racordare la re╚¢ea a tuturor solicitan╚¢ilor ╚Öi s─â comunice condi╚¢iile de racordare, conform reglement─ârilor ├«n vigoare.', NULL, 44, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1145, 6, '45', 'Articolul 45', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Operatorul de distribu╚¢ie...(1)...Operatorul de distribu╚¢ie are urm─âtoarele atribu╚¢ii principale:a)exploateaz─â, retehnologizeaz─â, reabiliteaz─â ╚Öi dezvolt─â re╚¢elele electrice de distribu╚¢ie, cu respectarea reglement─ârilor tehnice ├«n vigoare;...b)asigur─â tranzitarea energiei electrice prin re╚¢elele electrice de distribu╚¢ie, la cererea ╚Öi cu informarea operatorului de transport ╚Öi de sistem, pentru acele zone ale ╚¢─ârii ├«n care nu exist─â capacitate suficient─â prin re╚¢elele de transport, pentru evacuarea puterii din centrale electrice, inclusiv centrale electrice de cogenerare, pentru conectarea la sistemul electroenergetic al unei ╚¢─âri vecine, ├«n condi╚¢iile existen╚¢ei unui acord bilateral ├«n acest sens, ├«n cazul inciden╚¢elor ├«n SEN ╚Öi al execut─ârii lucr─ârilor de exploatare ╚Öi mentenan╚¢─â sau al lucr─ârilor noi ├«n re╚¢elele de transport, care pun ├«n indisponibilitate temporar─â re╚¢elele de transport din zon─â;...c)realizeaz─â lucr─âri de dezvoltare a re╚¢elelor electrice de distribu╚¢ie prin programe de dezvoltare optim─â a acestora, pe baza studiilor de perspectiv─â, prin consultare, dup─â caz, cu operatorul de transport ╚Öi de sistem ╚Öi prin programe specifice de modernizare pentru instala╚¢ii;...d)asigur─â conducerea operativ─â ├«n conformitate cu licen╚¢a de distribu╚¢ie, acord├ónd prioritate ├«n dispecerizare instala╚¢iilor de producere care folosesc surse de energie regenerabil─â sau celor care produc energie electric─â ├«n cogenerare de ├«nalt─â eficien╚¢─â, ├«n m─âsura ├«n care func╚¢ionarea sigur─â a re╚¢elei na╚¢ionale de energie electric─â permite acest lucru;...e)difuzeaz─â, ├«n mod nediscriminatoriu, informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de re╚¢ea, nefiindu-i permis─â divulgarea informa╚¢iilor comerciale cu caracter confiden╚¢ial ob╚¢inute pe parcursul activit─â╚¢ii sale;...f)supune aprob─ârii operatorului de transport ╚Öi de sistem programul repara╚¢iilor ╚Öi lucr─ârilor de ├«ntre╚¢inere planificate ├«n instala╚¢iile cu tensiunea nominal─â de linie de 110 kV;...g)monitorizeaz─â siguran╚¢a ├«n func╚¢ionare a re╚¢elelor electrice de distribu╚¢ie, precum ╚Öi indicatorii de performan╚¢─â a serviciului de distribu╚¢ie.......(2)...├Än scopul ├«ndeplinirii atribu╚¢iilor prev─âzute la alin. (1), operatorul de distribu╚¢ie poate ├«ntrerupe func╚¢ionarea instala╚¢iilor pentru timpul strict necesar lucr─ârilor de ├«ntre╚¢inere ╚Öi repara╚¢ii planificate, cu anun╚¢area prealabil─â a utilizatorilor re╚¢elei electrice de distribu╚¢ie....(3)...Operatorul de distribu╚¢ie poate participa la pia╚¢a angro de energie electric─â numai pentru tranzac╚¢ii necesare acoperirii consumului propriu tehnologic aferent re╚¢elelor electrice de distribu╚¢ie ╚Öi pentru locurile de consum proprii pe baza unor proceduri transparente ╚Öi nediscriminatorii, cu respectarea mecanismelor concuren╚¢iale aprobate de autoritatea competent─â....(4)...Atribu╚¢iile privind dezvoltarea sistemului de distribu╚¢ie prev─âzute la alin. (1) lit. a)-c) nu sunt aplicabile operatorului unui sistem de distribu╚¢ie ├«nchis....(5)...Operatorul de distribu╚¢ie are obliga╚¢ia execut─ârii lucr─ârilor de realizare/retehnologizare/reabilitare/modernizare a re╚¢elelor electrice ├«n vederea cre╚Öterii nivelului de calitate al serviciului de distribu╚¢ie a energiei electrice. ├Än acest scop ╚Öi pentru adoptarea unor solu╚¢ii tehnice c├ót mai eficiente, echipamentele tehnice pot fi amplasate pe domeniul public, ├«n zone intravilane ├«n variantele constructive subteran─â sau suprateran─â, f─âr─â afectarea circula╚¢iei publice, cu respectarea reglement─ârilor tehnice aplicabile ╚Öi a condi╚¢iilor tehnice standardizate ├«n vigoare privind amplasarea re╚¢elelor edilitare....(6)...Situa╚¢iile existente la data intr─ârii ├«n vigoare a prezentei legi, contrare prevederilor alin. (5), sunt solu╚¢ionate de operatorul de distribu╚¢ie ├«n termen de 60 de zile de la data primirii unei solicit─âri ├«n acest sens de la persoanele ├«ndrept─â╚¢ite.', NULL, 45, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1153, 6, '53', 'Articolul 53', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile furnizorului de ultim─â instan╚¢─â...(1)...Furnizorul de ultim─â instan╚¢─â are obliga╚¢ia de a asigura serviciul universal clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) lit. b)....(2)...Furnizorul de ultim─â instan╚¢─â are obliga╚¢ia de a furniza energie electric─â clien╚¢ilor finali al c─âror furnizor se afl─â ├«n situa╚¢ia de a i se retrage licen╚¢a de furnizare ├«n cursul desf─â╚Öur─ârii activit─â╚¢ii sau ├«n orice alt─â situa╚¢ie identificat─â de autoritatea competent─â ├«n care clien╚¢ii finali nu au asigurat─â furnizarea de energie electric─â din nicio alt─â surs─â, cu excep╚¢ia clien╚¢ilor deconecta╚¢i pentru sustragere de energie electric─â ori neplat─â, conform reglement─ârilor emise de autoritatea competent─â.', NULL, 53, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1146, 6, '46', 'Articolul 46', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile proprietarului re╚¢elei de distribu╚¢ie...(1)...Orice persoan─â care de╚¢ine ├«n proprietate sau ├«n folosin╚¢─â o re╚¢ea electric─â de distribu╚¢ie proprie la data intr─ârii ├«n vigoare a prezentei legi este obligat─â s─â asigure accesul la re╚¢ea, conform reglement─ârilor autorit─â╚¢ii competente....(2)...├Än cazul ├«n care de╚¢in─âtorul re╚¢elei de distribu╚¢ie a energiei electrice se afl─â ├«n imposibilitatea de a asigura serviciul universal pentru utilizatorii racorda╚¢i la acea re╚¢ea, operatorul de distribu╚¢ie concesionar, titular de licen╚¢─â ├«n zona ├«n care se afl─â re╚¢eaua, va prelua aceast─â re╚¢ea dup─â plata unei juste desp─âgubiri, cu avizul ANRE....(3)...├Än cazul ├«n care re╚¢eaua electric─â de distribu╚¢ie nu se afl─â ├«n proprietatea operatorului de distribu╚¢ie, proprietarul re╚¢elei de distribu╚¢ie are urm─âtoarele obliga╚¢ii:a)coopereaz─â cu operatorul de distribu╚¢ie ├«n vederea ├«ndeplinirii atribu╚¢iilor acestuia, furniz├óndu-i toate informa╚¢iile relevante;...b)coopereaz─â cu ANRE ╚Öi cu operatorul de distribu╚¢ie la stabilirea investi╚¢iilor ├«n re╚¢eaua de distribu╚¢ie, a modalit─â╚¢ii de finan╚¢are a acestora, precum ╚Öi a transferului serviciului de distribu╚¢ie c─âtre alt operator de distribu╚¢ie titular de licen╚¢─â;...c)de╚¢ine r─âspunderea privind activele re╚¢elei de distribu╚¢ie, cu excep╚¢ia r─âspunderii privind atribu╚¢iile operatorului de distribu╚¢ie;...d)faciliteaz─â finan╚¢area eventualelor extinderi ale re╚¢elei, cu excep╚¢ia investi╚¢iilor pentru care ╚Öi-a dat acordul s─â fie finan╚¢ate de c─âtre orice parte interesat─â, inclusiv de c─âtre operatorul de distribu╚¢ie....', NULL, 46, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1147, 6, '47', 'Articolul 47', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Limitarea sau ├«ntreruperea aliment─ârii...(1)...Operatorul de distribu╚¢ie este ├«ndrept─â╚¢it s─â ├«ntrerup─â alimentarea cu energie electric─â ├«n urm─âtoarele situa╚¢ii:a)c├ónd se pericliteaz─â via╚¢a, s─ân─âtatea oamenilor ori integritatea bunurilor materiale;...b)pentru prevenirea sau limitarea extinderii avariilor ├«n echipamentele energetice, ├«n zone de re╚¢ea electric─â sau la nivelul ├«ntregului SEN;...c)pentru executarea unor manevre sau lucr─âri ce nu se pot efectua f─âr─â ├«ntreruperi.......(2)...Operatorul de distribu╚¢ie este obligat s─â anun╚¢e utilizatorii afecta╚¢i de ├«ntreruperile planificate, respectiv pe furnizorii acestora, ├«n modul stabilit prin contracte ╚Öi s─â comunice durata planificat─â pentru ├«ntreruperile necesare execut─ârii unor lucr─âri de ├«ntre╚¢inere ╚Öi repara╚¢ii....(3)...Operatorul de distribu╚¢ie r─âspunde pentru daunele cauzate utilizatorilor din culpa sa....(4)...Prin regulamentul de furnizare se stabilesc condi╚¢iile ├«n care, ├«n situa╚¢ii excep╚¢ionale, se poate limita sau ├«ntrerupe alimentarea cu energie electric─â a clien╚¢ilor finali.', NULL, 47, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1148, 6, '48', 'Articolul 48', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Separarea activit─â╚¢ilor de distribu╚¢ie ╚Öi furnizare...(1)...├Än cazul ├«n care operatorul de distribu╚¢ie face parte dintr-un operator economic integrat pe vertical─â, acesta trebuie s─â fie independent cel pu╚¢in ├«n ceea ce prive╚Öte forma sa juridic─â, organizarea ╚Öi procesul decizional, ├«n raport cu celelalte activit─â╚¢i care nu au leg─âtur─â cu distribu╚¢ia. Aceast─â regul─â nu creeaz─â obliga╚¢ia separ─ârii propriet─â╚¢ii asupra activelor apar╚¢in├ónd operatorului de distribu╚¢ie fa╚¢─â de operatorul economic integrat pe vertical─â....(2)...Suplimentar fa╚¢─â de cerin╚¢ele prev─âzute la alin. (1), ├«n cazul ├«n care operatorul de distribu╚¢ie face parte dintr-un operator economic integrat pe vertical─â, acesta trebuie s─â fie independent ├«n ceea ce prive╚Öte organizarea sa ╚Öi procesul decizional, ├«n raport cu celelalte activit─â╚¢i care nu au leg─âtur─â cu distribu╚¢ia. ├Än acest scop, trebuie aplicate urm─âtoarele criterii minimale:a)persoanele responsabile pentru managementul operatorului de distribu╚¢ie nu pot participa ├«n structurile operatorului economic integrat pe vertical─â responsabile direct sau indirect pentru operarea zilnic─â a activit─â╚¢ilor de producere, transport sau furnizare de energie electric─â;...b)trebuie luate m─âsuri corespunz─âtoare pentru a se asigura c─â interesele profesionale ale persoanelor responsabile pentru managementul operatorului de distribu╚¢ie sunt luate ├«n considerare, ├«ntr-o manier─â de natur─â s─â asigure independen╚¢a de ac╚¢iune a acestora;...c)operatorul de distribu╚¢ie are dreptul de a lua decizii, ├«n mod efectiv, independent de operatorul economic integrat pe vertical─â, cu privire la activele necesare pentru operarea, men╚¢inerea sau dezvoltarea re╚¢elei. Se interzice societ─â╚¢ii-mam─â s─â dea orice fel de instruc╚¢iuni privind activitatea de distribu╚¢ie sau s─â ia decizii individuale ce au ├«n vedere construirea ori reabilitarea unor capacit─â╚¢i energetice de distribu╚¢ie care nu dep─â╚Öesc termenii situa╚¢iilor financiare aprobate sau ai oric─ârui instrument echivalent;...d)operatorul de distribu╚¢ie stabile╚Öte un program de conformitate, care con╚¢ine m─âsurile luate pentru a garanta excluderea practicilor discriminatorii ╚Öi stabile╚Öte ╚Öi obliga╚¢iile specifice impuse angaja╚¢ilor pentru realizarea obiectivului de independen╚¢─â;...e)operatorul de distribu╚¢ie desemneaz─â o persoan─â sau un organism, denumit─â/denumit agent de conformitate, care s─â asigure monitorizarea adecvat─â a respect─ârii programului de conformitate ╚Öi care depune la ANRE, ├«n luna decembrie a fiec─ârui an, un raport cu privire la m─âsurile luate ce se va public─â pe site-ul operatorului de distribu╚¢ie. Agentul de conformitate al operatorului de distribu╚¢ie este complet independent ╚Öi are acces la toate informa╚¢iile operatorului de distribu╚¢ie sau ale oric─ârui operator economic afiliat, care sunt necesare ├«n vederea ├«ndeplinirii atribu╚¢iilor sale.......(3)...Operatorii economici integra╚¢i pe vertical─â, care furnizeaz─â servicii pentru mai pu╚¢in de 100.000 de clien╚¢i conecta╚¢i sau care deservesc sisteme mici, izolate, nu au obliga╚¢ia de implementare a m─âsurilor prev─âzute la alin. (1) ╚Öi (2)....(4)...Operatorul de distribu╚¢ie este obligat s─â p─âstreze confiden╚¢ialitatea informa╚¢iilor comerciale ob╚¢inute ├«n cursul desf─â╚Öur─ârii activit─â╚¢ii sale....(5)...Operatorul de distribu╚¢ie este obligat s─â ├«mpiedice divulgarea discriminatorie a informa╚¢iilor privind propria activitate, care pot fi avantajoase din punct de vedere comercial....(6)...Pentru a ├«ndeplini obliga╚¢iile prev─âzute la alin. (2) lit. c), operatorul de distribu╚¢ie trebuie s─â aib─â la dispozi╚¢ie resursele necesare, care includ resurse umane, tehnice ╚Öi financiare....(7)...├Än desf─â╚Öurarea activit─â╚¢ii economice, inclusiv de comunicare ╚Öi publicitate, operatorul de distribu╚¢ie este obligat s─â nu creeze confuzie cu privire la identitatea separat─â fa╚¢─â de operatorul economic care realizeaz─â serviciul de furnizare a energiei electrice din cadrul operatorului economic integrat pe vertical─â....(8)...ANRE monitorizeaz─â activitatea operatorului de distribu╚¢ie care face parte dintr-un operator economic integrat pe vertical─â, lu├ónd m─âsuri pentru a evita sau elimina eventuale practici care denatureaz─â concuren╚¢a.', NULL, 48, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1149, 6, '49', 'Articolul 49', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii...Pentru protejarea re╚¢elelor electrice de distribu╚¢ie, se interzice persoanelor fizice ╚Öi juridice:a)s─â efectueze construc╚¢ii de orice fel ├«n zona de siguran╚¢─â a re╚¢elelor electrice de distribu╚¢ie, f─âr─â avizul de amplasament al operatorului de distribu╚¢ie;...b)s─â efectueze s─âp─âturi de orice fel sau s─â ├«nfiin╚¢eze planta╚¢ii ├«n zona de siguran╚¢─â a re╚¢elelor electrice de distribu╚¢ie, f─âr─â acordul operatorului de distribu╚¢ie;...c)s─â depoziteze materiale pe culoarul de trecere ╚Öi ├«n zonele de protec╚¢ie ╚Öi de siguran╚¢─â ale instala╚¢iilor, f─âr─â acordul operatorului de distribu╚¢ie;...d)s─â arunce obiecte de orice fel pe re╚¢elele electrice de distribu╚¢ie sau s─â intervin─â ├«n oricare alt mod asupra acestora;...e)s─â deterioreze construc╚¢iile, ├«ngr─âdirile sau inscrip╚¢iile de identificare ╚Öi avertizare aferente re╚¢elelor electrice de distribu╚¢ie;...f)s─â limiteze sau s─â ├«ngr─âdeasc─â, prin execu╚¢ia de ├«mprejmuire, prin construc╚¢ii ori prin orice alt mod, accesul la instala╚¢ii al operatorului de distribu╚¢ie....', NULL, 49, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1150, 6, '50', 'Articolul 50', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Sistemul de distribu╚¢ie ├«nchis...(1)...Sistemul de distribu╚¢ie ├«nchis este acel sistem prin care se distribuie energie electric─â ├«ntr-o zon─â industrial─â, comercial─â sau de servicii comune, limitat─â din punct de vedere geografic ╚Öi care, f─âr─â a aduce atingere dispozi╚¢iilor alin. (4), nu alimenteaz─â clien╚¢ii casnici, dac─â:a)din motive tehnice sau de securitate specifice, activit─â╚¢ile ori procesul de produc╚¢ie desf─â╚Öurate de utilizatorii sistemului respectiv sunt integrate; sau...b)sistemul respectiv distribuie energie electric─â, ├«n principal proprietarului sistemului de distribu╚¢ie, operatorului acestuia ori operatorilor economici afilia╚¢i acestora.......(2)...Operatorului unui sistem de distribu╚¢ie ├«nchis nu i se aplic─â:a)obliga╚¢ia de a cump─âra energia electric─â necesar─â acoperirii consumului propriu tehnologic pe baza unor proceduri transparente, nediscriminatorii, ├«n condi╚¢iile art. 36 alin. (4);...b)obliga╚¢ia ca tarifele de racordare la re╚¢ea sau metodologiile care stau la baza calcul─ârii acestora s─â fie aprobate ├«nainte de intrarea ├«n vigoare a acestora.......(3)...Tarifele pentru serviciul de distribu╚¢ie dintr-un sistem de distribu╚¢ie ├«nchis sau metodologiile care stau la baza calcul─ârii acestora pot fi ╚Öi aprobate ╚Öi revizuite de ANRE, la cererea unui utilizator al sistemului de distribu╚¢ie ├«nchis....(4)...Dispozi╚¢iile alin. (2) se aplic─â ╚Öi ├«n cazul ├«n care ├«n zona deservit─â de un sistem de distribu╚¢ie ├«nchis sunt amplasa╚¢i clien╚¢i casnici, numai dac─â consumul anual al acestora este mai mic de 5% din consumul anual al sistemului ├«nchis ╚Öi dac─â ace╚Ötia se afl─â ├«ntr-un raport de munc─â sau ├«ntr-o form─â de asociere cu proprietarul sistemului de distribu╚¢ie.', NULL, 50, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1151, 6, '51', 'Articolul 51', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Electrificarea localit─â╚¢ilor ╚Öi extinderea re╚¢elelor de distribu╚¢ie a energiei electrice...(1)...├Än executarea contractului de concesiune, la solicitarea autorit─â╚¢ilor administra╚¢iei publice locale sau centrale, ├«n baza planurilor de dezvoltare regional─â ╚Öi de urbanism, operatorul de distribu╚¢ie este obligat s─â asigure dezvoltarea ╚Öi finan╚¢area re╚¢elei de distribu╚¢ie pentru electrificarea localit─â╚¢ilor ori pentru extinderea re╚¢elelor de distribu╚¢ie ├«n zona acoperit─â de contractul de concesiune, respectiv de licen╚¢─â, pe care acesta o de╚¢ine....(2)...├Än cazul ├«n care realizarea investi╚¢iilor de la alin. (1) nu este justificat─â economic pentru operatorul de distribu╚¢ie, acesta poate propune, dup─â informarea ANRE, realizarea obiectivelor respective prin coparticiparea solicitantului la finan╚¢are cu fonduri din bugetele locale ╚Öi din bugetul de stat, ├«n condi╚¢iile legii....(3)...Evaluarea condi╚¢iilor de finan╚¢are a investi╚¢iilor prev─âzute la alin. (1) ╚Öi (2) se determin─â de operatorul de distribu╚¢ie, ├«n baza unui studiu de fezabilitate realizat ├«n termen de maximum 60 de zile de la primirea solicit─ârii, conform unei metodologii aprobate de ANRE....(4)...Pentru dezvoltarea programelor de electrificare a localit─â╚¢ilor ╚Öi de extindere a re╚¢elelor de distribu╚¢ie a energiei electrice, autorit─â╚¢ile administra╚¢iei publice locale ╚Öi ministerele implicate vor r─âspunde, ├«n termen de 60 de zile de la primirea solicit─ârilor operatorului de transport ╚Öi sistem, precum ╚Öi ale operatorilor de distribu╚¢ie, pentru realizarea planurilor de dezvoltare a re╚¢elei pe termen mediu ╚Öi lung....(5)...Localit─â╚¢ile care, din considerente tehnice sau economice, nu sunt conectate la SEN pot fi alimentate cu energie electric─â prin sisteme electroenergetice izolate....(6)...ANRE va stabili pre╚¢ul local ╚Öi condi╚¢iile minime privind continuitatea ╚Öi calitatea serviciului de furnizare.', NULL, 51, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1154, 6, '54', 'Articolul 54', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Desemnarea furnizorilor de ultim─â instan╚¢─â...Furnizorii de ultim─â instan╚¢─â se desemneaz─â de c─âtre ANRE dintre furnizorii existen╚¢i pe pia╚¢a de energie, prin mecanisme concuren╚¢iale, pe baza unui regulament care stabile╚Öte modalit─â╚¢ile ╚Öi criteriile de selec╚¢ie a acestora, pentru fiecare categorie de clien╚¢i finali pe care ace╚Ötia ├«i deservesc.', NULL, 54, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1155, 6, '55', 'Articolul 55', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Clien╚¢ii furnizorilor de ultim─â instan╚¢─â...(1)...Furnizorii de ultim─â instan╚¢─â sunt obliga╚¢i s─â asigure furnizarea de energie electric─â, ├«n condi╚¢ii de calitate ╚Öi la pre╚¢uri rezonabile, transparente, u╚Öor comparabile ╚Öi nediscriminatorii, conform reglement─ârilor ANRE, cu respectarea prevederilor art. 22 alin. (1), urm─âtoarelor categorii de clien╚¢i:a)clien╚¢ii finali care, la data intr─ârii ├«n vigoare a prezentei legi, nu ╚Öi-au exercitat dreptul de eligibilitate;...b)clien╚¢ii casnici ╚Öi clien╚¢ii noncasnici cu un num─âr mediu scriptic de salaria╚¢i mai mic de 50 ╚Öi o cifr─â de afaceri anual─â sau o valoare total─â a activelor din bilan╚¢ul contabil, conform raport─ârilor fiscale anuale, care nu dep─â╚Öe╚Öte 10 milioane euro.......(2)...Identificarea clien╚¢ilor finali noncasnici care ├«ndeplinesc condi╚¢iile men╚¢ionate la alin. (1) lit. b) se va realiza de c─âtre furnizorii de ultim─â instan╚¢─â, ├«n baza documentelor prev─âzute de legisla╚¢ia specific─â, transmise de clien╚¢i furnizorilor de ultim─â instan╚¢─â. Termenul pentru finalizarea identific─ârii acestora este de maximum 12 luni de la data intr─ârii ├«n vigoare a prezentei legi....(3)...Dup─â finalizarea procesului de identificare, clien╚¢ii finali noncasnici prev─âzu╚¢i la alin. (2) au obliga╚¢ia de a transmite, o dat─â la 2 ani, documentele men╚¢ionate la alin. (2) furnizorului de ultim─â instan╚¢─â.', NULL, 55, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1156, 6, '56', 'Articolul 56', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Trecerea clien╚¢ilor finali la furnizorul de ultim─â instan╚¢─â...(1)...Trecerea unui client aflat ├«n una dintre situa╚¢iile prev─âzute la art. 53 alin. (2) la furnizorul de ultim─â instan╚¢─â se face conform reglement─ârilor emise de autoritatea competent─â, f─âr─â a fi necesar─â semnarea unui contract de furnizare....(2)...├Än cazul schimb─ârii furnizorului ca urmare a trecerii la furnizorul de ultim─â instan╚¢─â ├«n condi╚¢iile art. 53 alin. (2), operatorul re╚¢elei la care este racordat locul de consum va asigura continuitatea ├«n alimentarea cu energie electric─â a acestuia, iar energia electric─â ╚Öi serviciile aferente furniz─ârii ├«n aceste condi╚¢ii vor fi suportate prin intermediul furnizorului de ultim─â instan╚¢─â....(3)...Furnizarea de energie electric─â de c─âtre furnizorul de ultim─â instan╚¢─â se face la tarife stabilite/pre╚¢uri avizate ├«n condi╚¢iile reglement─ârilor emise de autoritatea competent─â.', NULL, 56, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1157, 6, '57', 'Articolul 57', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile furnizorului...(1)...Furnizorul trebuie s─â pun─â la dispozi╚¢ia clien╚¢ilor casnici puncte unice de contact, conform reglement─ârilor ANRE, pentru informarea acestora cu privire la drepturile acestora, la legisla╚¢ia ├«n vigoare ╚Öi la c─âile de solu╚¢ionare a litigiilor ├«n cazul unor dezacorduri sau pl├óngeri....(2)...Furnizorul trebuie s─â pun─â la dispozi╚¢ia clien╚¢ilor casnici mai multe modalit─â╚¢i de plat─â a energiei furnizate ╚Öi s─â permit─â acestora s─â opteze pentru oricare dintre acestea....(3)...Furnizorul este obligat s─â asigure etichetarea energiei electrice conform reglement─ârilor autorit─â╚¢ii competente ╚Öi s─â informeze, periodic, clien╚¢ii finali privind structura, provenien╚¢a, caracteristicile ╚Öi impactul asupra mediului pentru energia electric─â furnizat─â acestora....(4)...Furnizorul este obligat s─â participe la activit─â╚¢i prin care se asigur─â siguran╚¢a ╚Öi securitatea SEN.', NULL, 57, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1158, 6, '58', 'Articolul 58', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢ia de contractare...(1)...La cererea scris─â a unui client final, furnizorul este obligat s─â comunice, ├«n scris, ├«ntr-un interval de cel mult 15 zile lucr─âtoare, o ofert─â privind condi╚¢iile de furnizare a energiei electrice, care con╚¢ine, obligatoriu, pre╚¢ul de furnizare, termenii de plat─â ╚Öi data-limit─â de ├«ncheiere a contractului de furnizare, elaborat ├«n conformitate cu reglement─ârile emise de autoritatea competent─â....(2)...Contractul de furnizare ├«ncheiat ├«n baza prevederilor alin. (1) este valabil o perioad─â nedeterminat─â sau pe o perioad─â convenit─â de p─âr╚¢i....(3)...Clientul final are dreptul de a denun╚¢a unilateral contractul de furnizare, cu notificare transmis─â furnizorului de energie electric─â cu cel pu╚¢in 21 de zile ├«nainte, cu respectarea condi╚¢iilor contractuale....(4)...Furnizorul este ├«ndrept─â╚¢it s─â rezilieze contractul ├«n urm─âtoarele cazuri:a)sustragerea de energie electric─â constatat─â prin hot─âr├óre judec─âtoreasc─â definitiv─â;...b)neplata facturilor;...c)alte situa╚¢ii prev─âzute de legisla╚¢ia ├«n vigoare.......(5)...Furnizorul este ├«ndrept─â╚¢it s─â solicite unui client final constituirea de garan╚¢ii financiare pentru o perioad─â de consum echivalent de maximum un an ├«n cazul constat─ârii, conform prevederilor legale ├«n vigoare, a unor ac╚¢iuni menite s─â denatureze ├«n orice fel indica╚¢iile echipamentelor de m─âsurare sau s─â sustrag─â energia electric─â prin ocolirea echipamentelor de m─âsurare din partea acestuia. Modalitatea de constituire a garan╚¢iilor financiare este prev─âzut─â ├«n contractele-cadru de furnizare a energiei electrice ╚Öi/sau ├«n alte reglement─âri emise de ANRE....(6)...Refuzul constituirii garan╚¢iilor prev─âzute la alin. (5) d─â dreptul furnizorului s─â solicite operatorului de distribu╚¢ie deconectarea clientului final de la re╚¢ea.', NULL, 58, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1159, 6, '59', 'Articolul 59', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Plata energiei furnizate├Än conformitate cu prevederile contractului, clientul final este obligat s─â pl─âteasc─â contravaloarea energiei electrice consumate la pre╚¢ul ╚Öi ├«n condi╚¢iile prev─âzute ├«n contractul de furnizare a energiei electrice....', NULL, 59, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1160, 6, '60', 'Articolul 60', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'R─âspunderea furnizorului...(1)...Furnizorul r─âspunde pentru toate daunele provocate clientului final din culpa sa, ├«n condi╚¢iile stabilite prin contractul de furnizare....(2)...Furnizorul este ├«ndrept─â╚¢it s─â recupereze daunele produse clien╚¢ilor finali ╚Öi, respectiv, furnizorului ├«nsu╚Öi de la operatorul de distribu╚¢ie, operatorul de transport ╚Öi de sistem sau de la produc─âtor, dovedite a fi cauzate din culpa oric─âruia dintre ace╚Ötia.', NULL, 60, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1167, 6, '67', 'Articolul 67', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Definirea surselor regenerabile de energie...├Än condi╚¢iile prezentului titlu, se definesc ca surse regenerabile de energie:a)energia eolian─â;...b)energia solar─â;...c)energia valurilor ╚Öi a mareelor;...d)energia geotermal─â;...e)energia hidroelectric─â;...f)energia con╚¢inut─â ├«n frac╚¢iunea biodegradabil─â a produselor, de╚Öeurilor ╚Öi reziduurilor din agricultur─â (inclusiv substan╚¢e vegetale ╚Öi reziduuri de origine animal─â), silvicultur─â ╚Öi industrii conexe, precum ╚Öi frac╚¢iunea biodegradabil─â a de╚Öeurilor industriale ╚Öi comunale, or─â╚Öene╚Öti ╚Öi municipale, denumit─â biomas─â;...g)energia con╚¢inut─â ├«n gazul de fermentare a de╚Öeurilor, denumit ╚Öi gaz de depozit;...h)energia con╚¢inut─â ├«n gazul de fermentare a n─âmolurilor din instala╚¢iile de epurare a apelor uzate;...i)energia con╚¢inut─â ├«n produse secundare gazoase, ob╚¢inute prin fermentare din materii reziduale organice, form├ónd categoria de combustibil gazos, denumit─â biogaz;...j)energia con╚¢inut─â ├«n produse lichide ob╚¢inute prin distilarea materiei organice fermentate, form├ónd categoria de combustibil lichid, denumit─â alcool carburant;...k)energia ob╚¢inut─â din alte surse regenerabile, neexploatate ├«n prezent....', NULL, 67, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1161, 6, '61', 'Articolul 61', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile clientului final de energie electric─â...(1)...Clientul final de energie electric─â r─âspunde pentru daunele dovedite cauzate furnizorului/operatorului de re╚¢ea din culpa sa....(2)...Nerespectarea contractului de furnizare a energiei electrice de c─âtre clientul final poate atrage suportarea urm─âtoarelor consecin╚¢e, dup─â caz:a)penaliz─âri;...b)sistarea temporar─â a furniz─ârii energiei electrice;...c)rezilierea contractului de furnizare a energiei electrice.......(3)...├Än cazul interven╚¢iilor asupra instala╚¢iilor electrice de c─âtre persoane neautorizate, operatorul de re╚¢ea este ├«ndrept─â╚¢it s─â ├«ntrerup─â alimentarea cu energie electric─â, ├«n conformitate cu reglement─ârile specifice ale ANRE....(4)...├Än cazul constat─ârii, conform prevederilor legale ├«n vigoare, a unor ac╚¢iuni menite s─â denatureze ├«n orice fel indica╚¢iile echipamentelor de m─âsurare sau s─â sustrag─â energia electric─â prin ocolirea echipamentelor de m─âsurare, clientul final este obligat s─â constituie o garan╚¢ie financiar─â, conform prevederilor art. 58 alin. (5)....(5)...Clientul final de energie electric─â cu o putere aprobat─â prin avizul tehnic de racordare de cel pu╚¢in 1.000 kVA este obligat s─â prezinte prognoze orare de consum furnizorului cu care are rela╚¢ii contractuale, conform reglement─ârilor emise de autoritatea competent─â....(6)...Clien╚¢ii finali au obliga╚¢ia pl─â╚¢ii contravalorii facturilor pentru energia electric─â ╚Öi pentru serviciile de care beneficiaz─â la termenele scadente prev─âzute ├«n contractele ├«ncheiate ├«ntre p─âr╚¢i....(7)...Pentru garantarea pl─â╚¢ilor facturilor la energia electric─â, furnizorii de ultim─â instan╚¢─â pot solicita clien╚¢ilor constituirea de garan╚¢ii financiare, numai ├«n condi╚¢iile stabilite de reglement─ârile emise de autoritatea competent─â.', NULL, 61, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1162, 6, '62', 'Articolul 62', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Drepturile clientului final de energie electric─â...(1)...Clien╚¢ii finali de energie electric─â au urm─âtoarele drepturi:a)s─â aib─â acces la re╚¢elele electrice de interes public ╚Öi s─â consume energie electric─â ├«n conformitate cu prevederile contractului de furnizare;...b)s─â solicite furnizorului modificarea ╚Öi completarea contractului de furnizare ╚Öi a anexelor la acesta sau s─â ini╚¢ieze acte adi╚¢ionale la contracte, atunci c├ónd apar elemente noi ori c├ónd consider─â necesar─â detalierea sau completarea unor clauze contractuale;...c)s─â aib─â acces pentru citire la grupurile de m─âsurare pentru decontare;...d)s─â solicite furnizorului sau operatorului de distribu╚¢ie s─â ia m─âsuri pentru remedierea defec╚¢iunilor ╚Öi a deranjamentelor survenite ├«n re╚¢elele electrice;...e)s─â primeasc─â desp─âgubiri de la furnizor, operator de re╚¢ea sau alt client final, conform prevederilor contractuale, c├ónd i se produc daune din vina acestora ori ├«n cazul nerespect─ârii indicatorilor de calitate a energiei;...f)s─â se asocieze voluntar ├«n vederea reprezent─ârii intereselor clien╚¢ilor, conform reglement─ârilor ANRE;...g)s─â achizi╚¢ioneze energia electric─â pe baz─â de contract bilateral de la un furnizor de energie, indiferent de statul membru ├«n care este ├«nregistrat furnizorul, cu condi╚¢ia s─â respecte normele aplicabile de comercializare ╚Öi de echilibrare stabilite pe teritoriul Rom├óniei;...h)s─â schimbe furnizorul, conform unei proceduri aprobate de ANRE;...i)s─â ├«ncheie contracte cu mai mul╚¢i furnizori de energie ├«n acela╚Öi timp, pentru un singur loc de consum, drept ce le revine exclusiv clien╚¢ilor noncasnici de mari dimensiuni, a c─âror putere maxim─â aprobat─â prin avizul tehnic de racordare este stabilit─â prin reglement─ârile ANRE;...j)s─â supun─â solu╚¢ion─ârii autorit─â╚¢ii competente divergen╚¢ele rezultate din derularea contractelor ├«ncheiate ├«n sectorul energiei electrice, ├«n baza unor proceduri elaborate de autoritatea competent─â;...k)s─â beneficieze de serviciile unui mediator, ├«n condi╚¢iile Legii nr. 192/2006 privind medierea ╚Öi organizarea profesiei de mediator, cu modific─ârile ╚Öi complet─ârile ulterioare, ├«n vederea solu╚¢ion─ârii extrajudiciare a litigiilor ap─ârute pe pia╚¢a de energie electric─â;...l)s─â denun╚¢e unilateral contractul de furnizare, ├«n condi╚¢iile art. 58 alin. (3).......(2)...Procedura prev─âzut─â la alin. (1) lit. h) stabile╚Öte ├«n principal etapele procesului de schimbare a furnizorului, modalitatea de stingere a obliga╚¢iilor de plat─â datorate de clientul final furnizorului ce urmeaz─â a fi schimbat, datele ce pot fi solicitate de clientul final sau de noul furnizor ├«n procesul de schimbare, precum ╚Öi operatorii economici care sunt obliga╚¢i s─â le furnizeze....(3)...Procedura prev─âzut─â la alin. (1) lit. h) trebuie s─â garanteze unui client care dore╚Öte s─â schimbe furnizorul, cu respectarea condi╚¢iilor contractuale, c─â aceast─â schimbare se poate realiza ├«n termen de 3 s─âpt─âm├óni de la ini╚¢ierea acesteia.', NULL, 62, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1163, 6, '63', 'Articolul 63', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Dreptul clien╚¢ilor finali de alegere a tarifelor...(1)...Clien╚¢ii finali prev─âzu╚¢i la art. 55 alin. (1) au dreptul s─â opteze pentru oricare dintre tarifele reglementate aplicabile categoriei din care fac parte sau s─â beneficieze de serviciul universal, ├«n condi╚¢iile stabilite prin reglement─ârile ANRE....(2)...Clien╚¢ii casnici au dreptul de a opta pentru orice modalitate de plat─â pe care furnizorul o pune la dispozi╚¢ie....(3)...Clien╚¢ii finali nu mai pot reveni la tarife reglementate dac─â ╚Öi-au exercitat dreptul de eligibilitate.', NULL, 63, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1164, 6, '64', 'Articolul 64', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Protec╚¢ia clien╚¢ilor vulnerabili...(1)...Clien╚¢ii vulnerabili beneficiaz─â de facilit─â╚¢i privind asigurarea serviciului de furnizare a energiei electrice ╚Öi de acces la re╚¢ea....(2)...Tipurile de facilit─â╚¢i pentru fiecare dintre categoriile de clien╚¢i vulnerabili, cu excep╚¢ia m─âsurilor de natur─â financiar─â, precum ╚Öi modalit─â╚¢ile de implementare a acestora se stabilesc de c─âtre ANRE....(3)...Este interzis─â deconectarea de la re╚¢eaua electric─â a clien╚¢ilor vulnerabili inclusiv ├«n situa╚¢ii de criz─â de energie.', NULL, 64, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1165, 6, '65', 'Articolul 65', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'M─âsurarea energiei electrice...(1)...Energia electric─â furnizat─â clien╚¢ilor finali pe pia╚¢a de energie electric─â se m─âsoar─â de c─âtre operatorii de m─âsurare, prin grupuri de m─âsurare, conform codului de m─âsurare elaborat de autoritatea competent─â....(2)...Transformatoarele de m─âsur─â aferente grupurilor de m─âsurare a energiei electrice ├«n vederea decont─ârii se asigur─â, dup─â caz, de:a)operatorul de transport ╚Öi de sistem;...b)produc─âtori;...c)operatorii de distribu╚¢ie;...d)clien╚¢ii finali de energie electric─â.......(3)...Operatorii de m─âsurare pot fi:a)operatorul de transport ╚Öi de sistem;...b)produc─âtorii;...c)operatorul de distribu╚¢ie.......(4)...Operatorul de transport ╚Öi de sistem, respectiv operatorul de distribu╚¢ie au obliga╚¢ia de a presta serviciul de m─âsurare a energiei electrice pentru utilizatorii re╚¢elelor electrice respective....(5)...Operatorul de transport ╚Öi de sistem, respectiv operatorul de distribu╚¢ie pot cesiona serviciul de m─âsurare ╚Öi gestiunea datelor m─âsurate unui operator economic, cu condi╚¢ia ├«ncadr─ârii ├«n costurile recunoscute de autoritatea competent─â ╚Öi cu respectarea reglement─ârilor aplicabile emise de aceasta.', NULL, 65, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1166, 6, '66', 'Articolul 66', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Sisteme de m─âsurare inteligent─â...(1)...P├ón─â la data de 3 septembrie 2012, ANRE va evalua implementarea sistemelor de m─âsurare inteligente din punctul de vedere al costurilor ╚Öi beneficiilor pe termen lung pentru pia╚¢─â, al rentabilit─â╚¢ii, precum ╚Öi al termenelor fezabile de implementare....(2)...├Än situa╚¢ia ├«n care prin evaluarea prev─âzut─â la alin. (1) se constat─â c─â implementarea sistemelor de m─âsurare inteligente este avantajoas─â pentru func╚¢ionarea pie╚¢ei de energie, ANRE aprob─â un calendar de implementare a sistemelor de m─âsurare inteligente, astfel ├«nc├ót circa 80% dintre clien╚¢i s─â dispun─â de sisteme de m─âsurare inteligente p├ón─â ├«n 2020. Implementarea acestor sisteme se va aproba ├«n cadrul planurilor anuale de investi╚¢ii ale operatorilor de distribu╚¢ie.', NULL, 66, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1546, 5, '171', 'Articolul 171', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Transmiterea cererilor de derogare...Autoritatea de reglementare transmite Comisiei Europene, f─âr─â ├«nt├órziere, o copie a tuturor cererilor de derogare, imediat dup─â primirea acestora. Decizia de derogare se comunic─â f─âr─â ├«nt├órziere de ANRE Comisiei Europene, ├«mpreun─â cu toate informa╚¢iile relevante.', NULL, 171, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1169, 6, '69', 'Articolul 69', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Cogenerarea de ├«nalt─â eficien╚¢─â...├Än scopul promov─ârii producerii energiei electrice ├«n cogenerare se define╚Öte cogenerarea de ├«nalt─â eficien╚¢─â ca fiind procesul de producere combinat─â a energiei electrice ╚Öi termice, care ├«ndepline╚Öte urm─âtoarele criterii:a)├«n cazul unit─â╚¢ilor de cogenerare cu puteri electrice instalate de peste 25 MW:......(i) realizarea unui randament global anual de minimum 70%; ╚Öi...(├«i) realizarea unei economii de energie primar─â de cel pu╚¢in 10% fa╚¢─â de valorile de referin╚¢─â ale producerii separate ale acelora╚Öi cantit─â╚¢i de energie electric─â ╚Öi termic─â;b)├«n cazul unit─â╚¢ilor de cogenerare cu puteri electrice instalate ├«ntre 1 MW ╚Öi 25 MW, realizarea de economie de energie primar─â de cel pu╚¢in 10% fa╚¢─â de valorile de referin╚¢─â ale producerii separate a acelora╚Öi cantit─â╚¢i de energie electric─â ╚Öi termic─â;...c)├«n cazul unit─â╚¢ilor de cogenerare cu puteri instalate sub 1 MW, realizarea de economie de energie primar─â fa╚¢─â de producerea separat─â a acelora╚Öi cantit─â╚¢i de energie electric─â ╚Öi termic─â....', NULL, 69, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1170, 6, '70', 'Articolul 70', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Reguli de acces la re╚¢ea pentru energia electric─â produs─â din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â...Autoritatea competent─â stabile╚Öte prin reguli tehnice ╚Öi comerciale:a)accesul garantat la re╚¢elele electrice ╚Öi dispecerizarea prioritar─â a energiei electrice produse din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â;...b)accesul prioritar la re╚¢elele electrice ╚Öi dispecerizarea prioritar─â a energiei electrice produse din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â ├«n centrale cu puteri instalate mai mici sau egale cu 1 MW, ├«n m─âsura ├«n care nu este afectat nivelul de siguran╚¢─â a SEN....', NULL, 70, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1171, 6, '71', 'Articolul 71', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Criterii de promovare a energiei electrice produse din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â...(1)...Criteriile de promovare a energiei electrice produse din surse regenerabile de energie au ├«n vedere urm─âtoarele:a)atingerea ╚¢intei na╚¢ionale privind ponderea energiei electrice produse din surse regenerabile de energie;...b)compatibilitatea cu principiile concuren╚¢iale de pia╚¢─â;...c)caracteristicile diferitelor surse regenerabile de energie ╚Öi tehnologiile de producere a energiei electrice;...d)promovarea utiliz─ârii surselor regenerabile de energie ├«n cel mai eficient mod.......(2)...Criteriile de promovare a energiei electrice produse ├«n cogenerarea de ├«nalt─â eficien╚¢─â au ├«n vedere urm─âtoarele:a)asigurarea accesului la schemele de sprijin, ├«n condi╚¢iile acoperirii costurilor justificate aferente cogener─ârii de ├«nalt─â eficien╚¢─â;...b)caracteristicile diferitelor tehnologii de producere a energiei electrice ├«n cogenerare;...c)promovarea utiliz─ârii eficiente a combustibililor;...d)asigurarea protec╚¢iei mediului, prin reducerea emisiilor poluante fa╚¢─â de producerea separat─â a energiei electrice ╚Öi termice....', NULL, 71, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1172, 6, '72', 'Articolul 72', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Scheme de sprijin pentru promovarea producerii energiei electrice din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â...(1)...Pentru promovarea producerii energiei electrice din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se aplic─â scheme de sprijin, ├«n conformitate cu prevederile legisla╚¢iei europene....(2)...Pentru accesul la schemele de sprijin pentru promovarea energiei electrice produse din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se aplic─â regulile de acreditare ╚Öi calificare stabilite de autoritatea competent─â.', NULL, 72, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1173, 6, '73', 'Articolul 73', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Garan╚¢iile de origine...Certificarea energiei electrice produse din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se atest─â prin intermediul garan╚¢iilor de origine emise de autoritatea competent─â.', NULL, 73, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
