INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1175, 6, '75', 'Articolul 75', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Fundamentarea acestora...(1)...Pentru activit─â╚¢ile desf─â╚Öurate ├«n sectorul energiei electrice se aplic─â urm─âtoarele pre╚¢uri ╚Öi tarife:a)pre╚¢uri rezultate din mecanismele concuren╚¢iale ale pie╚¢ei de energie electric─â;...b)tarife reglementate pentru serviciile de transport, de sistem ╚Öi de distribu╚¢ie a energiei electrice;...c)tarife/pre╚¢uri reglementate pentru furnizarea de energie electric─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1), cu respectarea prevederilor art. 22, p├ón─â la eliminarea acestora;...d)pre╚¢uri/tarife practicate de furnizorii de ultim─â instan╚¢─â clien╚¢ilor afla╚¢i ├«n situa╚¢ia prev─âzut─â la art. 53 alin. (2);...e)tarife reglementate pentru achizi╚¢ia serviciilor tehnologice de sistem, p├ón─â la crearea unei pie╚¢e concuren╚¢iale de servicii tehnologice de sistem;...f)tarife reglementate de racordare la re╚¢ea;...g)tarif reglementat practicat de operatorul pie╚¢ei de energie electric─â;...h)pre╚¢uri reglementate practicate de produc─âtorii/furnizorii de energie electric─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1), cu respectarea prevederilor art. 22;...i)pre╚¢uri reglementate pentru energia termic─â destinat─â consumului popula╚¢iei, produs─â ├«n instala╚¢iile de cogenerare;...j)pre╚¢uri rezultate din formule de stabilire/ajustare prev─âzute ├«n contractele de v├ónzare-cump─ârare a energiei termice ori din contractele de concesiune sau de asociere ori de parteneriat.......(2)...ANRE elaboreaz─â ╚Öi aprob─â regulile privind mecanismul de corelare a pre╚¢ului orar de ofert─â pe pia╚¢a de echilibrare cu pre╚¢ul de ├«nchidere a pie╚¢ei PZU stabilit pentru ora respectiv─â.', NULL, 75, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1195, 6, '95', 'Articolul 95', NULL, 'Energia electric─â', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Constatarea contraven╚¢iilor...├Än vederea constat─ârii contraven╚¢iilor prev─âzute la art. 93 alin. (1), agen╚¢ii constatatori men╚¢iona╚¢i la art. 93 alin. (3) au acces, ├«n condi╚¢iile legii, ├«n cl─âdiri, ├«nc─âperi, la instala╚¢ii ╚Öi ├«n orice alt loc, p├ón─â la receptoarele de energie, ╚Öi au dreptul s─â verifice instala╚¢ia, precum ╚Öi s─â execute m─âsur─âtori ╚Öi determin─âri. Proprietarii ╚Öi cei care exploateaz─â aceste cl─âdiri, locuri sau instala╚¢ii sunt obliga╚¢i s─â pun─â la dispozi╚¢ia agen╚¢ilor constatatori documentele ╚Öi actele specifice activit─â╚¢ii lor.', NULL, 95, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1176, 6, '76', 'Articolul 76', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Metodologiile de reglementare a pre╚¢urilor ╚Öi tarifelor...(1)...Pre╚¢urile ╚Öi tarifele reglementate pentru activit─â╚¢ile de producere, transport, distribu╚¢ie ╚Öi furnizare a energiei electrice ╚Öi a energiei termice produse ├«n centrale de cogenerare, precum ╚Öi pentru activitatea prestat─â de operatorul pie╚¢ei de energie electric─â se stabilesc pe baza metodologiilor aprobate ╚Öi publicate de autoritatea competent─â. ├Än calculul acestora vor fi luate ├«n considerare costurile justificate ale activit─â╚¢ilor respective, cheltuielile pentru dezvoltare ╚Öi protec╚¢ia mediului, precum ╚Öi o cot─â rezonabil─â de profit....(2)...Metodele de formare a pre╚¢urilor reglementate pentru energia termic─â ╚Öi a m─âsurilor de sprijin pentru energia electric─â produs─â ├«n centrale de cogenerare de ├«nalt─â eficien╚¢─â care beneficiaz─â de scheme de sprijin instituite la nivel na╚¢ional se stabilesc pe baz─â de hot─âr├óre a Guvernului....(3)...Metodologiile de reglementare a pre╚¢urilor ╚Öi tarifelor se aprob─â de autoritatea competent─â, dup─â parcurgerea procedurii de consultare public─â.', NULL, 76, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1177, 6, '77', 'Articolul 77', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Eviden╚¢ierea costurilor ╚Öi a veniturilor...Operatorii economici titulari de licen╚¢─â din sectorul energiei electrice, care practic─â pre╚¢uri ╚Öi tarife reglementate, sunt obliga╚¢i s─â prezinte autorit─â╚¢ii competente:a)costurile ╚Öi veniturile realizate separat din activit─â╚¢ile care fac obiectul licen╚¢elor acordate ├«n structura stabilit─â de autoritatea competent─â;...b)modul de alocare a activelor, pasivelor, cheltuielilor ╚Öi veniturilor, conform reglement─ârilor emise de autoritatea competent─â....', NULL, 77, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1178, 6, '78', 'Articolul 78', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Stabilirea costurilor ╚Öi veniturilor reglementate...(1)...├Än scopul controlului tarifelor la clien╚¢ii prev─âzu╚¢i la art. 55 alin. (1), autoritatea competent─â aprob─â metodologia de stabilire a costurilor/veniturilor reglementate pentru cump─ârarea/v├ónzarea energiei electrice destinate acestor clien╚¢i....(2)...Dep─â╚Öirea costurilor reglementate pentru cump─ârarea/v├ónzarea energiei electrice destinate clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) ╚Öi a costurilor reglementate pentru serviciile de transport ╚Öi distribu╚¢ie se poate face, cu aprobarea autorit─â╚¢ii competente, doar ├«n situa╚¢ii excep╚¢ionale, de for╚¢─â major─â sau determinat─â de schimb─âri legislative.', NULL, 78, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1179, 6, '79', 'Articolul 79', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Principiile pentru fundamentarea propunerilor de pre╚¢uri ╚Öi tarife reglementate...(1)...Propunerile de pre╚¢uri ╚Öi tarife reglementate se elaboreaz─â de operatorii economici din sectorul energiei electrice, cu respectarea metodologiilor emise de autoritatea competent─â, ╚Öi se comunic─â autorit─â╚¢ii, ├«mpreun─â cu fundamentarea ├«n formatul stabilit de aceasta....(2)...Operatorii economici din sectorul energiei electrice transmit autorit─â╚¢ii competente toate datele necesare folosite ├«n procesul de fundamentare a solicit─ârii ╚Öi vor asigura accesul la aceste date al reprezentan╚¢ilor desemna╚¢i de autoritatea competent─â....(3)...Ordinul de aprobare a pre╚¢urilor ╚Öi tarifelor reglementate pentru energia electric─â, precum ╚Öi cel de aprobare a metodologiilor de calcul al acestora, aplicabile clien╚¢ilor finali ╚Öi activit─â╚¢ilor cu caracter de monopol natural, se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I. Ordinul va con╚¢ine ╚Öi data intr─ârii lor ├«n vigoare....(4)...Principiile ce stau la baza stabilirii pre╚¢urilor ╚Öi tarifelor reglementate pentru activit─â╚¢ile desf─â╚Öurate ├«n sectorul energiei electrice, exclusiv pre╚¢urile pentru energia electric─â ╚Öi termic─â produs─â ├«n centrale de cogenerare de ├«nalt─â eficien╚¢─â ce beneficiaz─â de scheme de sprijin, au ├«n vedere urm─âtoarele:a)pre╚¢urile/tarifele trebuie s─â fie nediscriminatorii, bazate pe criterii obiective ╚Öi determinate ├«ntr-o manier─â transparent─â, pe baza metodologiilor aprobate ╚Öi publicate de autoritatea competent─â;...b)pre╚¢urile/tarifele energiei electrice trebuie s─â acopere costurile justificate economic;...c)pre╚¢urile/tarifele trebuie s─â asigure o rat─â rezonabil─â a rentabilit─â╚¢ii capitalului investit, stabilit─â conform reglement─ârilor emise de autoritatea competent─â;...d)tarifele ╚Öi pre╚¢urile reglementate pentru clien╚¢ii finali trebuie s─â permit─â acestora alegerea tipului de pre╚¢/tarif care le este mai favorabil, dintre cele practicate de furnizor, ├«n condi╚¢iile stabilite de autoritatea competent─â;...e)tarifele de racordare trebuie s─â con╚¢in─â numai costurile efective de realizare a lucr─ârilor de racordare a clien╚¢ilor la re╚¢eaua electric─â;...f)metodologiile de calcul al tarifelor de transport ╚Öi distribu╚¢ie trebuie s─â con╚¢in─â elemente stimulative pe termen scurt ╚Öi lung privind cre╚Öterea eficien╚¢ei energetice, a siguran╚¢ei ├«n alimentare, integrarea armonioas─â ├«n pia╚¢─â, precum ╚Öi sprijinirea activit─â╚¢ii de cercetare aferente domeniului de activitate;...g)tarifele de transport ╚Öi de distribu╚¢ie sunt nediscriminatorii ╚Öi reflect─â costurile justificate ale operatorilor, ╚¢in├ónd cont ╚Öi de costurile de re╚¢ea marginale evitate pe termen lung, ca urmare a producerii distribuite ╚Öi a m─âsurilor de gestionare a cererii.......(5)...Este interzis─â subven╚¢ia ├«ncruci╚Öat─â ├«ntre activit─â╚¢ile reglementate, precum ╚Öi ├«ntre activit─â╚¢ile reglementate ╚Öi cele nereglementate ale unui operator economic. ├Än cazul producerii de energie electric─â ╚Öi termic─â ├«n cogenerare de ├«nalt─â eficien╚¢─â sau din surse regenerabile de energie care beneficiaz─â de scheme de sprijin instituite la nivel na╚¢ional, subven╚¢ia ├«ncruci╚Öat─â se analizeaz─â ├«ntre activitatea de producere ├«n cogenerare sau din surse regenerabile de energie care beneficiaz─â de schema de sprijin ╚Öi restul activit─â╚¢ilor desf─â╚Öurate de operatorul economic....(6)...Furnizorului de ultim─â instan╚¢─â i se recunosc integral ├«n tarife costurile de achizi╚¢ie a energiei electrice ╚Öi costurile asociate, precum ╚Öi o cot─â rezonabil─â de profit....(7)...Principiile care stau la baza stabilirii pre╚¢urilor reglementate pentru energia electric─â ╚Öi termic─â produs─â ├«n centrale de cogenerare de ├«nalt─â eficien╚¢─â sau din surse regenerabile ce beneficiaz─â de scheme de sprijin au ├«n vedere urm─âtoarele:a)pentru energia electric─â ╚Öi termic─â produs─â ├«n cogenerare de ├«nalt─â eficien╚¢─â sau din surse regenerabile se aplic─â prevederile schemelor de sprijin, aprobate prin hot─âr├óre a Guvernului;...b)pentru energia termic─â produs─â ├«n capacit─â╚¢i de producere termice amplasate ├«ntr-o central─â electric─â de cogenerare ce beneficiaz─â de scheme de sprijin, pre╚¢urile se stabilesc pe baza metodologiei aprobate de autoritatea competent─â....', NULL, 79, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1180, 6, '80', 'Articolul 80', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Limitarea pre╚¢urilor/tarifelor...(1)...├Än situa╚¢ii de dezechilibre majore ├«ntre cerere ╚Öi ofert─â ╚Öi disfunc╚¢ionalit─â╚¢i evidente ale pie╚¢ei de energie electric─â, Guvernul, la propunerea ANRE ╚Öi cu avizul Consiliului Concuren╚¢ei, poate limita cre╚Öterea excesiv─â a pre╚¢urilor/tarifelor sau blocarea acestora pentru o perioad─â determinat─â de maximum 6 luni, perioad─â ce poate fi prelungit─â succesiv pentru durate de c├óte cel mult 3 luni, c├ót timp persist─â circumstan╚¢ele ce au determinat adoptarea respectivei decizii, prin:a)fixarea unei limite superioare a pre╚¢ului pe o pia╚¢─â centralizat─â;...b)limitarea venitului din activitatea reglementat─â.......(2)...Costurile recunoscute ╚Öi am├ónate, potrivit prevederilor alin. (1) lit. b), se recupereaz─â integral, ├«n conformitate cu procedura emis─â de autoritatea competent─â.', NULL, 80, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1181, 6, '81', 'Articolul 81', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Recuperarea costurilor...(1)...├Än cazul ├«n care este impus─â respectarea unor condi╚¢ii legale de ├«ndeplinire a obliga╚¢iilor de serviciu public ╚Öi de serviciu universal, participan╚¢ii la pia╚¢a de energie electric─â au dreptul de a-╚Öi recupera costurile rezultate din aplicarea acestor condi╚¢ii....(2)...Recuperarea acestor costuri se va face conform reglement─ârilor emise de autoritatea competent─â sau prin hot─âr├óre a Guvernului, ├«n func╚¢ie de modalitatea de impunere a obliga╚¢iilor de serviciu public, conform art. 16 alin. (2).', NULL, 81, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1182, 6, '82', 'Articolul 82', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Eviden╚¢ierea costurilor ├«n producerea combinat─â a energiei electrice ╚Öi termice...Operatorii economici care produc combinat energie electric─â ╚Öi termic─â ╚Öi care comercializeaz─â cel pu╚¢in una dintre acestea vor repartiza costurile ├«ntre cele dou─â forme de energie, potrivit reglement─ârilor aprobate de autoritatea competent─â.', NULL, 82, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1183, 6, '83', 'Articolul 83', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Serviciile conexe...Serviciile prestate ter╚¢ilor de c─âtre operatorii economici care produc energie hidroelectric─â ╚Öi care au ├«n administrare, concesiune sau ├«nchiriere baraje, diguri ╚Öi lacuri de acumulare se realizeaz─â pe baza contractelor ├«ncheiate cu beneficiarii.', NULL, 83, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1184, 6, '84', 'Articolul 84', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Dispunerea efectu─ârii investiga╚¢iilor...Pre╚Öedintele autorit─â╚¢ii competente dispune, prin decizie, efectuarea de investiga╚¢ii, potrivit prevederilor art. 86, de c─âtre personalul propriu ├«mputernicit ├«n acest sens, din oficiu sau ca r─âspuns la o pl├óngere ├«nregistrat─â la autoritatea competent─â, formulat─â de c─âtre o persoan─â fizic─â ori juridic─â afectat─â ├«n mod real ╚Öi direct de o poten╚¢ial─â ├«nc─âlcare a prevederilor prezentului titlu, numai ├«n domeniile ├«n care ANRE are competen╚¢a de investigare potrivit legii.', NULL, 84, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1185, 6, '85', 'Articolul 85', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Solicitarea informa╚¢iilor ╚Öi documentelor...├Än realizarea investiga╚¢iilor, precum ╚Öi a competen╚¢elor conferite ├«n baza prezentului titlu, autoritatea competent─â poate solicita operatorilor economici informa╚¢iile ╚Öi documentele care sunt necesare, men╚¢ion├ónd baza legal─â ╚Öi scopul solicit─ârii, ╚Öi poate stabili termene p├ón─â la care aceste informa╚¢ii ╚Öi documente s─â ├«i fie furnizate.', NULL, 85, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1210, 6, '110', 'Articolul 110', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de uz pentru executarea lucr─ârilor...├Än exercitarea dreptului de uz pentru executarea lucr─ârilor necesare ├«n vederea realiz─ârii, reabilit─ârii sau moderniz─ârii obiectivelor/sistemelor, concesionarul poate:a)s─â depoziteze pe terenurile proprietate privat─â, ├«n m─âsura strict necesar─â, materiale, echipamente, utilaje ╚Öi instala╚¢ii;...b)s─â desfiin╚¢eze culturi sau planta╚¢ii ori alte amenaj─âri existente ori numai s─â le restr├óng─â, ├«n m─âsura strict necesar─â, pentru executarea lucr─ârilor, ├«n condi╚¢iile legii;...c)s─â extrag─â materiale, s─â capteze ap─â, ├«n condi╚¢iile prev─âzute de legisla╚¢ia ├«n vigoare;...d)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea, s─â amplaseze birouri ╚Öi locuin╚¢e de ╚Öantier;...e)s─â opreasc─â ori s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura strict necesar─â, pentru executarea lucr─ârilor pentru obiectivul/sistemul respectiv....', NULL, 110, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1186, 6, '86', 'Articolul 86', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Drepturi de investigare...(1)...Pentru investigarea ├«nc─âlc─ârii prevederilor prezentului titlu ├«n condi╚¢iile art. 84, personalul ANRE ├«mputernicit ├«n acest sens are urm─âtoarele drepturi:a)s─â ├«ntre ├«n spa╚¢iile, terenurile sau mijloacele de transport pe care operatorii economici le de╚¢in legal;...b)s─â examineze orice documente, registre, acte financiar-contabile ╚Öi comerciale sau alte eviden╚¢e legate de activitatea operatorilor economici, indiferent de locul ├«n care sunt depozitate;...c)s─â cear─â oric─ârui reprezentant sau angajat al operatorului economic explica╚¢ii cu privire la faptele ori documentele legate de obiectul ╚Öi scopul investiga╚¢iei ╚Öi s─â consemneze sau s─â ├«nregistreze r─âspunsurile acestora;...d)s─â ridice sau s─â ob╚¢in─â, ├«n orice form─â, copii ori extrase din orice documente, registre, acte financiar-contabile ╚Öi comerciale sau din alte eviden╚¢e legate de activitatea operatorului economic;...e)s─â sigileze orice amplasament destinat activit─â╚¢ilor operatorului economic ╚Öi orice documente, registre, acte financiar-contabile ╚Öi comerciale sau alte eviden╚¢e legate de activitatea operatorului economic, pe durata ╚Öi ├«n m─âsura necesar─â investig─ârii.......(2)...Autoritatea competent─â va proceda la ac╚¢iunile prev─âzute la alin. (1) numai dac─â exist─â indicii c─â pot fi g─âsite documente sau pot fi ob╚¢inute informa╚¢ii considerate necesare pentru ├«ndeplinirea competen╚¢elor sale, iar rezultatul investiga╚¢iei va fi consemnat ├«ntr-un proces-verbal de constatare ╚Öi inventariere....(3)...Autoritatea competent─â poate face inspec╚¢ii inopinate ╚Öi poate solicita orice fel de informa╚¢ii sau justific─âri legate de ├«ndeplinirea competen╚¢elor de investigare, at├ót la fa╚¢a locului, c├ót ╚Öi prin convocare la sediul autorit─â╚¢ii competente.', NULL, 86, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1187, 6, '87', 'Articolul 87', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Autorizarea judiciar─â a investiga╚¢iilor...├Än baza autoriz─ârii judiciare date prin ├«ncheiere, conform art. 88, personalul ANRE, ├«mputernicit ├«n condi╚¢iile art. 84, poate efectua inspec╚¢ii ├«n orice alte spa╚¢ii, inclusiv domiciliul, terenuri sau mijloace de transport apar╚¢in├ónd conduc─âtorilor, administratorilor, directorilor ╚Öi altor angaja╚¢i ai operatorilor economici ori asocia╚¢iilor de operatori economici supu╚Öi investiga╚¢iei.', NULL, 87, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1188, 6, '88', 'Articolul 88', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Ob╚¢inerea autoriz─ârii judiciare...(1)...Personalul ANRE efectueaz─â inspec╚¢ii, ├«n conformitate cu prevederile art. 87, numai ├«n baza deciziei de ├«mputernicire emise de c─âtre pre╚Öedintele autorit─â╚¢ii competente ╚Öi cu autorizarea judiciar─â dat─â prin ├«ncheiere de c─âtre pre╚Öedintele Cur╚¢ii de Apel Bucure╚Öti sau de c─âtre un judec─âtor delegat de acesta. O copie certificat─â a deciziei de ├«mputernicire ╚Öi a autoriza╚¢iei judiciare se comunic─â obligatoriu persoanei supuse inspec╚¢iei, ├«nainte de ├«nceperea acesteia....(2)...Cererea de autorizare se judec─â ├«n camera de consiliu, f─âr─â citarea p─âr╚¢ilor. Judec─âtorul se pronun╚¢─â asupra cererii de autorizare ├«n termen de cel mult 48 de ore de la data ├«nregistr─ârii cererii. ├Äncheierea se motiveaz─â ╚Öi se comunic─â autorit─â╚¢ii competente, ├«n termen de cel mult 48 de ore de la pronun╚¢are....(3)...├Än cazul ├«n care inspec╚¢ia trebuie desf─â╚Öurat─â simultan ├«n mai multe spa╚¢ii dintre cele prev─âzute la art. 87, autoritatea competent─â va introduce o singur─â cerere, instan╚¢a pronun╚¢├óndu-se printr-o ├«ncheiere ├«n care se vor indica spa╚¢iile ├«n care urmeaz─â s─â se desf─â╚Öoare inspec╚¢ia....(4)...Cererea de autorizare trebuie s─â cuprind─â toate informa╚¢iile de natur─â s─â justifice inspec╚¢ia, iar judec─âtorul sesizat este ╚¢inut s─â verifice dac─â cererea este ├«ntemeiat─â....(5)...Oricare ar fi ├«mprejur─ârile, inspec╚¢ia se desf─â╚Öoar─â ├«ntre orele 8,00 ╚Öi 18,00 ╚Öi trebuie efectuat─â ├«n prezen╚¢a persoanei la care se efectueaz─â inspec╚¢ia sau a reprezentantului s─âu. Inspec╚¢ia poate continua ╚Öi dup─â ora 18,00 numai cu acordul persoanei la care se efectueaz─â inspec╚¢ia sau a reprezentantului s─âu....(6)...Inventarele ╚Öi punerile de sigilii se fac conform dispozi╚¢iilor Codului de procedur─â penal─â....(7)...├Äncheierea prev─âzut─â la alin. (1) poate fi atacat─â cu recurs la ├Änalta Curte de Casa╚¢ie ╚Öi Justi╚¢ie, ├«n termen de 48 de ore. Termenul de recurs pentru autoritatea competent─â curge de la momentul comunic─ârii ├«ncheierii, potrivit prevederilor alin. (2). ├Än ceea ce prive╚Öte persoana supus─â inspec╚¢iei, termenul de recurs curge de la momentul comunic─ârii ├«ncheierii, potrivit prevederilor alin. (1). Recursul nu este suspensiv de executare....(8)...Pre╚Öedintele Cur╚¢ii de Apel Bucure╚Öti sau judec─âtorul delegat de acesta are competen╚¢a s─â emit─â autoriza╚¢ia judiciar─â ├«n vederea efectu─ârii inspec╚¢iei, potrivit art. 87. Instan╚¢a de judecat─â verific─â dac─â decizia de ├«mputernicire emis─â de c─âtre pre╚Öedintele ANRE este autentic─â ╚Öi dac─â m─âsurile coercitive preconizate nu sunt nici arbitrare ╚Öi nici excesive, av├ónd ├«n vedere, ├«n special, gravitatea ├«nc─âlc─ârii suspectate, importan╚¢a elementelor de prob─â c─âutate, implicarea ├«ntreprinderii ├«n cauz─â ╚Öi probabilitatea rezonabil─â ca registrele ╚Öi documentele privind activitatea, care au leg─âtur─â cu obiectul inspec╚¢iei, s─â fie p─âstrate ├«n incintele pentru care se solicit─â autoriza╚¢ia. Instan╚¢a de judecat─â poate solicita ANRE explica╚¢ii detaliate privind elementele care ├«i sunt necesare pentru a-i permite s─â verifice propor╚¢ionalitatea m─âsurilor coercitive preconizate.', NULL, 88, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1189, 6, '89', 'Articolul 89', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Accesul la documente ╚Öi informa╚¢ii...(1)...Organele administra╚¢iei publice centrale ╚Öi locale, precum ╚Öi orice alte institu╚¢ii ╚Öi autorit─â╚¢i publice sunt obligate s─â permit─â autorit─â╚¢ii competente accesul la documentele, datele ╚Öi informa╚¢iile de╚¢inute de acestea, cu respectarea dispozi╚¢iilor legale....(2)...Autoritatea competent─â, primind acces la documentele, datele ╚Öi informa╚¢iile men╚¢ionate la alin. (1), are obliga╚¢ia de a respecta caracterul datelor ╚Öi informa╚¢iilor clasificate atribuit legal respectivelor documente, date ╚Öi informa╚¢ii.', NULL, 89, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1190, 6, '90', 'Articolul 90', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Procedura de investigare...Procedura de investigare se face ├«n conformitate curegulamentul privind organizarea ╚Öi desf─â╚Öurarea activit─â╚¢ii de investigare, aprobat prin ordin al pre╚Öedintelui ANRE.', NULL, 90, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1191, 6, '91', 'Articolul 91', NULL, 'Energia electric─â', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'R─âspunderi...├Änc─âlcarea prevederilor prezentului titlu atrage r─âspunderea disciplinar─â, civil─â, contraven╚¢ional─â sau penal─â, dup─â caz, a persoanelor vinovate.', NULL, 91, '2025-11-14 21:57:25.483736', '2025-11-14 21:57:25.483736', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
