INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1193, 6, '93', 'Articolul 93', NULL, 'Energia electric─â', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Contraven╚¢ii...(1)...Constituie contraven╚¢ii urm─âtoarele fapte:...1. desf─â╚Öurarea de c─âtre persoane fizice ori juridice de activit─â╚¢i ╚Öi/sau prest─âri de servicii ├«n sectorul energiei electrice f─âr─â a de╚¢ine licen╚¢─â ori autoriza╚¢ie de ├«nfiin╚¢are conform prevederilor prezentei legi ╚Öi ale regulamentului prev─âzut la art. 9 alin. (4);...2. proiectarea, executarea ╚Öi verificarea instala╚¢iilor electrice f─âr─â atestat ╚Öi/sau autoriza╚¢ie;...3. nerespectarea normelor tehnice emise de c─âtre ANRE;...4. nerespectarea condi╚¢iilor de valabilitate asociate licen╚¢elor/atestatelor/autoriza╚¢iilor emise de ANRE;...5. ├«nc─âlcarea de c─âtre participan╚¢ii la pia╚¢a de energie electric─â a reglement─ârilor tehnice ╚Öi/sau comerciale emise de ANRE;...6. refuzul de a permite efectuarea de ac╚¢iuni de control dispuse de ANRE, precum ╚Öi obstruc╚¢ionarea acesteia ├«n ├«ndeplinirea atribu╚¢iilor sale;...7. nefurnizarea/neprezentarea datelor, documentelor ╚Öi/sau a informa╚¢iilor solicitate ├«n termenele stabilite de ANRE sau furnizarea/prezentarea incomplet─â ori eronat─â a acestora, precum ╚Öi/sau nerealizarea m─âsurilor dispuse de aceasta;...8. refuzul nejustificat de a acorda acces oric─ârui solicitant la re╚¢ele de interes public;...9. furnizarea de c─âtre operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem a unor informa╚¢ii incomplete ori eronate necesare pentru accesul la re╚¢ea;...10. neasigurarea de c─âtre operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem a unui tratament nediscriminatoriu ├«ntre utilizatorii re╚¢elei electrice;...11. ├«nt├órzierea nejustificat─â ├«n racordarea/alimentarea cu energie electric─â a clien╚¢ilor sau ├«n realimentarea acestora dup─â ├«ntrerupere, precum ╚Öi propunerea unor solu╚¢ii de racordare, altele dec├ót cele optime din punct de vedere tehnic ╚Öi cu costuri minime, conform actelor normative ├«n vigoare ╚Öi reglement─ârilor emise de ANRE;...12. punerea sub tensiune a instala╚¢iilor electrice f─âr─â a exista ├«ncheiat contract de furnizare a energiei electrice, precum ╚Öi/sau f─âr─â a exista montat un grup de m─âsurare a energiei electrice, dup─â caz;...13. necomunicarea unei oferte privind condi╚¢iile de furnizare a energiei electrice solicitan╚¢ilor, conform prevederilor art. 58 alin. (1);...14. sistarea nejustificat─â a aliment─ârii cu energie electric─â a clien╚¢ilor;...15. nerespectarea prevederilor standardelor de performan╚¢─â referitoare la serviciile publice de transport ╚Öi distribu╚¢ie, precum ╚Öi la serviciul universal din sectorul energiei electrice;...16. nerespectarea prevederilor clauzelor obligatorii din contractele-cadru emise de ANRE;...17. nerespectarea prevederilor legale privind schimbarea furnizorului de energie electric─â;...18. divulgarea informa╚¢iilor sensibile din punct de vedere comercial privind opera╚¢iunile comerciale de c─âtre operatorul de transport ╚Öi de sistem, de operatorii de distribu╚¢ie sau de operatorul pie╚¢ei de energie electric─â, inclusiv a celor privind toate tranzac╚¢iile din cadrul contractelorde furnizare de energie electric─â ╚Öi/sau al instrumentelor financiare derivate din sectorul energiei electrice, cu excep╚¢ia cazului ├«n care acestea sunt solicitate de autorit─â╚¢ile competente;...19. nerespectarea cerin╚¢elor de independen╚¢─â a operatorului de distribu╚¢ie ori a operatorului de transport ╚Öi de sistem ╚Öi/sau a reglement─ârilor privind controlul direct ori indirect asupra unor ├«ntreprinderi din sectorul producerii, furniz─ârii, distribu╚¢i