INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1376, 5, '1', 'Articolul 1', NULL, 'Energia electric─â', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, 'Domeniul de reglementare...(1)...Prezentul titlu stabile╚Öte cadrul de reglementare pentru desf─â╚Öurarea activit─â╚¢ilor ├«n sectorul energiei electrice ╚Öi al energiei termice produse ├«n cogenerare, ├«n vederea utiliz─ârii optime a resurselor primare de energie ├«n condi╚¢iile de accesibilitate, disponibilitate ╚Öi suportabilitate ╚Öi cu respectarea normelor de siguran╚¢─â, calitate ╚Öi protec╚¢ie a mediului....(2)...Nu se supun dispozi╚¢iilor prezentului titlu:a)acumulatoarele electrice, grupurile electrogene mobile, instala╚¢iile electrice amplasate pe vehicule de orice fel;...b)sursele sta╚¢ionare de energie electric─â ├«n curent continuu, dac─â nu sunt racordate prin invertoare la Sistemul electroenergetic na╚¢ional, denumit ├«n continuare SEN....', NULL, 1, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1377, 5, '2', 'Articolul 2', NULL, 'Energia electric─â', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, 'Obiectivele activit─â╚¢ilor din domeniul energiei electrice ╚Öi al energiei termice produse ├«n cogenerare...Activit─â╚¢ile din domeniul energiei electrice ╚Öi al energiei termice produse ├«n cogenerare trebuie s─â se desf─â╚Öoare pentru realizarea urm─âtoarelor obiective de baz─â:a)asigurarea dezvolt─ârii durabile a economiei na╚¢ionale;...b)diversificarea bazei de resurse energetice primare;...c)crearea ╚Öi asigurarea func╚¢ion─ârii pie╚¢elor concuren╚¢iale de energie electric─â;...d)asigurarea accesului nediscriminatoriu ╚Öi reglementat al tuturor participan╚¢ilor la pia╚¢a de energie electric─â ╚Öi la re╚¢elele electrice de interes public;...e)transparen╚¢a tarifelor, pre╚¢urilor ╚Öi taxelor la energie electric─â ├«n cadrul unei politici de tarifare, urm─ârind cre╚Öterea eficien╚¢ei energetice pe ciclul de producere, transport, distribu╚¢ie ╚Öi utilizare a energiei electrice;...f)constituirea stocurilor de siguran╚¢─â la combustibilii necesari pentru producerea energiei electrice, precum ╚Öi a energiei termice produse ├«n cogenerare;...g)asigurarea func╚¢ion─ârii interconectate a SEN cu sistemele electroenergetice ale ╚¢─ârilor vecine ╚Öi cu sistemele electroenergetice din Re╚¢eaua European─â a Operatorilor de Transport ╚Öi Sistem de Energie Electric─â - ENTSO-E, denumit─â ├«n continuare ENTSO-E;...h)├«mbun─ât─â╚¢irea competitivit─â╚¢ii pie╚¢ei interne de energie electric─â ╚Öi participarea activ─â la formarea at├ót a pie╚¢ei regionale, c├ót ╚Öi a pie╚¢ei interne de energie a Uniunii Europene ╚Öi la dezvoltarea schimburilor t