INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1354, 2, '32', 'Articolul 32', NULL, NULL, NULL, 'Limit─âri ale dreptului de proprietate privat─â  ├«n vederea construirii ╚Öi exploat─ârii  centralei electrice eoliene offshore', NULL, NULL, NULL, NULL, '(1)...├Än favoarea concesionarilor perimetrelor eoliene offshore care desf─â╚Öoar─â opera╚¢iuni pentru exploatarea energiei eoliene offshore sau construirea centralei electrice eoliene offshore pot fi instituite limit─âri ale dreptului de proprietate asupra terenurilor ╚Öi bunurilor proprietate privat─â a statului sau a altor persoane fizice ori juridice pe toat─â durata realiz─ârii opera╚¢iunilor pentru exploatarea energiei eoliene offshore sau a construirii centralei electrice eoliene offshore....(2)...Limit─ârile prev─âzute laalin. (1)nu se aplic─â imobilelor aflate ├«n proprietatea institu╚¢iilor din sistemul de ap─ârare, ordine public─â ╚Öi securitate na╚¢ional─â ╚Öi nici celor destinate realiz─ârii unor obiective din cadrul Sistemului integrat pentru securitatea frontierei de stat, gestionat de c─âtre Inspectoratul General al Poli╚¢iei de Frontier─â....(3)...Limit─ârile dreptului de proprietate instituite prin prezenta lege constau ├«n:a)dreptul de acces, inclusiv cu autovehicule, utilaje ╚Öi echipamente, dreptul de folosin╚¢─â ╚Öi dreptul de efectuare a lucr─ârilor la conductele subterane, submarine, de suprafa╚¢─â sau aeriene ╚Öi la instala╚¢iile, cablurile ╚Öi echipamentele conexe acestora, cu privire la imobilele necesare realiz─ârii, reloc─ârii, retehnologiz─ârii sau desfiin╚¢─ârii capacit─â╚¢ii energetice;...b)dreptul de acces, inclusiv cu autovehicule, utilaje ╚Öi echipamente ╚Öi dreptul de folosin╚¢─â cu privire la imobilele