INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1380, 5, '5', 'Articolul 5', NULL, 'Energia electric─â', NULL, 'Strategia ╚Öi politica energetic─â, autorit─â╚¢i ╚Öi competen╚¢e', NULL, NULL, NULL, NULL, 'Programul energetic...(1)...Politica energetic─â se concretizeaz─â ├«ntr-un program cuprinz├ónd m─âsuri de stimulare a activit─â╚¢ilor de investi╚¢ii, cercetare-dezvoltare, dezvoltare durabil─â, valorificare eficient─â a resurselor energetice, a eficien╚¢ei energetice ╚Öi a altor activit─â╚¢i ├«n vederea asigur─ârii siguran╚¢ei ╚Öi securit─â╚¢ii ├«n func╚¢ionare a SEN, aprobat prin hot─âr├óre a Guvernului....(2)...Guvernul, ministerul de resort, celelalte organe de specialitate ale administra╚¢iei publice centrale ╚Öi locale iau m─âsuri pentru realizarea obiectivelor ├«nscrise ├«nprogramul prev─âzut la alin. (1) ╚Öi examineaz─â, anual sau ori de c├óte ori este necesar, stadiul ├«ndeplinirii prevederilor acestuia....(3)...Prin hot─âr├óre a Guvernului, pentru ra╚¢iuni legate de siguran╚¢─â aliment─ârii cu energie electric─â, se poate acorda acces garantat la re╚¢elele electrice pentru energia electric─â produs─â ├«n centrale electrice care utilizeaz─â combustibili din produc╚¢ia intern─â, dar numai pentru cantit─â╚¢i anuale corespunz─âtoare unei energii primare de maximum 15% din cantitatea total─â de combustibil echivalent, necesar─â pentru a produce energia electric─â aferent─â consumului brut final al ╚¢─ârii.', NULL, 5, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
