INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1388, 5, '13', 'Articolul 13', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Obliga╚¢iile decurg├ónd din autoriza╚¢iile de ├«nfiin╚¢are ╚Öi din licen╚¢e...(1)...Titularul autoriza╚¢iei de ├«nfiin╚¢are are urm─âtoarele obliga╚¢ii:a)s─â stabileasc─â ╚Öi s─â aplice, pe ├«ntreaga durat─â a execut─ârii lucr─ârilor, m─âsurile de protec╚¢ie a persoanelor, bunurilor ╚Öi mediului;...b)s─â ob╚¢in─â toate avizele, acordurile ╚Öi atestatele prev─âzute de lege pentru realizarea obiectivului autorizat.......(2)...Pe durata de valabilitate a licen╚¢elor, titularii de licen╚¢e sunt obliga╚¢i:a)s─â respecte condi╚¢iile care ├«nso╚¢esc licen╚¢a;...b)s─â ╚¢in─â situa╚¢ii contabile separate pentru fiecare activitate care face obiectul licen╚¢ei acordate de autoritatea competent─â, ├«n cazul operatorului economic ├«n domeniul energiei electrice integrat, pe vertical─â sau pe orizontal─â, s─â procedeze la fel ca ├«n cazul ├«n care activitatea respectiv─â ar fi efectuat─â de operatori economici separa╚¢i ╚Öi s─â ├«ntocmeasc─â rapoarte financiare ├«n formatul solicitat de autoritatea competent─â;...c)s─â constituie ╚Öi s─â men╚¢in─â garan╚¢ii financiare care s─â le permit─â desf─â╚Öurarea activit─â╚¢ii ╚Öi asigurarea continuit─â╚¢ii serviciului;...d)s─â pun─â la dispozi╚¢ia autorit─â╚¢ii competente informa╚¢iile necesare pentru desf─â╚Öurarea ├«n bune condi╚¢ii a activit─â╚¢ii acesteia;...e)s─â ├«ntocmeasc─â, s─â supun─â spre auditare ╚Öi s─â publice eviden╚¢ele lor contabile anuale la nivel de societate comercial─â, f─âr─â a cuprinde ├«n mod distinct sediile secundare f─âr─â personalitate juridic─â, conform legisla╚¢iei specifice, adoptate ├«n conformitate cu cea de-a Patra Directiv─â 78/660/CEE a Consiliului din 25 iulie 1978.......(3)...Operatorii economici care nu au obliga╚¢ia de a public─â conturile anuale p─âstreaz─â la sediul lor un exemplar din acestea la dispozi╚¢ia publicului....(4)...Activitatea de audit prev─âzut─â la alin. (2) lit. e) const─â, ├«n special, ├«n verificarea respect─ârii obliga╚¢iei de a evita discrimin─ârile ╚Öi subven╚¢iile ├«ncruci╚Öate ├«ntre activit─â╚¢ile desf─â╚Öurate de operatorul economic auditat.', NULL, 13, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1389, 5, '14', 'Articolul 14', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Drepturile ╚Öi obliga╚¢iile titularilor de autoriza╚¢ii de ├«nfiin╚¢are ╚Öi de licen╚¢─â asupra propriet─â╚¢ii ter╚¢ilor...(1)...Dreptul de uz asupra terenului pentru executarea lucr─ârilor necesare realiz─ârii/reloc─ârii/desfiin╚¢─ârii sau retehnologiz─ârii de capacit─â╚¢i energetice se ├«ntinde pe durata necesar─â execut─ârii lucr─ârilor. ├Än exercitarea acestui drept de uz, titularul autoriza╚¢iei de ├«nfiin╚¢are/relocare/desfiin╚¢are sau retehnologizare, dup─â caz, cu respectarea prevederilor legale, poate:a)s─â depoziteze, pe terenurile necesare execut─ârii lucr─ârilor, materiale, echipamente, utilaje, instala╚¢ii;...b)s─â desfiin╚¢eze culturi sau planta╚¢ii, construc╚¢ii sau alte amenaj─âri existente ori numai s─â le restr├óng─â, ├«n m─âsura strict necesar─â execut─ârii lucr─ârilor pentru capacitatea autorizat─â, ├«n condi╚¢iile legii;...c)s─â ├«ndep─ârteze materiale, s─â capteze ap─â, ├«n condi╚¢iile prev─âzute de legisla╚¢ia ├«n vigoare;...d)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea, s─â amplaseze birouri ╚Öi locuin╚¢e de ╚Öantier, cu acordul prealabil al proprietarului;...e)s─â opreasc─â ori s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura strict necesar─â execut─ârii lucr─ârilor pentru capacitatea autorizat─â, cu respectarea prevederilor legale ├«n vigoare.......(2)...Dreptul de uz prev─âzut la alin. (1) ├«nceteaz─â ├«naintea expir─ârii duratei stabilite pentru executarea lucr─ârilor sau ├«naintea acestui termen, la data termin─ârii anticipate a lucr─ârilor sau la data sist─ârii acestora ╚Öi renun╚¢─ârii la autoriza╚¢ii. Oricare dintre aceste situa╚¢ii trebuie notificat─â de ├«ndat─â proprietarului....(3)...Dreptul de uz asupra terenului pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii energetice se ├«ntinde pe toat─â durata func╚¢ion─ârii capacit─â╚¢ii, iar exercitarea lui se face ori de c├óte ori este necesar pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii. ├Än exercitarea acestui drept titularul licen╚¢ei poate:a)s─â depoziteze materiale, echipamente, utilaje, instala╚¢ii pentru ├«ntre╚¢inere, revizii, repara╚¢ii ╚Öi interven╚¢ii necesare pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii;...b)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea;...c)s─â desfiin╚¢eze sau s─â reduc─â culturi, planta╚¢ii ori alte amenaj─âri existente ╚Öi s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura ╚Öi pe durata strict necesare execut─ârii opera╚¢iilor de ├«ntre╚¢inere, repara╚¢ii, revizii sau interven╚¢ii pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii, cu respectarea legisla╚¢iei ├«n vigoare.......(4)...Titularul licen╚¢ei este obligat s─â ├«n╚Ötiin╚¢eze ├«n scris proprietarul bunurilor sau prestatorul activit─â╚¢ilor care vor fi afectate ca urmare a lucr─ârilor la capacit─â╚¢ile energetice, cu excep╚¢ia cazurilor de avarii, situa╚¢ie ├«n care proprietarii sunt ├«n╚Ötiin╚¢a╚¢i ├«n termenul cel mai scurt....(5)...Titularul licen╚¢ei este obligat s─â pl─âteasc─â proprietarilor desp─âgubirea cuvenit─â pentru pagubele produse, s─â degajeze terenul ╚Öi s─â-l repun─â ├«n situa╚¢ia anterioar─â, ├«n cel mai scurt timp posibil....(6)...Servitutea de trecere subteran─â, de suprafa╚¢─â sau aerian─â cuprinde dreptul de acces ╚Öi de executare a lucr─ârilor la locul de amplasare a capacit─â╚¢ilor energetice cu ocazia interven╚¢iei pentru retehnologiz─âri, repara╚¢ii, revizii ╚Öi avarii....(7)...Pentru a evita punerea ├«n pericol a persoanelor, a bunurilor sau a unor activit─â╚¢i desf─â╚Öurate ├«n zona de executare a lucr─ârilor de realizare ori retehnologizare de capacit─â╚¢i energetice, precum ╚Öi a opera╚¢iilor de revizie sau repara╚¢ie la capacitatea ├«n func╚¢iune, titularul autoriza╚¢iei sau al licen╚¢ei are dreptul de a ob╚¢ine restr├óngerea ori sistarea, pe toat─â durata lucr─ârilor, a activit─â╚¢ilor desf─â╚Öurate ├«n vecin─âtate de alte persoane. ├Än acest caz, persoanele afectate vor fi ├«n╚Ötiin╚¢ate, ├«n scris, despre data ├«nceperii, respectiv a finaliz─ârii lucr─ârilor....(8)...La ├«ncetarea exercit─ârii drepturilor prev─âzute la art. 12 alin. (2), titularul autoriza╚¢iei de ├«nfiin╚¢are, respectiv titularul licen╚¢ei este obligat s─â asigure degajarea terenului ╚Öi repunerea lui ├«n situa╚¢ia ini╚¢ial─â....(9)...Dreptul de acces la utilit─â╚¢ile publice, prev─âzut la art. 12 alin. (2) lit. e), trebuie exercitat de titularul autoriza╚¢iei sau al licen╚¢ei cu bun─â-credin╚¢─â ╚Öi ├«n mod rezonabil, f─âr─â a prejudicia accesul altor persoane la respectivele utilit─â╚¢i publice.', NULL, 14, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1390, 5, '15', 'Articolul 15', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Zonele de protec╚¢ie ╚Öi zonele de siguran╚¢─â...(1)...Pentru protec╚¢ia ╚Öi func╚¢ionarea normal─â a capacit─â╚¢ilor energetice ╚Öi a anexelor acestora, precum ╚Öi pentru evitarea punerii ├«n pericol a persoanelor, bunurilor ╚Öi mediului se instituie zone de protec╚¢ie ╚Öi de siguran╚¢─â....(2)...Zonele de protec╚¢ie ╚Öi de siguran╚¢─â se determin─â pentru fiecare capacitate, ├«n conformitate cu normele tehnice elaborate de autoritatea competent─â....(3)...Asupra terenurilor aflate ├«n proprietatea ter╚¢ilor, cuprinse ├«n zonele de protec╚¢ie ╚Öi de siguran╚¢─â, se stabile╚Öte drept de servitute legal─â.', NULL, 15, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
