INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1392, 5, '17', 'Articolul 17', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, 'Exproprierea...(1)...Terenul necesar ├«nfiin╚¢─ârii ╚Öi func╚¢ion─ârii capacit─â╚¢ii energetice este fie proprietate privat─â a unui ter╚¢ sau a titularului autoriza╚¢iei, fie proprietate public─â....(2)...Dac─â terenul necesar pentru ├«nfiin╚¢area ╚Öi func╚¢ionarea capacit─â╚¢ii energetice este proprietate privat─â a unui ter╚¢, solicitantul autoriza╚¢iei de ├«nfiin╚¢are are ca prim─â op╚¢iune cump─ârarea terenului de la proprietar sau s─â ini╚¢ieze procedura legal─â de expropriere a terenului pentru cauz─â de utilitate public─â, cu desp─âgubirea proprietarului, ├«n condi╚¢iile legii, ╚Öi s─â ob╚¢in─â concesiunea acestuia, pe durata existen╚¢ei capacit─â╚¢ii energetice.', NULL, 17, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1393, 5, '18', 'Articolul 18', NULL, 'Energia electric─â', NULL, 'Autoriza╚¢ii, licen╚¢e ╚Öi concesiuni', NULL, 'Proceduri pentru noi capacit─â╚¢i energetice de produc╚¢ie', NULL, NULL, 'Procedura de licita╚¢ie ╚Öi alte proceduri...(1)...├Än cazul ├«n care, ├«n urma procedurii de autorizare, capacit─â╚¢ile de produc╚¢ie care se construiesc sau m─âsurile luate pe linia managementului orientat c─âtre eficien╚¢a energetic─â ╚Öi satisfacerea cererii nu sunt suficiente pentru a garanta siguran╚¢a aliment─ârii pentru consumul intern, ministerul de resort poate ini╚¢ia o procedur─â de licita╚¢ie sau orice alt─â procedur─â similar─â pentru atribuirea unui contract, ├«n condi╚¢ii de transparen╚¢─â ╚Öi nediscriminare, pe baza unor criterii publicate, prin care s─â invite noi operatori economici sau titulari de licen╚¢─â de producere preexisten╚¢i s─â oferteze pentru realizarea de noi capacit─â╚¢i de producere a energiei electrice....(2)...├Än condi╚¢iile alin. (1) ╚Öi ├«n scopul asigur─ârii protec╚¢iei mediului ╚Öi al promov─ârii noilor tehnologii, ministerul de resort poate ini╚¢ia o procedur─â de licita╚¢ie privind realizarea de noi capacit─â╚¢i de producere a energiei electrice. Aceast─â procedur─â se poate referi la noi capacit─â╚¢i sau la resursele de management orientat c─âtre eficien╚¢─â energetic─â ╚Öi satisfacerea cererii....(3)...Ofertantului declarat c├ó╚Ötig─âtor i se va acorda autoriza╚¢ie de ├«nfiin╚¢are ╚Öi licen╚¢─â, potrivit dispozi╚¢iilor sec╚¢iunii 1 din prezentul capitol....(4)...Procedura de desf─â╚Öurare a licita╚¢iei pentru capacit─â╚¢i de producere noi, precum ╚Öi lista de criterii care stau la baza selec╚¢iei ofertelor ╚Öi atribuirea contractului se aprob─â prin hot─âr├óre a Guvernului, la propunerea ministerului de resort....(5)...Procedura prev─âzut─â la alin. (4) se va elabora cu respectarea urm─âtoarelor principii:a)informa╚¢iile privind procedura de ofertare pentru capacit─â╚¢ile de producere ╚Öi m─âsurile de eficien╚¢─â energetic─â ╚Öi satisfacerea cererii se public─â ├«n Jurnalul Oficial al Uniunii Europene, cu cel pu╚¢in 6 luni ├«nainte de termenul final pentru depunerea ofertelor;...b)caietul de sarcini se pune la dispozi╚¢ia oric─ârui operator economic interesat, la solicitarea acestuia;...c)caietul de sarcini con╚¢ine descrierea detaliat─â a specifica╚¢iilor contractului ╚Öi a procedurii care trebuie urmat─â de c─âtre to╚¢i ofertan╚¢ii, precum ╚Öi lista complet─â a criteriilor care determin─â selec╚¢ia ofertan╚¢ilor ╚Öi atribuirea contractului, inclusiv stimulentele, cum ar fi subven╚¢iile, care sunt acoperite de ofert─â;...d)pentru invita╚¢iile de participare la procedura de ofertare care se refer─â la capacitatea de producere previzionat─â a fi necesar─â, trebuie luate ├«n considerare ╚Öi ofertele de furnizare de energie electric─â cu garan╚¢ii pe termen lung, propuse de unit─â╚¢ile de produc╚¢ie existente, cu condi╚¢ia ca acestea s─â permit─â acoperirea nevoilor suplimentare.......(6)...Organizarea, monitorizarea ╚Öi controlul procedurii de ofertare se realizeaz─â de operatorul de transport ╚Öi de sistem.', NULL, 18, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
