INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1395, 5, '20', 'Articolul 20', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Pia╚¢a de energie electric─â...(1)...Pia╚¢a de energie electric─â este compus─â din pia╚¢a reglementat─â ╚Öi pia╚¢a concuren╚¢ial─â, iar tranzac╚¢iile cu energie se fac angro sau cu am─ânuntul....(2)...Cre╚Öterea ponderii pie╚¢ei concuren╚¢iale se realizeaz─â gradat, prin asigurarea accesului pe aceast─â pia╚¢─â pentru c├ót mai mul╚¢i participan╚¢i, produc─âtori, furnizori ╚Öi clien╚¢i finali, ├«n conformitate cu prevederile art. 23....(3)...Se ├«nfiin╚¢eaz─â Comisia de solu╚¢ionare a disputelor, ca organism care solu╚¢ioneaz─â disputele pe pia╚¢a angro ╚Öi cu am─ânuntul ap─ârute ├«ntre participan╚¢ii la pia╚¢a de energie electric─â....(4)...Comisia de solu╚¢ionare a disputelor este format─â din 5 membri, care sunt numi╚¢i prin decizie a pre╚Öedintelui ANRE pe o perioad─â de 3 ani, dintre speciali╚Ötii ANRE cu vechime de cel pu╚¢in 8 ani ├«n sectorul energiei electrice....(5)...Comisia de solu╚¢ionare a disputelor ├«╚Öi desf─â╚Öoar─â activitatea pe baza unui regulament de organizare ╚Öi func╚¢ionare, aprobat prin decizie a pre╚Öedintelui ANRE, dup─â consultare public─â.', NULL, 20, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1396, 5, '21', 'Articolul 21', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Participan╚¢ii la pia╚¢a de energie electric─â...(1)...Participan╚¢ii la pia╚¢a de energie electric─â trebuie s─â respecte regulile de func╚¢ionare a acesteia, prev─âzute prin ordine, decizii ╚Öi avize favorabile emise de autoritatea competent─â ╚Öi s─â efectueze pl─â╚¢ile pentru energia electric─â ╚Öi serviciile de care beneficiaz─â, rezultate din tranzac╚¢iile efectuate ├«n conformitate cu aceste reguli, la termenele scadente prev─âzute ├«n contractele ├«ncheiate ├«ntre p─âr╚¢i....(2)...Participan╚¢ii la pia╚¢a de energie electric─â ╚Öi structurile opera╚¢ionale asociate sunt: produc─âtorul, operatorul de transport ╚Öi de sistem, operatorul pie╚¢ei de energie electric─â, operatorul de distribu╚¢ie, furnizorul ╚Öi clientul....(3)...Participan╚¢ii la pia╚¢─â sunt obliga╚¢i s─â-╚Öi asume responsabilitatea financiar─â pentru plata dezechilibrelor pe care le genereaz─â pe pia╚¢a de energie electric─â, cu excep╚¢ia dezechilibrelor pentru care se aplic─â reguli de socializare/redistribuire stabilite de autoritatea competent─â sau prev─âzute explicit de legisla╚¢ia ├«n vigoare, s─â respecte prevederile licen╚¢elor ╚Öi ale reglement─ârilor emise de autoritatea competent─â. Obliga╚¢iile de plat─â vor fi achitate ├«n termen de maximum o lun─â de la ├«ncheierea lunii de tranzac╚¢ionare, pe baza cantit─â╚¢ilor de energie electric─â tranzac╚¢ionate m─âsurate....(4)...Clien╚¢ii au obliga╚¢ia pl─â╚¢ii contravalorii facturilor pentru energia electric─â ╚Öi pentru serviciile de care beneficiaz─â, la termenele scadente prev─âzute ├«n contractele ├«ncheiate ├«ntre p─âr╚¢i, precum ╚Öi constituirea de garan╚¢ii financiare ├«n vederea evit─ârii riscurilor de neplat─â pe pia╚¢a de energie electric─â....(5)...Participan╚¢ii la pia╚¢a de energie electric─â trebuie s─â notifice operatorului de transport ╚Öi de sistem importurile, exporturile ╚Öi tranzitele pe intervale de tranzac╚¢ionare, cu partenerii externi, pe fiecare grani╚¢─â....(6)...Participan╚¢ii la pia╚¢a de energie electric─â trebuie s─â notifice operatorului de transport ╚Öi de sistem toate cantit─â╚¢ile contractate nete reciproce....(7)...Participan╚¢ii la pia╚¢a de energie electric─â au obliga╚¢ia s─â transmit─â operatoruluipie╚¢ei de energie electric─â ╚Öi de echilibrare informa╚¢ii privind cantit─â╚¢ile de energie electric─â tranzac╚¢ionate aferente contractelor de v├ónzare-cump─ârare de energie electric─â ├«ncheiate.', NULL, 21, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
