INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1403, 5, '28', 'Articolul 28', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile produc─âtorilor...Produc─âtorii au, ├«n principal, urm─âtoarele obliga╚¢ii:a)s─â asigure livr─ârile de energie electric─â ╚Öi serviciile tehnologice de sistem, cu respectarea condi╚¢iilor impuse prin licen╚¢e, clauze contractuale ╚Öi reglement─âri ├«n vigoare;...b)├«n cazul unit─â╚¢ilor dispecerizabile s─â oferteze ├«ntreaga putere electric─â disponibil─â pe pia╚¢a de echilibrare, definit─â conform reglement─ârilor emise de autoritatea competent─â;...c)s─â oferteze public ╚Öi nediscriminatoriu pe pia╚¢a concuren╚¢ial─â ├«ntreaga energie electric─â disponibil─â;...d)s─â oferteze nediscriminatoriu serviciile tehnologice de sistem;...e)s─â nu transmit─â la operatorul de transport ╚Öi de sistem notific─âri fizice ├«n dezechilibru negativ fa╚¢─â de contractele pe care le au ├«ncheiate, cu excep╚¢ia produc─âtorilor care beneficiaz─â de scheme de sprijin, conform prevederilor prezentului titlu;...f)s─â men╚¢in─â o rezerv─â de combustibil la un nivel suficient sau, dup─â caz, o rezerv─â suficient─â de ap─â, pentru ├«ndeplinirea obliga╚¢iilor de produc╚¢ie ╚Öi furnizare continu─â a energiei electrice, prev─âzute de reglement─ârile ├«n vigoare;...g)s─â se conformeze, din punct de vedere operativ, cerin╚¢elor operatorului de transport ╚Öi de sistem ╚Öi s─â ├«nfiin╚¢eze, dup─â caz, trepte proprii de conducere operativ─â;...h)s─â transmit─â autorit─â╚¢ii competente un raport anual de activitate, conform reglement─ârilor ├«n vigoare, chiar ├«n condi╚¢iile ├«n care nu de╚¢ine licen╚¢a de producere sau capacit─â╚¢ile sunt transferate altui operator economic....', NULL, 28, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1404, 5, '29', 'Articolul 29', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Drepturile produc─âtorilor...(1)...Produc─âtorii de energie electric─â au, ├«n principal, urm─âtoarele drepturi:a)s─â aib─â acces la re╚¢elele electrice de interes public, ├«n condi╚¢iile prezentului titlu;...b)s─â ob╚¢in─â, ├«n condi╚¢iile legii, culoar de trecere pentru liniile electrice proprii;...c)s─â tranzac╚¢ioneze energia electric─â ╚Öi serviciile tehnologice de sistem pe pia╚¢a reglementat─â ╚Öi concuren╚¢ial─â ├«n mod transparent ╚Öi nediscriminatoriu;...d)s─â ├«nfiin╚¢eze ╚Öi s─â men╚¢in─â un sistem propriu de telecomunica╚¢ii pentru leg─âtura cu capacit─â╚¢ile sale de produc╚¢ie, cu clien╚¢ii sau cu treptele de conducere operativ─â;...e)s─â comercializeze energia termic─â produs─â ├«n cogenerare;...f)s─â acceseze schemele de sprijin instituite la nivel na╚¢ional.......(2)...F─âr─â a periclita siguran╚¢a ├«n func╚¢ionare a SEN, produc─âtorii de energie electric─â pot oferta pe pia╚¢a intern─â european─â servicii tehnologice de sistem conform reglement─ârilor ANRE, cu avizul operatorului de transport ╚Öi de sistem.', NULL, 29, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1405, 5, '30', 'Articolul 30', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Transportul energiei electrice...(1)...Transportul energiei electrice se realizeaz─â de c─âtre operatorul de transport ╚Öi de sistem, persoan─â juridic─â certificat─â de autoritatea competent─â, ├«n condi╚¢iile prezentei legi....(2)...Re╚¢eaua electric─â de transport al energiei electrice este proprietatea public─â a statului....(3)...Terenurile pe care se situeaz─â re╚¢elele electrice de transport existente la intrarea ├«n vigoare a prezentei legi sunt ╚Öi r─âm├ón ├«n proprietatea public─â a statului pe durata de existen╚¢─â a re╚¢elei....(4)...Fac excep╚¢ie de la prevederile alin. (2) ╚Öi (3) terenurile ╚Öi componentele de re╚¢ea electric─â de transport pentru care operatorul de transport ╚Öi de sistem a dob├óndit dreptul de proprietate, ├«n condi╚¢iile legii.', NULL, 30, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1406, 5, '31', 'Articolul 31', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Certificarea operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem se organizeaz─â ╚Öi func╚¢ioneaz─â dup─â modelul "operator de sistem independent" ╚Öi este persoana juridic─â certificat─â de autoritatea competent─â ├«n ceea ce prive╚Öte ├«ndeplinirea condi╚¢iilor prev─âzute la art. 34....(2)...Pentru a respecta condi╚¢iile prev─âzute la art. 34, prin hot─âr├óre a Guvernului, elaborat─â la propunerea ministerului de resort, se desemneaz─â entitatea public─â ce de╚¢ine calitatea de ac╚¢ionar majoritar la operatorii economici care desf─â╚Öoar─â activit─â╚¢i de producere ╚Öi furnizare, pe de o parte, precum ╚Öi entitatea public─â ce de╚¢ine calitatea de ac╚¢ionar majoritar la operatorul de transport ╚Öi de sistem, pe de alt─â parte....(3)...├Än termen de 15 zile de la adoptarea m─âsurilor prev─âzute la alin. (2), operatorul de transport ╚Öi de sistem transmite ANRE, cu avizul proprietarului re╚¢elei de transport, cererea de certificare ├«nso╚¢it─â de documenta╚¢ia justificativ─â privind ├«ndeplinirea cerin╚¢elor prev─âzute la art. 34....(4)...ANRE emite o decizie de certificare preliminar─â ├«n termen de maximum 4 luni de la data ├«nregistr─ârii cererii operatorului de transport ╚Öi de sistem care se notific─â Comisiei Europene, ├«nso╚¢it─â de documenta╚¢ia aferent─â....(5)...Procedura de certificare a operatorului de transport ╚Öi de sistem se finalizeaz─â conform prevederilor art. 3 din Regulamentul (CE) nr. 714/2009 ....(6)...Desemnarea operatorului de transport ╚Öi de sistem ├«n calitate de operator de sistem independent se aprob─â de Comisia European─â ca urmare a comunic─ârii de c─âtre ANRE a operatorului economic certificat, dup─â ├«ncheierea procedurii de certificare conform alin. (5)....(7)...├Ämpreun─â cu decizia de certificare, autoritatea competent─â este obligat─â s─â notifice Comisiei Europene orice solicitare de certificare a proprietarului re╚¢elei de transport sau a operatorului de transport ╚Öi de sistem, care este controlat de o persoan─â ori de persoane din una sau mai multe ╚¢─âri ter╚¢e, precum ╚Öi orice circumstan╚¢e ├«n urma c─ârora o persoan─â sau persoane din una ori mai multe ╚¢─âri ter╚¢e ar dob├óndi controlul asupra re╚¢elei de transport sau asupra operatorului de transport ╚Öi de sistem, dac─â este cazul.', NULL, 31, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1407, 5, '32', 'Articolul 32', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Certificarea unui operator de transport ╚Öi de sistem care este controlat de ╚¢─âri ter╚¢e...(1)...├Änainte de certificarea unui operator de transport ╚Öi de sistem care este controlat de o persoan─â sau de persoane din una sau mai multe ╚¢─âri ter╚¢e, ANRE solicit─â un aviz Comisiei Europene cu privire la faptul c─â:a)entitatea respectiv─â respect─â cerin╚¢ele art. 34; ╚Öi...b)acordarea certific─ârii nu pune ├«n pericol siguran╚¢a aliment─ârii cu energie a Uniunii Europene.......(2)...├Än termen de dou─â luni de la emiterea avizului de c─âtre Comisia European─â, ANRE adopt─â decizia final─â cu privire la certificare, pe care o public─â ├«mpreun─â cu avizul Comisiei Europene. ├Än situa╚¢ia ├«n care Comisia European─â nu emite un aviz ├«n termen de dou─â luni de la primirea solicit─ârii, se poate considera c─â nu exist─â obiec╚¢ii privind decizia ANRE....(3)...├Än cazul ├«n care decizia final─â a ANRE difer─â de avizul Comisiei Europene, se public─â motivarea acestei decizii....(4)...ANRE refuz─â certificarea prev─âzut─â la alin. (1) ├«n urm─âtoarele situa╚¢ii:a)entitatea ├«n cauz─â nu ├«ndepline╚Öte cerin╚¢ele prev─âzute la art. 34;...b)acordarea certific─ârii poate pune ├«n pericol siguran╚¢a aliment─ârii cu energie electric─â pe teritoriul na╚¢ional sau al Uniunii Europene. La examinarea acestui aspect, ANRE ╚¢ine seama de:......(i) drepturile ╚Öi obliga╚¢iile Uniunii Europene fa╚¢─â de ╚¢ara ter╚¢─â respectiv─â ├«n temeiul dreptului interna╚¢ional, inclusiv al oric─ârui acord ├«ncheiat cu una sau mai multe ╚¢─âri ter╚¢e la care Uniunea European─â este parte ╚Öi ├«n care sunt abordate aspecte legate de siguran╚¢─â aliment─ârii cu energie;...(├«i) drepturile ╚Öi obliga╚¢iile Rom├óniei fa╚¢─â de ╚¢ara ter╚¢─â respectiv─â ├«n temeiul acordurilor ├«ncheiate cu ╚¢ara ter╚¢─â respectiv─â, ├«n m─âsura ├«n care acestea respect─â dreptul european; precum ╚Öi...(iii) alte elemente specifice ale cazului ╚Öi de ╚¢ara ter╚¢─â implicat─â.', NULL, 32, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1408, 5, '33', 'Articolul 33', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Reevaluarea certific─ârii operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem notific─â ANRE orice tranzac╚¢ie planificat─â care poate necesita o reevaluare a conformit─â╚¢ii acesteia cu cerin╚¢ele art. 34, precum ╚Öi orice circumstan╚¢e ├«n baza c─ârora o persoan─â sau persoane dintr-una ori din mai multe ╚¢─âri ter╚¢e ar putea dob├óndi controlul asupra sistemului de transport sau asupra operatorului de transport ╚Öi de sistem....(2)...ANRE poate decide o reevaluare a ├«ndeplinirii de c─âtre operatorul de transport ╚Öi de sistem a cerin╚¢elor prev─âzute la art. 34:a)din oficiu;...b)├«n urma notific─ârii de c─âtre operatorul de transport ╚Öi de sistem ├«n condi╚¢iile alin. (1);...c)la cererea motivat─â a Comisiei Europene....', NULL, 33, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1438, 5, '63', 'Articolul 63', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Dreptul clien╚¢ilor finali de alegere a tarifelor...(1)...Clien╚¢ii finali prev─âzu╚¢i la art. 55 alin. (1) au dreptul s─â opteze pentru oricare dintre tarifele reglementate aplicabile categoriei din care fac parte sau s─â beneficieze de serviciul universal, ├«n condi╚¢iile stabilite prin reglement─ârile ANRE....(2)...Clien╚¢ii casnici au dreptul de a opta pentru orice modalitate de plat─â pe care furnizorul o pune la dispozi╚¢ie....(3)...Clien╚¢ii finali nu mai pot reveni la tarife reglementate dac─â ╚Öi-au exercitat dreptul de eligibilitate.', NULL, 63, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1409, 5, '34', 'Articolul 34', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Condi╚¢ii de certificare a operatorului de transport ╚Öi de sistem...(1)...Certificarea operatorului de transport ╚Öi de sistem se realizeaz─â dac─â sunt ├«ndeplinite urm─âtoarele cerin╚¢e:a)aceea╚Öi persoan─â sau acelea╚Öi persoane nu au dreptul:......(i) s─â exercite, direct sau indirect, controlul asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere ori de furnizare ╚Öi ├«n acela╚Öi timp s─â exercite, direct sau indirect, controlul ori s─â exercite vreun drept asupra operatorului de transport ╚Öi de sistem sau asupra unei re╚¢ele de transport; ori...(├«i) s─â exercite, direct sau indirect, controlul asupra operatorului de transport ╚Öi de sistem ori asupra unei re╚¢ele de transport ╚Öi s─â exercite, direct sau indirect, controlul ori s─â exercite vreun drept asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare;b)aceea╚Öi persoan─â sau acelea╚Öi persoane nu sunt abilitate s─â numeasc─â membri ├«n consiliul de supraveghere, ├«n consiliul de administra╚¢ie ori ├«n alte organisme care reprezint─â operatorul de transport ╚Öi de sistem din punct de vedere juridic ├«n cazul unui operator de transport ╚Öi de sistem sau ├«n cazul unei re╚¢ele de transport ╚Öi, de asemenea, s─â exercite, direct ori indirect, controlul sau s─â exercite vreun drept asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere ori de furnizare;...c)aceea╚Öi persoan─â nu este abilitat─â s─â fie membru ├«n consiliul de supraveghere, ├«n consiliul de administra╚¢ie sau ├«n alte organisme care reprezint─â operatorul economic din punct de vedere juridic, at├ót ├«n cazul unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare, c├ót ╚Öi ├«n cazul unui operator de transport ╚Öi de sistem ori ├«n cazul unei re╚¢ele de transport;...d)operatorul de transport ╚Öi de sistem dispune de resursele financiare, tehnice, fizice ╚Öi umane pentru ├«ndeplinirea obliga╚¢iilor prev─âzute la art. 36;...e)operatorul de transport ╚Öi de sistem ├«╚Öi asum─â angajamentul de a respecta un plan de dezvoltare a re╚¢elei de transport pe 10 ani, aprobat de autoritatea competent─â;...f)operatorul de transport ╚Öi de sistem are capacitatea de a respecta obliga╚¢iile ce ├«i revin ├«n temeiul Regulamentului (CE) nr. 714/2009, inclusiv ├«n ceea ce prive╚Öte cooperarea cu al╚¢i operatori de transport ╚Öi de sistem la nivel european ╚Öi regional;...g)proprietarul re╚¢elei de transport ├«ndepline╚Öte cerin╚¢ele prev─âzute la art. 37.......(2)...Drepturile prev─âzute la alin. (1) lit. a) ╚Öi b) cuprind, ├«n special:...(i) competen╚¢a de a exercita drepturi de vot;...(├«i) competen╚¢a de a numi membri ├«n consiliul de supraveghere, consiliul de administra╚¢ie sau alte organe care reprezint─â operatorul economic din punct de vedere juridic; sau...(iii) de╚¢inerea unei cote majoritare....(3)...├Än sensul alin. (1) lit. a), no╚¢iunea "operator economic care desf─â╚Öoar─â activitate de producere sau de furnizare de energie" include ╚Öi activit─â╚¢ile de producere ╚Öi furnizare de gaze naturale, iar termenii "operator de transport ╚Öi sistem" ╚Öi "re╚¢ea de transport" includ ╚Öi termenii care se utilizeaz─â ├«n acela╚Öi sens ├«n sectorul gazelor naturale.', NULL, 34, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1410, 5, '35', 'Articolul 35', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Planuri de dezvoltare...(1)...Operatorul de transport ╚Öi de sistem are obliga╚¢ia de a elabora planuri de investi╚¢ii ╚Öi de dezvoltare a re╚¢elei de transport pe 10 ani, ├«n concordan╚¢─â cu stadiul actual ╚Öi evolu╚¢ia viitoare a consumului de energie ╚Öi a surselor, inclusiv importurile ╚Öi exporturile de energie....(2)...Planurile de dezvoltare prev─âzute la alin. (1) con╚¢in modalit─â╚¢ile de finan╚¢are ╚Öi realizare a investi╚¢iilor privind re╚¢elele de transport, cu luarea ├«n considerare ╚Öi a planurilor de amenajare ╚Öi sistematizare a teritoriului str─âb─âtut de acestea, ├«n condi╚¢iile respect─ârii normelor de protec╚¢ie a mediului....(3)...Planurile prev─âzute la alin. (1) se aprob─â de c─âtre ANRE.', NULL, 35, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1411, 5, '36', 'Articolul 36', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem presteaz─â serviciul public de transport pentru to╚¢i utilizatorii re╚¢elelor electrice de transport, ├«n condi╚¢ii nediscriminatorii, asigur├ónd accesul la acestea oric─ârui solicitant ├«n condi╚¢iile legii ╚Öi evit├ónd ├«n special discriminarea ├«n favoarea operatorilor economici afilia╚¢i, cu respectarea normelor ╚Öi performan╚¢elor prev─âzute ├«n reglement─ârile tehnice ├«n vigoare....(2)...Operatorul de transport ╚Öi de sistem poate s─â participe la tranzac╚¢ionarea energiei electrice numai ├«n urm─âtoarele situa╚¢ii:a)pentru acoperirea consumului ├«n re╚¢elele electrice proprii ╚Öi ├«n locurile de consum care ├«i apar╚¢in;...b)pentru men╚¢inerea echilibrului produc╚¢ie-consum, prin opera╚¢iuni de v├ónzare-cump─ârare ├«n pia╚¢a de echilibrare sau prin opera╚¢iuni de v├ónzare-cump─ârare cu al╚¢i operatori de transport din ╚¢─ârile vecine, conform reglement─ârilor ├«n vigoare ╚Öi normelor ENTSO-E.......(3)...Operatorul de transport ╚Öi de sistem poate efectua opera╚¢iuni de v├ónzare-cump─ârare de servicii tehnologice de sistem cu operatorii de transport ╚Öi de sistem ai ╚¢─ârilor cu care Rom├ónia a ├«ncheiat acorduri care faciliteaz─â crearea ╚Öi dezvoltarea pie╚¢elor regionale, cu produc─âtorii sau clien╚¢ii, conform reglement─ârilor emise de autoritatea competent─â....(4)...Tranzac╚¢iile cu energie electric─â ╚Öi servicii tehnologice de sistem se realizeaz─â pe baza unor proceduri transparente ╚Öi nediscriminatorii, prin mecanisme concuren╚¢iale, conform reglement─ârilor autorit─â╚¢ii competente....(5)...Operatorul de transport ╚Öi de sistem presteaz─â serviciul de sistem ├«n condi╚¢ii nediscriminatorii pentru to╚¢i utilizatorii SEN, asigur├ónd conducerea operativ─â, ├«n scopul realiz─ârii siguran╚¢ei func╚¢ion─ârii, stabilit─â╚¢ii frecven╚¢ei ╚Öi tensiunii, continuit─â╚¢ii ├«n alimentarea clien╚¢ilor ╚Öi coordon─ârii schimburilor de energie electric─â cu alte sisteme electroenergetice....(6)...Operatorul de transport ╚Öi de sistem poate de╚¢ine ac╚¢iuni la societ─â╚¢i comerciale ├«nfiin╚¢ate pe teritoriul na╚¢ional sau al altor state, av├ónd ca obiect principal de activitate alocarea capacit─â╚¢ilor de interconexiune ╚Öi verificarea siguran╚¢ei re╚¢elei la nivel regional, acoperind teritoriul a dou─â sau mai multe state....(7)...Operatorul de transport ╚Öi de sistem desf─â╚Öoar─â, ├«n principal, urm─âtoarele activit─â╚¢i:a)asigur─â capacitatea pe termen lung a re╚¢elei de transport de a satisface cererile rezonabile de transport de energie electric─â ╚Öi exploateaz─â, ├«ntre╚¢ine, reabiliteaz─â ╚Öi dezvolt─â ├«n condi╚¢ii economice re╚¢eaua de transport pentru a-i asigura siguran╚¢a, fiabilitatea ╚Öi eficien╚¢a, cu respectarea normelor privind protec╚¢ia mediului;...b)garanteaz─â mijloacele adecvate pentru ├«ndeplinirea obliga╚¢iilor de serviciu public;...c)contribuie la realizarea siguran╚¢ei ├«n alimentarea cu energie electric─â, prin asigurarea unor capacit─â╚¢i de transport adecvate ╚Öi prin men╚¢inerea fiabilit─â╚¢ii acestora;...d)asigur─â gestionarea fluxurilor de energie ├«n re╚¢eaua de transport, ╚¢in├ónd seama de schimburile de energie cu alte sisteme interconectate;...e)achizi╚¢ioneaz─â serviciile tehnologice de sistem ╚Öi calific─â produc─âtorii ╚Öi clien╚¢ii dispecerizabili care furnizeaz─â servicii tehnologice de sistem, pe baza unei proceduri proprii, aprobat─â de autoritatea competent─â;...f)realizeaz─â schimbul de informa╚¢ii cu al╚¢i operatori de transport ╚Öi de sistem interconecta╚¢i ╚Öi cu al╚¢i operatori economici din sectorul energiei, cu respectarea reglement─ârilor ENTSO-E privind protocoalele de schimb de informa╚¢ii, rapoartele, structura ╚Öi procedurile de acces la bazele de date;...g)asigur─â alocarea capacit─â╚¢ilor de interconexiune, colecteaz─â venituri rezultate din gestionarea congestiilor ╚Öi efectueaz─â pl─â╚¢i ├«n temeiul mecanismului de compensare ├«ntre operatorii de transport ╚Öi de sistem ├«n conformitate cu art. 13 din Regulamentul (CE) nr. 714/2009, acord├ónd ╚Öi gestion├ónd accesul ter╚¢ilor ╚Öi oferind explica╚¢ii motivate atunci c├ónd refuz─â un astfel de acces, sub supravegherea ANRE;...h)exploateaz─â, ├«ntre╚¢ine ╚Öi dezvolt─â instala╚¢iile de m─âsurare ╚Öi contorizare a transferului de energie electric─â prin re╚¢elele electrice de transport ╚Öi la interfa╚¢a cu utilizatorii re╚¢elelor electrice de transport care ├«i apar╚¢in, instala╚¢iile de informatic─â ╚Öi telecomunica╚¢ii din re╚¢elele electrice de transport aferente SEN;...i)analizeaz─â ╚Öi avizeaz─â ├«ndeplinirea condi╚¢iilor tehnice de racordare de c─âtre utilizatorii re╚¢elelor electrice de transport, ├«n conformitate cu prevederile reglement─ârilor tehnice ├«n vigoare;...j)asigur─â transmiterea rezultatelor m─âsur─ârilor de energie electric─â la operatorul pie╚¢ei de energie electric─â ├«n vederea realiz─ârii decont─ârii tranzac╚¢iilor din pia╚¢a de echilibrare ╚Öi a dezechilibrelor p─âr╚¢ilor responsabile cu echilibrarea, precum ╚Öi accesul beneficiarilor serviciului de transport pentru verificarea grupurilor de m─âsurare;...k)realizeaz─â planificarea opera╚¢ional─â ╚Öi conducerea operativ─â a SEN la nivel central ╚Öi teritorial pe baza prognozei proprii, conform reglement─ârilor legale ├«n vigoare, acord├ónd prioritate instala╚¢iilor de producere care utilizeaz─â surse regenerabile de energie sau care produc energie electric─â ├«n cogenerare de ├«nalt─â eficien╚¢─â, ├«n m─âsura ├«n care func╚¢ionarea sigur─â a re╚¢elei na╚¢ionale de energie electric─â permite acest lucru;...l)autorizeaz─â personalul care realizeaz─â conducerea operativ─â, conform reglement─ârilor ├«n vigoare;...m)culege, ├«nregistreaz─â ╚Öi arhiveaz─â datele statistice privind func╚¢ionarea SEN;...n)elaboreaz─â ╚Öi supune aprob─ârii autorit─â╚¢ii competente normele tehnice ╚Öi reglement─ârile specifice necesare pentru realizarea activit─â╚¢ii de conducere operativ─â, cu consultarea participan╚¢ilor la pia╚¢a de energie electric─â;...o)elaboreaz─â, ├«n condi╚¢iile legii, planul de ap─ârare a SEN ├«mpotriva perturba╚¢iilor majore;...p)elaboreaz─â studii, programe ╚Öi lucr─âri privind dezvoltarea SEN;...q)elaboreaz─â ╚Öi supune aprob─ârii autorit─â╚¢ii competente regulile privind managementul congestiilor, inclusiv pe capacit─â╚¢ile de interconexiune, precum ╚Öi normele de atribuire a capacit─â╚¢ilor de interconexiune;...r)organizeaz─â ╚Öi administreaz─â pia╚¢a de echilibrare a energiei electrice.......(8)...├Än caz de avarie major─â care pericliteaz─â siguran╚¢a func╚¢ion─ârii SEN, operatorul de transport ╚Öi de sistem poate dispune, pe o perioad─â limitat─â, folosirea rezervei de ap─â ├«n afara programelor de exploatare a acumul─ârilor, cu obliga╚¢ia anun╚¢─ârii autorit─â╚¢ilor competente ├«n gospod─ârirea apelor....(9)...Restric╚¢iile ├«n aprovizionarea cu energie electric─â ├«n situa╚¢ii de urgen╚¢─â trebuie s─â respecte criterii predefinite ├«n ceea ce prive╚Öte gestionarea dezechilibrelor de c─âtre operatorul de transport ╚Öi de sistem. Orice m─âsur─â de siguran╚¢─â trebuie luat─â ├«n str├óns─â colaborare ╚Öi cu consultarea altor operatori de transport ╚Öi de sistem implica╚¢i, cu respectarea contractelor bilaterale aplicabile, inclusiv a acordurilor privind schimbul de informa╚¢ii....(10)...Operatorul de transport ╚Öi de sistem ╚Öi proprietarul re╚¢elei de transport public─â informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de re╚¢ea, conform reglement─ârilor ANRE, ├«n vederea asigur─ârii accesului eficient la re╚¢ea, a unei concuren╚¢e efective ╚Öi a func╚¢ion─ârii eficiente a pie╚¢ei de energie, nefiindu-le permis─â divulgarea informa╚¢iilor sensibile din punct de vedere comercial ob╚¢inute pe parcursul activit─â╚¢ilor acestora, inclusiv a celor ob╚¢inute de la ter╚¢i ├«n contextul acord─ârii sau al negocierii accesului la re╚¢ea....(11)...Cheltuielile pentru modificarea instala╚¢iilor de transport al energiei electrice, ca urmare a racord─ârii de noi utilizatori sau a schimb─ârii caracteristicilor energetice ini╚¢iale ale utilizatorilor existen╚¢i, inclusiv pentru eliberarea unor amplasamente, sunt suportate conform reglement─ârilor ├«n vigoare....(12)...Operatorul de transport ╚Öi de sistem aloc─â capacit─â╚¢i de interconexiune pe una sau mai multe grani╚¢e ├«mpreun─â cu al╚¢i operatori economici, inclusiv pentru cei ├«nregistra╚¢i pe teritoriul unui stat membru al Uniunii Europene sau al Tratatului de instituire a Comunit─â╚¢ii Energiei, ├«n condi╚¢iile semn─ârii unui memorandum de ├«n╚¢elegere ├«ntre p─âr╚¢i, avizat de ANRE.', NULL, 36, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1412, 5, '37', 'Articolul 37', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Atribu╚¢iile proprietarului re╚¢elei de transport...(1)...Proprietarul re╚¢elei de transport:a)coopereaz─â cu operatorul de transport ╚Öi de sistem ├«n vederea ├«ndeplinirii atribu╚¢iilor acestuia, furniz├óndu-i toate informa╚¢iile relevante;...b)finan╚¢eaz─â ╚Öi/sau ├«╚Öi d─â acordul asupra modalit─â╚¢ii de finan╚¢are a investi╚¢iilor ├«n re╚¢eaua de transport, stabilite de operatorul de transport ╚Öi de sistem ╚Öi aprobate de ANRE;...c)de╚¢ine r─âspunderea privind activele re╚¢elei de transport, cu excep╚¢ia r─âspunderii privind atribu╚¢iile operatorului de transport ╚Öi de sistem;...d)ofer─â garan╚¢ii pentru facilitarea finan╚¢─ârii eventualelor extinderi ale re╚¢elei, cu excep╚¢ia investi╚¢iilor pentru care ╚Öi-a dat acordul s─â fie finan╚¢ate de c─âtre orice parte interesat─â, inclusiv de c─âtre operatorul de transport ╚Öi de sistem, potrivit prevederilor lit. b).......(2)...Consiliul Concuren╚¢ei, ├«n str├óns─â cooperare cu ANRE, este abilitat cu competen╚¢ele necesare pentru a monitoriza ├«n mod eficient respectarea de c─âtre proprietarul re╚¢elei de transport a obliga╚¢iilor acestuia, ├«n temeiul alin. (1).', NULL, 37, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1413, 5, '38', 'Articolul 38', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Separarea proprietarului re╚¢elei de transport...(1)...├Än cazul ├«n care proprietarul re╚¢elei de transport face parte dintr-un operator economic integrat pe vertical─â, proprietarul re╚¢elei de transport este independent cel pu╚¢in ├«n ceea ce prive╚Öte statutul s─âu juridic, organizarea ╚Öi procesul decizional fa╚¢─â de alte activit─â╚¢i care nu au leg─âtur─â cu transportul energiei electrice....(2)...Pentru a asigura independen╚¢a proprietarului re╚¢elei de transport ├«n condi╚¢iile alin. (1), se aplic─â urm─âtoarele criterii minime:a)persoanele cu func╚¢ie de conducere din cadrul proprietarului re╚¢elei de transport nu pot face parte din structurile operatorului economic integrat din domeniul energiei electrice care r─âspunde, direct sau indirect, de gestionarea zilnic─â a activit─â╚¢ilor de producere, distribu╚¢ie ╚Öi furnizare a energiei electrice;...b)persoanele cu func╚¢ie de conducere din cadrul proprietarului re╚¢elei de transport ac╚¢ioneaz─â independent de orice interes de pia╚¢─â ├«n ├«ndeplinirea atribu╚¢iilor de serviciu;...c)proprietarul re╚¢elei de transport stabile╚Öte un program de conformitate, care con╚¢ine m─âsurile luate pentru a garanta excluderea practicilor discriminatorii ╚Öi stabile╚Öte ╚Öi obliga╚¢iile specifice impuse angaja╚¢ilor pentru realizarea obiectivului de independen╚¢─â;...d)proprietarul re╚¢elei de transport desemneaz─â o persoan─â sau un organism, denumit─â/ denumit agent de conformitate, care s─â asigure monitorizarea adecvat─â a respect─ârii programului de conformitate ╚Öi care depune la ANRE, ├«n luna decembrie a fiec─ârui an, un raport cu privire la m─âsurile luate, ce se va public─â pe site-ul operatorului de transport ╚Öi de sistem.......(3)...Proprietarul re╚¢elei de transport transmite spre aprobare, la ANRE, toate proiectele de contracte ce urmeaz─â a fi ├«ncheiate cu operatorul de transport ╚Öi de sistem, inclusiv cele referitoare la utilizarea bunurilor existente, precum ╚Öi a celor realizate ca urmare a investi╚¢iilor ├«n re╚¢eaua de transport.', NULL, 38, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1414, 5, '39', 'Articolul 39', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Raportarea fluxurilor fizice de energie electric─â...Operatorul de transport ╚Öi de sistem este obligat, conform reglement─ârilor ANRE, s─â ├«ntocmeasc─â, din 3 ├«n 3 luni, un raport cu privire la fluxurile fizice de energie electric─â ce s-au derulat ├«n cadrul importurilor de energie electric─â din ╚¢─âri ter╚¢e Uniunii Europene ├«n cursul ultimelor 3 luni anterioare datei raport─ârii, care se transmite ANRE ╚Öi ministerului de resort, ├«n vederea inform─ârii Comisiei Europene.', NULL, 39, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1415, 5, '40', 'Articolul 40', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii privind de╚¢inerea controlului asupra operatorilor de transport ╚Öi de sistem...Operatorilor economici care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare de energie electric─â le este interzis ca, direct ori indirect, s─â exercite controlul ori s─â exercite vreun drept cu privire la operatorii de transport ╚Öi de sistem separa╚¢i din alte state ale Uniunii Europene care aplic─â dispozi╚¢iile art. 9 alin. (1) din Directiva 2009/72/CE.', NULL, 40, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1416, 5, '41', 'Articolul 41', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii privind personalul operatorului de transport ╚Öi de sistem...Persoanele care exercit─â ├«n cadrul operatorului de transport ╚Öi de sistem func╚¢ii de conducere sau alte func╚¢ii relevante ├«n temeiul c─ârora au avut acces la informa╚¢ii comerciale sensibile nu pot ocupa func╚¢ii similare ├«n cadrul operatorilor economici din domeniul producerii, distribu╚¢iei ╚Öi/sau furniz─ârii de energie electric─â, pe o perioad─â de minimum 2 ani de la data ├«ncet─ârii raporturilor contractuale cu operatorul de transport ╚Öi de sistem, interdic╚¢ie prev─âzut─â ╚Öi reglementat─â ├«n contractul individual de munc─â.', NULL, 41, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1439, 5, '64', 'Articolul 64', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Protec╚¢ia clien╚¢ilor vulnerabili...(1)...Clien╚¢ii vulnerabili beneficiaz─â de facilit─â╚¢i privind asigurarea serviciului de furnizare a energiei electrice ╚Öi de acces la re╚¢ea....(2)...Tipurile de facilit─â╚¢i pentru fiecare dintre categoriile de clien╚¢i vulnerabili, cu excep╚¢ia m─âsurilor de natur─â financiar─â, precum ╚Öi modalit─â╚¢ile de implementare a acestora se stabilesc de c─âtre ANRE....(3)...Este interzis─â deconectarea de la re╚¢eaua electric─â a clien╚¢ilor vulnerabili inclusiv ├«n situa╚¢ii de criz─â de energie.', NULL, 64, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1417, 5, '42', 'Articolul 42', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii...Pentru protec╚¢ia instala╚¢iilor de transport se interzice persoanelor fizice sau juridice:a)s─â efectueze construc╚¢ii de orice fel ├«n zona de siguran╚¢─â a instala╚¢iilor, f─âr─â avizul de amplasament al operatorului de transport ╚Öi de sistem;...b)s─â efectueze s─âp─âturi de orice fel sau s─â ├«nfiin╚¢eze planta╚¢ii ├«n zona de siguran╚¢─â a re╚¢elelor electrice de transport, f─âr─â acordul operatorului de transport ╚Öi de sistem;...c)s─â depoziteze materiale pe culoarele de trecere ╚Öi ├«n zonele de protec╚¢ie ╚Öi de siguran╚¢─â a instala╚¢iilor, f─âr─â acordul operatorului de transport ╚Öi de sistem;...d)s─â arunce obiecte de orice fel pe re╚¢elele electrice de transport sau s─â intervin─â ├«n orice alt mod asupra acestora;...e)s─â deterioreze construc╚¢iile, ├«ngr─âdirile sau inscrip╚¢iile de identificare ╚Öi de avertizare aferente instala╚¢iilor de transport;...f)s─â limiteze sau s─â ├«ngr─âdeasc─â, prin execu╚¢ia de ├«mprejmuire, prin construc╚¢ii ori prin orice alt mod, accesul la instala╚¢ii al operatorului de transport ╚Öi de sistem....', NULL, 42, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1418, 5, '43', 'Articolul 43', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Operatorul pie╚¢ei de energie electric─â...(1)...Operatorul pie╚¢ei centralizate de energie electric─â este persoana juridic─â titular─â de licen╚¢─â care asigur─â organizarea ╚Öi administrarea pie╚¢elor centralizate de energie electric─â, cu excep╚¢ia pie╚¢ei de echilibrare, ├«n vederea tranzac╚¢ion─ârii de energie electric─â pe termen scurt, mediu ╚Öi lung, conform reglement─ârilor emise de autoritatea competent─â....(2)...Operatorului pie╚¢ei de energie electric─â nu ├«i este permis─â divulgarea informa╚¢iilor legate de tranzac╚¢iile de energie electric─â pe care le de╚¢ine, ob╚¢inute pe parcursul activit─â╚¢ii sale, altfel dec├ót ├«n condi╚¢iile legii....(3)...Pre╚¢urile stabilite pe pie╚¢ele centralizate de energie electric─â se fac publice conform reglement─ârilor ANRE.', NULL, 43, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1419, 5, '44', 'Articolul 44', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Distribu╚¢ia energiei electrice...(1)...Distribu╚¢ia energiei electrice se realizeaz─â de c─âtre operatorul de distribu╚¢ie, persoan─â juridic─â, titular─â de licen╚¢─â....(2)...Operatorii de distribu╚¢ie presteaz─â servicii pentru to╚¢i utilizatorii re╚¢elelor electrice de distribu╚¢ie, ├«n condi╚¢ii nediscriminatorii, asigur├ónd accesul la acestea oric─ârui solicitant care ├«ndepline╚Öte cerin╚¢ele prezentei legi, cu respectarea normelor ╚Öi standardelor de performan╚¢─â prev─âzute ├«n reglement─ârile tehnice ├«n vigoare....(3)...Re╚¢elele electrice de distribu╚¢ie se delimiteaz─â fa╚¢─â de instala╚¢iile de producere sau de re╚¢elele electrice de transport ╚Öi de cele ale clien╚¢ilor finali ├«n punctele de delimitare patrimonial─â....(4)...Terenurile pe care se situeaz─â re╚¢elele electrice de distribu╚¢ie existente la intrarea ├«n vigoare a prezentei legi sunt ╚Öi r─âm├ón ├«n proprietatea public─â a statului....(5)...Fac excep╚¢ie de la prevederile alin. (4) terenurile pentru care operatorul de distribu╚¢ie, titular de licen╚¢─â, a dob├óndit dreptul de proprietate, ├«n condi╚¢iile legii....(6)...Re╚¢elele electrice de distribu╚¢ie se dezvolt─â pe baza principiilor de eficien╚¢─â economic─â, cu respectarea planurilor de urbanism, a dreptului de proprietate, a protec╚¢iei mediului, a s─ân─ât─â╚¢ii ╚Öi vie╚¢ii persoanelor ╚Öi cu economisirea energiei, conform normelor tehnice ╚Öi de siguran╚¢─â cuprinse ├«n reglement─ârile tehnice ├«n vigoare....(7)...Cheltuielile pentru modificarea instala╚¢iilor de distribu╚¢ie a energiei electrice, ca urmare a racord─ârii de noi utilizatori sau a schimb─ârii caracteristicilor energetice ini╚¢iale ale utilizatorilor existen╚¢i, inclusiv pentru eliberarea unor amplasamente, sunt suportate conform reglement─ârilor emise de autoritatea competent─â, ├«n baza unor criterii obiective....(8)...├Än cazul ├«n care cheltuielile prev─âzute la alin. (7) sunt suportate de c─âtre client, capacit─â╚¢ile energetice rezultate vor fi preluate de c─âtre operatorul de distribu╚¢ie cu desp─âgubire, conform unei metodologii aprobate de ANRE, ├«n 60 de zile de la data intr─ârii ├«n vigoare a prezentei legi....(9)...P├ón─â la data intr─ârii ├«n vigoare a reglement─ârilor emise de autoritatea competent─â, suportarea cheltuielilor de modificare a instala╚¢iilor se afl─â ├«n sarcina celui care a generat modificarea....(10)...Operatorul de distribu╚¢ie este obligat s─â ofere o solu╚¢ie de racordare la re╚¢ea a tuturor solicitan╚¢ilor ╚Öi s─â comunice condi╚¢iile de racordare, conform reglement─ârilor ├«n vigoare.', NULL, 44, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1420, 5, '45', 'Articolul 45', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Operatorul de distribu╚¢ie...(1)...Operatorul de distribu╚¢ie are urm─âtoarele atribu╚¢ii principale:a)exploateaz─â, retehnologizeaz─â, reabiliteaz─â ╚Öi dezvolt─â re╚¢elele electrice de distribu╚¢ie, cu respectarea reglement─ârilor tehnice ├«n vigoare;...b)asigur─â tranzitarea energiei electrice prin re╚¢elele electrice de distribu╚¢ie, la cererea ╚Öi cu informarea operatorului de transport ╚Öi de sistem, pentru acele zone ale ╚¢─ârii ├«n care nu exist─â capacitate suficient─â prin re╚¢elele de transport, pentru evacuarea puterii din centrale electrice, inclusiv centrale electrice de cogenerare, pentru conectarea la sistemul electroenergetic al unei ╚¢─âri vecine, ├«n condi╚¢iile existen╚¢ei unui acord bilateral ├«n acest sens, ├«n cazul inciden╚¢elor ├«n SEN ╚Öi al execut─ârii lucr─ârilor de exploatare ╚Öi mentenan╚¢─â sau al lucr─ârilor noi ├«n re╚¢elele de transport, care pun ├«n indisponibilitate temporar─â re╚¢elele de transport din zon─â;...c)realizeaz─â lucr─âri de dezvoltare a re╚¢elelor electrice de distribu╚¢ie prin programe de dezvoltare optim─â a acestora, pe baza studiilor de perspectiv─â, prin consultare, dup─â caz, cu operatorul de transport ╚Öi de sistem ╚Öi prin programe specifice de modernizare pentru instala╚¢ii;...d)asigur─â conducerea operativ─â ├«n conformitate cu licen╚¢a de distribu╚¢ie, acord├ónd prioritate ├«n dispecerizare instala╚¢iilor de producere care folosesc surse de energie regenerabil─â sau celor care produc energie electric─â ├«n cogenerare de ├«nalt─â eficien╚¢─â, ├«n m─âsura ├«n care func╚¢ionarea sigur─â a re╚¢elei na╚¢ionale de energie electric─â permite acest lucru;...e)difuzeaz─â, ├«n mod nediscriminatoriu, informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de re╚¢ea, nefiindu-i permis─â divulgarea informa╚¢iilor comerciale cu caracter confiden╚¢ial ob╚¢inute pe parcursul activit─â╚¢ii sale;...f)supune aprob─ârii operatorului de transport ╚Öi de sistem programul repara╚¢iilor ╚Öi lucr─ârilor de ├«ntre╚¢inere planificate ├«n instala╚¢iile cu tensiunea nominal─â de linie de 110 kV;...g)monitorizeaz─â siguran╚¢a ├«n func╚¢ionare a re╚¢elelor electrice de distribu╚¢ie, precum ╚Öi indicatorii de performan╚¢─â a serviciului de distribu╚¢ie.......(2)...├Än scopul ├«ndeplinirii atribu╚¢iilor prev─âzute la alin. (1), operatorul de distribu╚¢ie poate ├«ntrerupe func╚¢ionarea instala╚¢iilor pentru timpul strict necesar lucr─ârilor de ├«ntre╚¢inere ╚Öi repara╚¢ii planificate, cu anun╚¢area prealabil─â a utilizatorilor re╚¢elei electrice de distribu╚¢ie....(3)...Operatorul de distribu╚¢ie poate participa la pia╚¢a angro de energie electric─â numai pentru tranzac╚¢ii necesare acoperirii consumului propriu tehnologic aferent re╚¢elelor electrice de distribu╚¢ie ╚Öi pentru locurile de consum proprii pe baza unor proceduri transparente ╚Öi nediscriminatorii, cu respectarea mecanismelor concuren╚¢iale aprobate de autoritatea competent─â....(4)...Atribu╚¢iile privind dezvoltarea sistemului de distribu╚¢ie prev─âzute la alin. (1) lit. a)-c) nu sunt aplicabile operatorului unui sistem de distribu╚¢ie ├«nchis....(5)...Operatorul de distribu╚¢ie are obliga╚¢ia execut─ârii lucr─ârilor de realizare/retehnologizare/reabilitare/modernizare a re╚¢elelor electrice ├«n vederea cre╚Öterii nivelului de calitate al serviciului de distribu╚¢ie a energiei electrice. ├Än acest scop ╚Öi pentru adoptarea unor solu╚¢ii tehnice c├ót mai eficiente, echipamentele tehnice pot fi amplasate pe domeniul public, ├«n zone intravilane ├«n variantele constructive subteran─â sau suprateran─â, f─âr─â afectarea circula╚¢iei publice, cu respectarea reglement─ârilor tehnice aplicabile ╚Öi a condi╚¢iilor tehnice standardizate ├«n vigoare privind amplasarea re╚¢elelor edilitare....(6)...Situa╚¢iile existente la data intr─ârii ├«n vigoare a prezentei legi, contrare prevederilor alin. (5), sunt solu╚¢ionate de operatorul de distribu╚¢ie ├«n termen de 60 de zile de la data primirii unei solicit─âri ├«n acest sens de la persoanele ├«ndrept─â╚¢ite.', NULL, 45, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1421, 5, '46', 'Articolul 46', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile proprietarului re╚¢elei de distribu╚¢ie...(1)...Orice persoan─â care de╚¢ine ├«n proprietate sau ├«n folosin╚¢─â o re╚¢ea electric─â de distribu╚¢ie proprie la data intr─ârii ├«n vigoare a prezentei legi este obligat─â s─â asigure accesul la re╚¢ea, conform reglement─ârilor autorit─â╚¢ii competente....(2)...├Än cazul ├«n care de╚¢in─âtorul re╚¢elei de distribu╚¢ie a energiei electrice se afl─â ├«n imposibilitatea de a asigura serviciul universal pentru utilizatorii racorda╚¢i la acea re╚¢ea, operatorul de distribu╚¢ie concesionar, titular de licen╚¢─â ├«n zona ├«n care se afl─â re╚¢eaua, va prelua aceast─â re╚¢ea dup─â plata unei juste desp─âgubiri, cu avizul ANRE....(3)...├Än cazul ├«n care re╚¢eaua electric─â de distribu╚¢ie nu se afl─â ├«n proprietatea operatorului de distribu╚¢ie, proprietarul re╚¢elei de distribu╚¢ie are urm─âtoarele obliga╚¢ii:a)coopereaz─â cu operatorul de distribu╚¢ie ├«n vederea ├«ndeplinirii atribu╚¢iilor acestuia, furniz├óndu-i toate informa╚¢iile relevante;...b)coopereaz─â cu ANRE ╚Öi cu operatorul de distribu╚¢ie la stabilirea investi╚¢iilor ├«n re╚¢eaua de distribu╚¢ie, a modalit─â╚¢ii de finan╚¢are a acestora, precum ╚Öi a transferului serviciului de distribu╚¢ie c─âtre alt operator de distribu╚¢ie titular de licen╚¢─â;...c)de╚¢ine r─âspunderea privind activele re╚¢elei de distribu╚¢ie, cu excep╚¢ia r─âspunderii privind atribu╚¢iile operatorului de distribu╚¢ie;...d)faciliteaz─â finan╚¢area eventualelor extinderi ale re╚¢elei, cu excep╚¢ia investi╚¢iilor pentru care ╚Öi-a dat acordul s─â fie finan╚¢ate de c─âtre orice parte interesat─â, inclusiv de c─âtre operatorul de distribu╚¢ie....', NULL, 46, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1422, 5, '47', 'Articolul 47', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Limitarea sau ├«ntreruperea aliment─ârii...(1)...Operatorul de distribu╚¢ie este ├«ndrept─â╚¢it s─â ├«ntrerup─â alimentarea cu energie electric─â ├«n urm─âtoarele situa╚¢ii:a)c├ónd se pericliteaz─â via╚¢a, s─ân─âtatea oamenilor ori integritatea bunurilor materiale;...b)pentru prevenirea sau limitarea extinderii avariilor ├«n echipamentele energetice, ├«n zone de re╚¢ea electric─â sau la nivelul ├«ntregului SEN;...c)pentru executarea unor manevre sau lucr─âri ce nu se pot efectua f─âr─â ├«ntreruperi.......(2)...Operatorul de distribu╚¢ie este obligat s─â anun╚¢e utilizatorii afecta╚¢i de ├«ntreruperile planificate, respectiv pe furnizorii acestora, ├«n modul stabilit prin contracte ╚Öi s─â comunice durata planificat─â pentru ├«ntreruperile necesare execut─ârii unor lucr─âri de ├«ntre╚¢inere ╚Öi repara╚¢ii....(3)...Operatorul de distribu╚¢ie r─âspunde pentru daunele cauzate utilizatorilor din culpa sa....(4)...Prin regulamentul de furnizare se stabilesc condi╚¢iile ├«n care, ├«n situa╚¢ii excep╚¢ionale, se poate limita sau ├«ntrerupe alimentarea cu energie electric─â a clien╚¢ilor finali.', NULL, 47, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1423, 5, '48', 'Articolul 48', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Separarea activit─â╚¢ilor de distribu╚¢ie ╚Öi furnizare...(1)...├Än cazul ├«n care operatorul de distribu╚¢ie face parte dintr-un operator economic integrat pe vertical─â, acesta trebuie s─â fie independent cel pu╚¢in ├«n ceea ce prive╚Öte forma sa juridic─â, organizarea ╚Öi procesul decizional, ├«n raport cu celelalte activit─â╚¢i care nu au leg─âtur─â cu distribu╚¢ia. Aceast─â regul─â nu creeaz─â obliga╚¢ia separ─ârii propriet─â╚¢ii asupra activelor apar╚¢in├ónd operatorului de distribu╚¢ie fa╚¢─â de operatorul economic integrat pe vertical─â....(2)...Suplimentar fa╚¢─â de cerin╚¢ele prev─âzute la alin. (1), ├«n cazul ├«n care operatorul de distribu╚¢ie face parte dintr-un operator economic integrat pe vertical─â, acesta trebuie s─â fie independent ├«n ceea ce prive╚Öte organizarea sa ╚Öi procesul decizional, ├«n raport cu celelalte activit─â╚¢i care nu au leg─âtur─â cu distribu╚¢ia. ├Än acest scop, trebuie aplicate urm─âtoarele criterii minimale:a)persoanele responsabile pentru managementul operatorului de distribu╚¢ie nu pot participa ├«n structurile operatorului economic integrat pe vertical─â responsabile direct sau indirect pentru operarea zilnic─â a activit─â╚¢ilor de producere, transport sau furnizare de energie electric─â;...b)trebuie luate m─âsuri corespunz─âtoare pentru a se asigura c─â interesele profesionale ale persoanelor responsabile pentru managementul operatorului de distribu╚¢ie sunt luate ├«n considerare, ├«ntr-o manier─â de natur─â s─â asigure independen╚¢a de ac╚¢iune a acestora;...c)operatorul de distribu╚¢ie are dreptul de a lua decizii, ├«n mod efectiv, independent de operatorul economic integrat pe vertical─â, cu privire la activele necesare pentru operarea, men╚¢inerea sau dezvoltarea re╚¢elei. Se interzice societ─â╚¢ii-mam─â s─â dea orice fel de instruc╚¢iuni privind activitatea de distribu╚¢ie sau s─â ia decizii individuale ce au ├«n vedere construirea ori reabilitarea unor capacit─â╚¢i energetice de distribu╚¢ie care nu dep─â╚Öesc termenii situa╚¢iilor financiare aprobate sau ai oric─ârui instrument echivalent;...d)operatorul de distribu╚¢ie stabile╚Öte un program de conformitate, care con╚¢ine m─âsurile luate pentru a garanta excluderea practicilor discriminatorii ╚Öi stabile╚Öte ╚Öi obliga╚¢iile specifice impuse angaja╚¢ilor pentru realizarea obiectivului de independen╚¢─â;...e)operatorul de distribu╚¢ie desemneaz─â o persoan─â sau un organism, denumit─â/denumit agent de conformitate, care s─â asigure monitorizarea adecvat─â a respect─ârii programului de conformitate ╚Öi care depune la ANRE, ├«n luna decembrie a fiec─ârui an, un raport cu privire la m─âsurile luate ce se va public─â pe site-ul operatorului de distribu╚¢ie. Agentul de conformitate al operatorului de distribu╚¢ie este complet independent ╚Öi are acces la toate informa╚¢iile operatorului de distribu╚¢ie sau ale oric─ârui operator economic afiliat, care sunt necesare ├«n vederea ├«ndeplinirii atribu╚¢iilor sale.......(3)...Operatorii economici integra╚¢i pe vertical─â, care furnizeaz─â servicii pentru mai pu╚¢in de 100.000 de clien╚¢i conecta╚¢i sau care deservesc sisteme mici, izolate, nu au obliga╚¢ia de implementare a m─âsurilor prev─âzute la alin. (1) ╚Öi (2)....(4)...Operatorul de distribu╚¢ie este obligat s─â p─âstreze confiden╚¢ialitatea informa╚¢iilor comerciale ob╚¢inute ├«n cursul desf─â╚Öur─ârii activit─â╚¢ii sale....(5)...Operatorul de distribu╚¢ie este obligat s─â ├«mpiedice divulgarea discriminatorie a informa╚¢iilor privind propria activitate, care pot fi avantajoase din punct de vedere comercial....(6)...Pentru a ├«ndeplini obliga╚¢iile prev─âzute la alin. (2) lit. c), operatorul de distribu╚¢ie trebuie s─â aib─â la dispozi╚¢ie resursele necesare, care includ resurse umane, tehnice ╚Öi financiare....(7)...├Än desf─â╚Öurarea activit─â╚¢ii economice, inclusiv de comunicare ╚Öi publicitate, operatorul de distribu╚¢ie este obligat s─â nu creeze confuzie cu privire la identitatea separat─â fa╚¢─â de operatorul economic care realizeaz─â serviciul de furnizare a energiei electrice din cadrul operatorului economic integrat pe vertical─â....(8)...ANRE monitorizeaz─â activitatea operatorului de distribu╚¢ie care face parte dintr-un operator economic integrat pe vertical─â, lu├ónd m─âsuri pentru a evita sau elimina eventuale practici care denatureaz─â concuren╚¢a.', NULL, 48, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1431, 5, '56', 'Articolul 56', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Trecerea clien╚¢ilor finali la furnizorul de ultim─â instan╚¢─â...(1)...Trecerea unui client aflat ├«n una dintre situa╚¢iile prev─âzute la art. 53 alin. (2) la furnizorul de ultim─â instan╚¢─â se face conform reglement─ârilor emise de autoritatea competent─â, f─âr─â a fi necesar─â semnarea unui contract de furnizare....(2)...├Än cazul schimb─ârii furnizorului ca urmare a trecerii la furnizorul de ultim─â instan╚¢─â ├«n condi╚¢iile art. 53 alin. (2), operatorul re╚¢elei la care este racordat locul de consum va asigura continuitatea ├«n alimentarea cu energie electric─â a acestuia, iar energia electric─â ╚Öi serviciile aferente furniz─ârii ├«n aceste condi╚¢ii vor fi suportate prin intermediul furnizorului de ultim─â instan╚¢─â....(3)...Furnizarea de energie electric─â de c─âtre furnizorul de ultim─â instan╚¢─â se face la tarife stabilite/pre╚¢uri avizate ├«n condi╚¢iile reglement─ârilor emise de autoritatea competent─â.', NULL, 56, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1424, 5, '49', 'Articolul 49', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Interdic╚¢ii...Pentru protejarea re╚¢elelor electrice de distribu╚¢ie, se interzice persoanelor fizice ╚Öi juridice:a)s─â efectueze construc╚¢ii de orice fel ├«n zona de siguran╚¢─â a re╚¢elelor electrice de distribu╚¢ie, f─âr─â avizul de amplasament al operatorului de distribu╚¢ie;...b)s─â efectueze s─âp─âturi de orice fel sau s─â ├«nfiin╚¢eze planta╚¢ii ├«n zona de siguran╚¢─â a re╚¢elelor electrice de distribu╚¢ie, f─âr─â acordul operatorului de distribu╚¢ie;...c)s─â depoziteze materiale pe culoarul de trecere ╚Öi ├«n zonele de protec╚¢ie ╚Öi de siguran╚¢─â ale instala╚¢iilor, f─âr─â acordul operatorului de distribu╚¢ie;...d)s─â arunce obiecte de orice fel pe re╚¢elele electrice de distribu╚¢ie sau s─â intervin─â ├«n oricare alt mod asupra acestora;...e)s─â deterioreze construc╚¢iile, ├«ngr─âdirile sau inscrip╚¢iile de identificare ╚Öi avertizare aferente re╚¢elelor electrice de distribu╚¢ie;...f)s─â limiteze sau s─â ├«ngr─âdeasc─â, prin execu╚¢ia de ├«mprejmuire, prin construc╚¢ii ori prin orice alt mod, accesul la instala╚¢ii al operatorului de distribu╚¢ie....', NULL, 49, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1425, 5, '50', 'Articolul 50', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Sistemul de distribu╚¢ie ├«nchis...(1)...Sistemul de distribu╚¢ie ├«nchis este acel sistem prin care se distribuie energie electric─â ├«ntr-o zon─â industrial─â, comercial─â sau de servicii comune, limitat─â din punct de vedere geografic ╚Öi care, f─âr─â a aduce atingere dispozi╚¢iilor alin. (4), nu alimenteaz─â clien╚¢ii casnici, dac─â:a)din motive tehnice sau de securitate specifice, activit─â╚¢ile ori procesul de produc╚¢ie desf─â╚Öurate de utilizatorii sistemului respectiv sunt integrate; sau...b)sistemul respectiv distribuie energie electric─â, ├«n principal proprietarului sistemului de distribu╚¢ie, operatorului acestuia ori operatorilor economici afilia╚¢i acestora.......(2)...Operatorului unui sistem de distribu╚¢ie ├«nchis nu i se aplic─â:a)obliga╚¢ia de a cump─âra energia electric─â necesar─â acoperirii consumului propriu tehnologic pe baza unor proceduri transparente, nediscriminatorii, ├«n condi╚¢iile art. 36 alin. (4);...b)obliga╚¢ia ca tarifele de racordare la re╚¢ea sau metodologiile care stau la baza calcul─ârii acestora s─â fie aprobate ├«nainte de intrarea ├«n vigoare a acestora.......(3)...Tarifele pentru serviciul de distribu╚¢ie dintr-un sistem de distribu╚¢ie ├«nchis sau metodologiile care stau la baza calcul─ârii acestora pot fi ╚Öi aprobate ╚Öi revizuite de ANRE, la cererea unui utilizator al sistemului de distribu╚¢ie ├«nchis....(4)...Dispozi╚¢iile alin. (2) se aplic─â ╚Öi ├«n cazul ├«n care ├«n zona deservit─â de un sistem de distribu╚¢ie ├«nchis sunt amplasa╚¢i clien╚¢i casnici, numai dac─â consumul anual al acestora este mai mic de 5% din consumul anual al sistemului ├«nchis ╚Öi dac─â ace╚Ötia se afl─â ├«ntr-un raport de munc─â sau ├«ntr-o form─â de asociere cu proprietarul sistemului de distribu╚¢ie.', NULL, 50, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1426, 5, '51', 'Articolul 51', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Electrificarea localit─â╚¢ilor ╚Öi extinderea re╚¢elelor de distribu╚¢ie a energiei electrice...(1)...├Än executarea contractului de concesiune, la solicitarea autorit─â╚¢ilor administra╚¢iei publice locale sau centrale, ├«n baza planurilor de dezvoltare regional─â ╚Öi de urbanism, operatorul de distribu╚¢ie este obligat s─â asigure dezvoltarea ╚Öi finan╚¢area re╚¢elei de distribu╚¢ie pentru electrificarea localit─â╚¢ilor ori pentru extinderea re╚¢elelor de distribu╚¢ie ├«n zona acoperit─â de contractul de concesiune, respectiv de licen╚¢─â, pe care acesta o de╚¢ine....(2)...├Än cazul ├«n care realizarea investi╚¢iilor de la alin. (1) nu este justificat─â economic pentru operatorul de distribu╚¢ie, acesta poate propune, dup─â informarea ANRE, realizarea obiectivelor respective prin coparticiparea solicitantului la finan╚¢are cu fonduri din bugetele locale ╚Öi din bugetul de stat, ├«n condi╚¢iile legii....(3)...Evaluarea condi╚¢iilor de finan╚¢are a investi╚¢iilor prev─âzute la alin. (1) ╚Öi (2) se determin─â de operatorul de distribu╚¢ie, ├«n baza unui studiu de fezabilitate realizat ├«n termen de maximum 60 de zile de la primirea solicit─ârii, conform unei metodologii aprobate de ANRE....(4)...Pentru dezvoltarea programelor de electrificare a localit─â╚¢ilor ╚Öi de extindere a re╚¢elelor de distribu╚¢ie a energiei electrice, autorit─â╚¢ile administra╚¢iei publice locale ╚Öi ministerele implicate vor r─âspunde, ├«n termen de 60 de zile de la primirea solicit─ârilor operatorului de transport ╚Öi sistem, precum ╚Öi ale operatorilor de distribu╚¢ie, pentru realizarea planurilor de dezvoltare a re╚¢elei pe termen mediu ╚Öi lung....(5)...Localit─â╚¢ile care, din considerente tehnice sau economice, nu sunt conectate la SEN pot fi alimentate cu energie electric─â prin sisteme electroenergetice izolate....(6)...ANRE va stabili pre╚¢ul local ╚Öi condi╚¢iile minime privind continuitatea ╚Öi calitatea serviciului de furnizare.', NULL, 51, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1427, 5, '52', 'Articolul 52', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Furnizarea energiei electrice...(1)...Furnizarea energiei electrice este activitatea prin care persoana juridic─â titular─â de licen╚¢─â comercializeaz─â energie electric─â clien╚¢ilor. Pentru furnizarea energiei electrice la clien╚¢i se ├«ncheie un contract, ├«n conformitate cu prevederile legale ├«n vigoare....(2)...Energia electric─â furnizat─â clientului se factureaz─â de c─âtre furnizor, conform reglement─ârilor ANRE ╚Öi prevederilor legale ├«n vigoare....(3)...Sunt interzise consumul ╚Öi furnizarea de energie electric─â f─âr─â ├«ncheierea contractului de furnizare, cu excep╚¢ia situa╚¢iilor reglementate ├«n mod distinct de c─âtre autoritatea competent─â.', NULL, 52, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1428, 5, '53', 'Articolul 53', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile furnizorului de ultim─â instan╚¢─â...(1)...Furnizorul de ultim─â instan╚¢─â are obliga╚¢ia de a asigura serviciul universal clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) lit. b)....(2)...Furnizorul de ultim─â instan╚¢─â are obliga╚¢ia de a furniza energie electric─â clien╚¢ilor finali al c─âror furnizor se afl─â ├«n situa╚¢ia de a i se retrage licen╚¢a de furnizare ├«n cursul desf─â╚Öur─ârii activit─â╚¢ii sau ├«n orice alt─â situa╚¢ie identificat─â de autoritatea competent─â ├«n care clien╚¢ii finali nu au asigurat─â furnizarea de energie electric─â din nicio alt─â surs─â, cu excep╚¢ia clien╚¢ilor deconecta╚¢i pentru sustragere de energie electric─â ori neplat─â, conform reglement─ârilor emise de autoritatea competent─â.', NULL, 53, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1429, 5, '54', 'Articolul 54', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Desemnarea furnizorilor de ultim─â instan╚¢─â...Furnizorii de ultim─â instan╚¢─â se desemneaz─â de c─âtre ANRE dintre furnizorii existen╚¢i pe pia╚¢a de energie, prin mecanisme concuren╚¢iale, pe baza unui regulament care stabile╚Öte modalit─â╚¢ile ╚Öi criteriile de selec╚¢ie a acestora, pentru fiecare categorie de clien╚¢i finali pe care ace╚Ötia ├«i deservesc.', NULL, 54, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1430, 5, '55', 'Articolul 55', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Clien╚¢ii furnizorilor de ultim─â instan╚¢─â...(1)...Furnizorii de ultim─â instan╚¢─â sunt obliga╚¢i s─â asigure furnizarea de energie electric─â, ├«n condi╚¢ii de calitate ╚Öi la pre╚¢uri rezonabile, transparente, u╚Öor comparabile ╚Öi nediscriminatorii, conform reglement─ârilor ANRE, cu respectarea prevederilor art. 22 alin. (1), urm─âtoarelor categorii de clien╚¢i:a)clien╚¢ii finali care, la data intr─ârii ├«n vigoare a prezentei legi, nu ╚Öi-au exercitat dreptul de eligibilitate;...b)clien╚¢ii casnici ╚Öi clien╚¢ii noncasnici cu un num─âr mediu scriptic de salaria╚¢i mai mic de 50 ╚Öi o cifr─â de afaceri anual─â sau o valoare total─â a activelor din bilan╚¢ul contabil, conform raport─ârilor fiscale anuale, care nu dep─â╚Öe╚Öte 10 milioane euro.......(2)...Identificarea clien╚¢ilor finali noncasnici care ├«ndeplinesc condi╚¢iile men╚¢ionate la alin. (1) lit. b) se va realiza de c─âtre furnizorii de ultim─â instan╚¢─â, ├«n baza documentelor prev─âzute de legisla╚¢ia specific─â, transmise de clien╚¢i furnizorilor de ultim─â instan╚¢─â. Termenul pentru finalizarea identific─ârii acestora este de maximum 12 luni de la data intr─ârii ├«n vigoare a prezentei legi....(3)...Dup─â finalizarea procesului de identificare, clien╚¢ii finali noncasnici prev─âzu╚¢i la alin. (2) au obliga╚¢ia de a transmite, o dat─â la 2 ani, documentele men╚¢ionate la alin. (2) furnizorului de ultim─â instan╚¢─â.', NULL, 55, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1432, 5, '57', 'Articolul 57', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile furnizorului...(1)...Furnizorul trebuie s─â pun─â la dispozi╚¢ia clien╚¢ilor casnici puncte unice de contact, conform reglement─ârilor ANRE, pentru informarea acestora cu privire la drepturile acestora, la legisla╚¢ia ├«n vigoare ╚Öi la c─âile de solu╚¢ionare a litigiilor ├«n cazul unor dezacorduri sau pl├óngeri....(2)...Furnizorul trebuie s─â pun─â la dispozi╚¢ia clien╚¢ilor casnici mai multe modalit─â╚¢i de plat─â a energiei furnizate ╚Öi s─â permit─â acestora s─â opteze pentru oricare dintre acestea....(3)...Furnizorul este obligat s─â asigure etichetarea energiei electrice conform reglement─ârilor autorit─â╚¢ii competente ╚Öi s─â informeze, periodic, clien╚¢ii finali privind structura, provenien╚¢a, caracteristicile ╚Öi impactul asupra mediului pentru energia electric─â furnizat─â acestora....(4)...Furnizorul este obligat s─â participe la activit─â╚¢i prin care se asigur─â siguran╚¢a ╚Öi securitatea SEN.', NULL, 57, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1433, 5, '58', 'Articolul 58', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢ia de contractare...(1)...La cererea scris─â a unui client final, furnizorul este obligat s─â comunice, ├«n scris, ├«ntr-un interval de cel mult 15 zile lucr─âtoare, o ofert─â privind condi╚¢iile de furnizare a energiei electrice, care con╚¢ine, obligatoriu, pre╚¢ul de furnizare, termenii de plat─â ╚Öi data-limit─â de ├«ncheiere a contractului de furnizare, elaborat ├«n conformitate cu reglement─ârile emise de autoritatea competent─â....(2)...Contractul de furnizare ├«ncheiat ├«n baza prevederilor alin. (1) este valabil o perioad─â nedeterminat─â sau pe o perioad─â convenit─â de p─âr╚¢i....(3)...Clientul final are dreptul de a denun╚¢a unilateral contractul de furnizare, cu notificare transmis─â furnizorului de energie electric─â cu cel pu╚¢in 21 de zile ├«nainte, cu respectarea condi╚¢iilor contractuale....(4)...Furnizorul este ├«ndrept─â╚¢it s─â rezilieze contractul ├«n urm─âtoarele cazuri:a)sustragerea de energie electric─â constatat─â prin hot─âr├óre judec─âtoreasc─â definitiv─â;...b)neplata facturilor;...c)alte situa╚¢ii prev─âzute de legisla╚¢ia ├«n vigoare.......(5)...Furnizorul este ├«ndrept─â╚¢it s─â solicite unui client final constituirea de garan╚¢ii financiare pentru o perioad─â de consum echivalent de maximum un an ├«n cazul constat─ârii, conform prevederilor legale ├«n vigoare, a unor ac╚¢iuni menite s─â denatureze ├«n orice fel indica╚¢iile echipamentelor de m─âsurare sau s─â sustrag─â energia electric─â prin ocolirea echipamentelor de m─âsurare din partea acestuia. Modalitatea de constituire a garan╚¢iilor financiare este prev─âzut─â ├«n contractele-cadru de furnizare a energiei electrice ╚Öi/sau ├«n alte reglement─âri emise de ANRE....(6)...Refuzul constituirii garan╚¢iilor prev─âzute la alin. (5) d─â dreptul furnizorului s─â solicite operatorului de distribu╚¢ie deconectarea clientului final de la re╚¢ea.', NULL, 58, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1434, 5, '59', 'Articolul 59', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Plata energiei furnizate├Än conformitate cu prevederile contractului, clientul final este obligat s─â pl─âteasc─â contravaloarea energiei electrice consumate la pre╚¢ul ╚Öi ├«n condi╚¢iile prev─âzute ├«n contractul de furnizare a energiei electrice....', NULL, 59, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1435, 5, '60', 'Articolul 60', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'R─âspunderea furnizorului...(1)...Furnizorul r─âspunde pentru toate daunele provocate clientului final din culpa sa, ├«n condi╚¢iile stabilite prin contractul de furnizare....(2)...Furnizorul este ├«ndrept─â╚¢it s─â recupereze daunele produse clien╚¢ilor finali ╚Öi, respectiv, furnizorului ├«nsu╚Öi de la operatorul de distribu╚¢ie, operatorul de transport ╚Öi de sistem sau de la produc─âtor, dovedite a fi cauzate din culpa oric─âruia dintre ace╚Ötia.', NULL, 60, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1436, 5, '61', 'Articolul 61', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile clientului final de energie electric─â...(1)...Clientul final de energie electric─â r─âspunde pentru daunele dovedite cauzate furnizorului/operatorului de re╚¢ea din culpa sa....(2)...Nerespectarea contractului de furnizare a energiei electrice de c─âtre clientul final poate atrage suportarea urm─âtoarelor consecin╚¢e, dup─â caz:a)penaliz─âri;...b)sistarea temporar─â a furniz─ârii energiei electrice;...c)rezilierea contractului de furnizare a energiei electrice.......(3)...├Än cazul interven╚¢iilor asupra instala╚¢iilor electrice de c─âtre persoane neautorizate, operatorul de re╚¢ea este ├«ndrept─â╚¢it s─â ├«ntrerup─â alimentarea cu energie electric─â, ├«n conformitate cu reglement─ârile specifice ale ANRE....(4)...├Än cazul constat─ârii, conform prevederilor legale ├«n vigoare, a unor ac╚¢iuni menite s─â denatureze ├«n orice fel indica╚¢iile echipamentelor de m─âsurare sau s─â sustrag─â energia electric─â prin ocolirea echipamentelor de m─âsurare, clientul final este obligat s─â constituie o garan╚¢ie financiar─â, conform prevederilor art. 58 alin. (5)....(5)...Clientul final de energie electric─â cu o putere aprobat─â prin avizul tehnic de racordare de cel pu╚¢in 1.000 kVA este obligat s─â prezinte prognoze orare de consum furnizorului cu care are rela╚¢ii contractuale, conform reglement─ârilor emise de autoritatea competent─â....(6)...Clien╚¢ii finali au obliga╚¢ia pl─â╚¢ii contravalorii facturilor pentru energia electric─â ╚Öi pentru serviciile de care beneficiaz─â la termenele scadente prev─âzute ├«n contractele ├«ncheiate ├«ntre p─âr╚¢i....(7)...Pentru garantarea pl─â╚¢ilor facturilor la energia electric─â, furnizorii de ultim─â instan╚¢─â pot solicita clien╚¢ilor constituirea de garan╚¢ii financiare, numai ├«n condi╚¢iile stabilite de reglement─ârile emise de autoritatea competent─â.', NULL, 61, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1437, 5, '62', 'Articolul 62', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Drepturile clientului final de energie electric─â...(1)...Clien╚¢ii finali de energie electric─â au urm─âtoarele drepturi:a)s─â aib─â acces la re╚¢elele electrice de interes public ╚Öi s─â consume energie electric─â ├«n conformitate cu prevederile contractului de furnizare;...b)s─â solicite furnizorului modificarea ╚Öi completarea contractului de furnizare ╚Öi a anexelor la acesta sau s─â ini╚¢ieze acte adi╚¢ionale la contracte, atunci c├ónd apar elemente noi ori c├ónd consider─â necesar─â detalierea sau completarea unor clauze contractuale;...c)s─â aib─â acces pentru citire la grupurile de m─âsurare pentru decontare;...d)s─â solicite furnizorului sau operatorului de distribu╚¢ie s─â ia m─âsuri pentru remedierea defec╚¢iunilor ╚Öi a deranjamentelor survenite ├«n re╚¢elele electrice;...e)s─â primeasc─â desp─âgubiri de la furnizor, operator de re╚¢ea sau alt client final, conform prevederilor contractuale, c├ónd i se produc daune din vina acestora ori ├«n cazul nerespect─ârii indicatorilor de calitate a energiei;...f)s─â se asocieze voluntar ├«n vederea reprezent─ârii intereselor clien╚¢ilor, conform reglement─ârilor ANRE;...g)s─â achizi╚¢ioneze energia electric─â pe baz─â de contract bilateral de la un furnizor de energie, indiferent de statul membru ├«n care este ├«nregistrat furnizorul, cu condi╚¢ia s─â respecte normele aplicabile de comercializare ╚Öi de echilibrare stabilite pe teritoriul Rom├óniei;...h)s─â schimbe furnizorul, conform unei proceduri aprobate de ANRE;...i)s─â ├«ncheie contracte cu mai mul╚¢i furnizori de energie ├«n acela╚Öi timp, pentru un singur loc de consum, drept ce le revine exclusiv clien╚¢ilor noncasnici de mari dimensiuni, a c─âror putere maxim─â aprobat─â prin avizul tehnic de racordare este stabilit─â prin reglement─ârile ANRE;...j)s─â supun─â solu╚¢ion─ârii autorit─â╚¢ii competente divergen╚¢ele rezultate din derularea contractelor ├«ncheiate ├«n sectorul energiei electrice, ├«n baza unor proceduri elaborate de autoritatea competent─â;...k)s─â beneficieze de serviciile unui mediator, ├«n condi╚¢iile Legii nr. 192/2006 privind medierea ╚Öi organizarea profesiei de mediator, cu modific─ârile ╚Öi complet─ârile ulterioare, ├«n vederea solu╚¢ion─ârii extrajudiciare a litigiilor ap─ârute pe pia╚¢a de energie electric─â;...l)s─â denun╚¢e unilateral contractul de furnizare, ├«n condi╚¢iile art. 58 alin. (3).......(2)...Procedura prev─âzut─â la alin. (1) lit. h) stabile╚Öte ├«n principal etapele procesului de schimbare a furnizorului, modalitatea de stingere a obliga╚¢iilor de plat─â datorate de clientul final furnizorului ce urmeaz─â a fi schimbat, datele ce pot fi solicitate de clientul final sau de noul furnizor ├«n procesul de schimbare, precum ╚Öi operatorii economici care sunt obliga╚¢i s─â le furnizeze....(3)...Procedura prev─âzut─â la alin. (1) lit. h) trebuie s─â garanteze unui client care dore╚Öte s─â schimbe furnizorul, cu respectarea condi╚¢iilor contractuale, c─â aceast─â schimbare se poate realiza ├«n termen de 3 s─âpt─âm├óni de la ini╚¢ierea acesteia.', NULL, 62, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1440, 5, '65', 'Articolul 65', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'M─âsurarea energiei electrice...(1)...Energia electric─â furnizat─â clien╚¢ilor finali pe pia╚¢a de energie electric─â se m─âsoar─â de c─âtre operatorii de m─âsurare, prin grupuri de m─âsurare, conform codului de m─âsurare elaborat de autoritatea competent─â....(2)...Transformatoarele de m─âsur─â aferente grupurilor de m─âsurare a energiei electrice ├«n vederea decont─ârii se asigur─â, dup─â caz, de:a)operatorul de transport ╚Öi de sistem;...b)produc─âtori;...c)operatorii de distribu╚¢ie;...d)clien╚¢ii finali de energie electric─â.......(3)...Operatorii de m─âsurare pot fi:a)operatorul de transport ╚Öi de sistem;...b)produc─âtorii;...c)operatorul de distribu╚¢ie.......(4)...Operatorul de transport ╚Öi de sistem, respectiv operatorul de distribu╚¢ie au obliga╚¢ia de a presta serviciul de m─âsurare a energiei electrice pentru utilizatorii re╚¢elelor electrice respective....(5)...Operatorul de transport ╚Öi de sistem, respectiv operatorul de distribu╚¢ie pot cesiona serviciul de m─âsurare ╚Öi gestiunea datelor m─âsurate unui operator economic, cu condi╚¢ia ├«ncadr─ârii ├«n costurile recunoscute de autoritatea competent─â ╚Öi cu respectarea reglement─ârilor aplicabile emise de aceasta.', NULL, 65, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1441, 5, '66', 'Articolul 66', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Sisteme de m─âsurare inteligent─â...(1)...P├ón─â la data de 3 septembrie 2012, ANRE va evalua implementarea sistemelor de m─âsurare inteligente din punctul de vedere al costurilor ╚Öi beneficiilor pe termen lung pentru pia╚¢─â, al rentabilit─â╚¢ii, precum ╚Öi al termenelor fezabile de implementare....(2)...├Än situa╚¢ia ├«n care prin evaluarea prev─âzut─â la alin. (1) se constat─â c─â implementarea sistemelor de m─âsurare inteligente este avantajoas─â pentru func╚¢ionarea pie╚¢ei de energie, ANRE aprob─â un calendar de implementare a sistemelor de m─âsurare inteligente, astfel ├«nc├ót circa 80% dintre clien╚¢i s─â dispun─â de sisteme de m─âsurare inteligente p├ón─â ├«n 2020. Implementarea acestor sisteme se va aproba ├«n cadrul planurilor anuale de investi╚¢ii ale operatorilor de distribu╚¢ie.', NULL, 66, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1442, 5, '67', 'Articolul 67', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Definirea surselor regenerabile de energie...├Än condi╚¢iile prezentului titlu, se definesc ca surse regenerabile de energie:a)energia eolian─â;...b)energia solar─â;...c)energia valurilor ╚Öi a mareelor;...d)energia geotermal─â;...e)energia hidroelectric─â;...f)energia con╚¢inut─â ├«n frac╚¢iunea biodegradabil─â a produselor, de╚Öeurilor ╚Öi reziduurilor din agricultur─â (inclusiv substan╚¢e vegetale ╚Öi reziduuri de origine animal─â), silvicultur─â ╚Öi industrii conexe, precum ╚Öi frac╚¢iunea biodegradabil─â a de╚Öeurilor industriale ╚Öi comunale, or─â╚Öene╚Öti ╚Öi municipale, denumit─â biomas─â;...g)energia con╚¢inut─â ├«n gazul de fermentare a de╚Öeurilor, denumit ╚Öi gaz de depozit;...h)energia con╚¢inut─â ├«n gazul de fermentare a n─âmolurilor din instala╚¢iile de epurare a apelor uzate;...i)energia con╚¢inut─â ├«n produse secundare gazoase, ob╚¢inute prin fermentare din materii reziduale organice, form├ónd categoria de combustibil gazos, denumit─â biogaz;...j)energia con╚¢inut─â ├«n produse lichide ob╚¢inute prin distilarea materiei organice fermentate, form├ónd categoria de combustibil lichid, denumit─â alcool carburant;...k)energia ob╚¢inut─â din alte surse regenerabile, neexploatate ├«n prezent....', NULL, 67, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1443, 5, '68', 'Articolul 68', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Tehnologii de cogenerare...├Än condi╚¢iile prezentului titlu, se definesc urm─âtoarele tehnologii de cogenerare cu:a)turbine cu gaze ├«n ciclu combinat cu recuperare de c─âldur─â ╚Öi turbine cu abur;...b)turbina cu abur cu contrapresiune;...c)turbina cu abur cu condensa╚¢ie ╚Öi prize reglabile;...d)turbine cu gaze ╚Öi cazane recuperatoare;...e)motoare cu ardere intern─â;...f)microturbine;...g)motoare Stirling;...h)celule de combustie;...i)motoare termice;...j)cicluri organice Rankine;...k)orice alt─â instala╚¢ie sau combina╚¢ie de instala╚¢ii prin care se produce simultan energie termic─â ╚Öi electric─â....', NULL, 68, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1444, 5, '69', 'Articolul 69', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Cogenerarea de ├«nalt─â eficien╚¢─â...├Än scopul promov─ârii producerii energiei electrice ├«n cogenerare se define╚Öte cogenerarea de ├«nalt─â eficien╚¢─â ca fiind procesul de producere combinat─â a energiei electrice ╚Öi termice, care ├«ndepline╚Öte urm─âtoarele criterii:a)├«n cazul unit─â╚¢ilor de cogenerare cu puteri electrice instalate de peste 25 MW:......(i) realizarea unui randament global anual de minimum 70%; ╚Öi...(├«i) realizarea unei economii de energie primar─â de cel pu╚¢in 10% fa╚¢─â de valorile de referin╚¢─â ale producerii separate ale acelora╚Öi cantit─â╚¢i de energie electric─â ╚Öi termic─â;b)├«n cazul unit─â╚¢ilor de cogenerare cu puteri electrice instalate ├«ntre 1 MW ╚Öi 25 MW, realizarea de economie de energie primar─â de cel pu╚¢in 10% fa╚¢─â de valorile de referin╚¢─â ale producerii separate a acelora╚Öi cantit─â╚¢i de energie electric─â ╚Öi termic─â;...c)├«n cazul unit─â╚¢ilor de cogenerare cu puteri instalate sub 1 MW, realizarea de economie de energie primar─â fa╚¢─â de producerea separat─â a acelora╚Öi cantit─â╚¢i de energie electric─â ╚Öi termic─â....', NULL, 69, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1445, 5, '70', 'Articolul 70', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Reguli de acces la re╚¢ea pentru energia electric─â produs─â din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â...Autoritatea competent─â stabile╚Öte prin reguli tehnice ╚Öi comerciale:a)accesul garantat la re╚¢elele electrice ╚Öi dispecerizarea prioritar─â a energiei electrice produse din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â;...b)accesul prioritar la re╚¢elele electrice ╚Öi dispecerizarea prioritar─â a energiei electrice produse din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â ├«n centrale cu puteri instalate mai mici sau egale cu 1 MW, ├«n m─âsura ├«n care nu este afectat nivelul de siguran╚¢─â a SEN....', NULL, 70, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1455, 5, '80', 'Articolul 80', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Limitarea pre╚¢urilor/tarifelor...(1)...├Än situa╚¢ii de dezechilibre majore ├«ntre cerere ╚Öi ofert─â ╚Öi disfunc╚¢ionalit─â╚¢i evidente ale pie╚¢ei de energie electric─â, Guvernul, la propunerea ANRE ╚Öi cu avizul Consiliului Concuren╚¢ei, poate limita cre╚Öterea excesiv─â a pre╚¢urilor/tarifelor sau blocarea acestora pentru o perioad─â determinat─â de maximum 6 luni, perioad─â ce poate fi prelungit─â succesiv pentru durate de c├óte cel mult 3 luni, c├ót timp persist─â circumstan╚¢ele ce au determinat adoptarea respectivei decizii, prin:a)fixarea unei limite superioare a pre╚¢ului pe o pia╚¢─â centralizat─â;...b)limitarea venitului din activitatea reglementat─â.......(2)...Costurile recunoscute ╚Öi am├ónate, potrivit prevederilor alin. (1) lit. b), se recupereaz─â integral, ├«n conformitate cu procedura emis─â de autoritatea competent─â.', NULL, 80, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1446, 5, '71', 'Articolul 71', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Criterii de promovare a energiei electrice produse din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â...(1)...Criteriile de promovare a energiei electrice produse din surse regenerabile de energie au ├«n vedere urm─âtoarele:a)atingerea ╚¢intei na╚¢ionale privind ponderea energiei electrice produse din surse regenerabile de energie;...b)compatibilitatea cu principiile concuren╚¢iale de pia╚¢─â;...c)caracteristicile diferitelor surse regenerabile de energie ╚Öi tehnologiile de producere a energiei electrice;...d)promovarea utiliz─ârii surselor regenerabile de energie ├«n cel mai eficient mod.......(2)...Criteriile de promovare a energiei electrice produse ├«n cogenerarea de ├«nalt─â eficien╚¢─â au ├«n vedere urm─âtoarele:a)asigurarea accesului la schemele de sprijin, ├«n condi╚¢iile acoperirii costurilor justificate aferente cogener─ârii de ├«nalt─â eficien╚¢─â;...b)caracteristicile diferitelor tehnologii de producere a energiei electrice ├«n cogenerare;...c)promovarea utiliz─ârii eficiente a combustibililor;...d)asigurarea protec╚¢iei mediului, prin reducerea emisiilor poluante fa╚¢─â de producerea separat─â a energiei electrice ╚Öi termice....', NULL, 71, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1447, 5, '72', 'Articolul 72', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Scheme de sprijin pentru promovarea producerii energiei electrice din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â...(1)...Pentru promovarea producerii energiei electrice din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se aplic─â scheme de sprijin, ├«n conformitate cu prevederile legisla╚¢iei europene....(2)...Pentru accesul la schemele de sprijin pentru promovarea energiei electrice produse din surse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se aplic─â regulile de acreditare ╚Öi calificare stabilite de autoritatea competent─â.', NULL, 72, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1448, 5, '73', 'Articolul 73', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Garan╚¢iile de origine...Certificarea energiei electrice produse din surse regenerabile ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se atest─â prin intermediul garan╚¢iilor de origine emise de autoritatea competent─â.', NULL, 73, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1449, 5, '74', 'Articolul 74', NULL, 'Energia electric─â', NULL, 'Promovarea energiei electrice produse din resurse regenerabile de energie ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â', NULL, NULL, NULL, NULL, 'Aplicarea schemelor de sprijin...Aplicarea schemelor de sprijin pentru promovarea producerii energiei electrice din surse regenerabile de energie electric─â ╚Öi ├«n cogenerare de ├«nalt─â eficien╚¢─â se face dup─â autorizarea acestora de c─âtre Comisia European─â, ├«n condi╚¢iile legii.', NULL, 74, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1450, 5, '75', 'Articolul 75', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Fundamentarea acestora...(1)...Pentru activit─â╚¢ile desf─â╚Öurate ├«n sectorul energiei electrice se aplic─â urm─âtoarele pre╚¢uri ╚Öi tarife:a)pre╚¢uri rezultate din mecanismele concuren╚¢iale ale pie╚¢ei de energie electric─â;...b)tarife reglementate pentru serviciile de transport, de sistem ╚Öi de distribu╚¢ie a energiei electrice;...c)tarife/pre╚¢uri reglementate pentru furnizarea de energie electric─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1), cu respectarea prevederilor art. 22, p├ón─â la eliminarea acestora;...d)pre╚¢uri/tarife practicate de furnizorii de ultim─â instan╚¢─â clien╚¢ilor afla╚¢i ├«n situa╚¢ia prev─âzut─â la art. 53 alin. (2);...e)tarife reglementate pentru achizi╚¢ia serviciilor tehnologice de sistem, p├ón─â la crearea unei pie╚¢e concuren╚¢iale de servicii tehnologice de sistem;...f)tarife reglementate de racordare la re╚¢ea;...g)tarif reglementat practicat de operatorul pie╚¢ei de energie electric─â;...h)pre╚¢uri reglementate practicate de produc─âtorii/furnizorii de energie electric─â clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1), cu respectarea prevederilor art. 22;...i)pre╚¢uri reglementate pentru energia termic─â destinat─â consumului popula╚¢iei, produs─â ├«n instala╚¢iile de cogenerare;...j)pre╚¢uri rezultate din formule de stabilire/ajustare prev─âzute ├«n contractele de v├ónzare-cump─ârare a energiei termice ori din contractele de concesiune sau de asociere ori de parteneriat.......(2)...ANRE elaboreaz─â ╚Öi aprob─â regulile privind mecanismul de corelare a pre╚¢ului orar de ofert─â pe pia╚¢a de echilibrare cu pre╚¢ul de ├«nchidere a pie╚¢ei PZU stabilit pentru ora respectiv─â.', NULL, 75, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1451, 5, '76', 'Articolul 76', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Metodologiile de reglementare a pre╚¢urilor ╚Öi tarifelor...(1)...Pre╚¢urile ╚Öi tarifele reglementate pentru activit─â╚¢ile de producere, transport, distribu╚¢ie ╚Öi furnizare a energiei electrice ╚Öi a energiei termice produse ├«n centrale de cogenerare, precum ╚Öi pentru activitatea prestat─â de operatorul pie╚¢ei de energie electric─â se stabilesc pe baza metodologiilor aprobate ╚Öi publicate de autoritatea competent─â. ├Än calculul acestora vor fi luate ├«n considerare costurile justificate ale activit─â╚¢ilor respective, cheltuielile pentru dezvoltare ╚Öi protec╚¢ia mediului, precum ╚Öi o cot─â rezonabil─â de profit....(2)...Metodele de formare a pre╚¢urilor reglementate pentru energia termic─â ╚Öi a m─âsurilor de sprijin pentru energia electric─â produs─â ├«n centrale de cogenerare de ├«nalt─â eficien╚¢─â care beneficiaz─â de scheme de sprijin instituite la nivel na╚¢ional se stabilesc pe baz─â de hot─âr├óre a Guvernului....(3)...Metodologiile de reglementare a pre╚¢urilor ╚Öi tarifelor se aprob─â de autoritatea competent─â, dup─â parcurgerea procedurii de consultare public─â.', NULL, 76, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1452, 5, '77', 'Articolul 77', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Eviden╚¢ierea costurilor ╚Öi a veniturilor...Operatorii economici titulari de licen╚¢─â din sectorul energiei electrice, care practic─â pre╚¢uri ╚Öi tarife reglementate, sunt obliga╚¢i s─â prezinte autorit─â╚¢ii competente:a)costurile ╚Öi veniturile realizate separat din activit─â╚¢ile care fac obiectul licen╚¢elor acordate ├«n structura stabilit─â de autoritatea competent─â;...b)modul de alocare a activelor, pasivelor, cheltuielilor ╚Öi veniturilor, conform reglement─ârilor emise de autoritatea competent─â....', NULL, 77, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1453, 5, '78', 'Articolul 78', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Stabilirea costurilor ╚Öi veniturilor reglementate...(1)...├Än scopul controlului tarifelor la clien╚¢ii prev─âzu╚¢i la art. 55 alin. (1), autoritatea competent─â aprob─â metodologia de stabilire a costurilor/veniturilor reglementate pentru cump─ârarea/v├ónzarea energiei electrice destinate acestor clien╚¢i....(2)...Dep─â╚Öirea costurilor reglementate pentru cump─ârarea/v├ónzarea energiei electrice destinate clien╚¢ilor prev─âzu╚¢i la art. 55 alin. (1) ╚Öi a costurilor reglementate pentru serviciile de transport ╚Öi distribu╚¢ie se poate face, cu aprobarea autorit─â╚¢ii competente, doar ├«n situa╚¢ii excep╚¢ionale, de for╚¢─â major─â sau determinat─â de schimb─âri legislative.', NULL, 78, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1454, 5, '79', 'Articolul 79', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Principiile pentru fundamentarea propunerilor de pre╚¢uri ╚Öi tarife reglementate...(1)...Propunerile de pre╚¢uri ╚Öi tarife reglementate se elaboreaz─â de operatorii economici din sectorul energiei electrice, cu respectarea metodologiilor emise de autoritatea competent─â, ╚Öi se comunic─â autorit─â╚¢ii, ├«mpreun─â cu fundamentarea ├«n formatul stabilit de aceasta....(2)...Operatorii economici din sectorul energiei electrice transmit autorit─â╚¢ii competente toate datele necesare folosite ├«n procesul de fundamentare a solicit─ârii ╚Öi vor asigura accesul la aceste date al reprezentan╚¢ilor desemna╚¢i de autoritatea competent─â....(3)...Ordinul de aprobare a pre╚¢urilor ╚Öi tarifelor reglementate pentru energia electric─â, precum ╚Öi cel de aprobare a metodologiilor de calcul al acestora, aplicabile clien╚¢ilor finali ╚Öi activit─â╚¢ilor cu caracter de monopol natural, se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I. Ordinul va con╚¢ine ╚Öi data intr─ârii lor ├«n vigoare....(4)...Principiile ce stau la baza stabilirii pre╚¢urilor ╚Öi tarifelor reglementate pentru activit─â╚¢ile desf─â╚Öurate ├«n sectorul energiei electrice, exclusiv pre╚¢urile pentru energia electric─â ╚Öi termic─â produs─â ├«n centrale de cogenerare de ├«nalt─â eficien╚¢─â ce beneficiaz─â de scheme de sprijin, au ├«n vedere urm─âtoarele:a)pre╚¢urile/tarifele trebuie s─â fie nediscriminatorii, bazate pe criterii obiective ╚Öi determinate ├«ntr-o manier─â transparent─â, pe baza metodologiilor aprobate ╚Öi publicate de autoritatea competent─â;...b)pre╚¢urile/tarifele energiei electrice trebuie s─â acopere costurile justificate economic;...c)pre╚¢urile/tarifele trebuie s─â asigure o rat─â rezonabil─â a rentabilit─â╚¢ii capitalului investit, stabilit─â conform reglement─ârilor emise de autoritatea competent─â;...d)tarifele ╚Öi pre╚¢urile reglementate pentru clien╚¢ii finali trebuie s─â permit─â acestora alegerea tipului de pre╚¢/tarif care le este mai favorabil, dintre cele practicate de furnizor, ├«n condi╚¢iile stabilite de autoritatea competent─â;...e)tarifele de racordare trebuie s─â con╚¢in─â numai costurile efective de realizare a lucr─ârilor de racordare a clien╚¢ilor la re╚¢eaua electric─â;...f)metodologiile de calcul al tarifelor de transport ╚Öi distribu╚¢ie trebuie s─â con╚¢in─â elemente stimulative pe termen scurt ╚Öi lung privind cre╚Öterea eficien╚¢ei energetice, a siguran╚¢ei ├«n alimentare, integrarea armonioas─â ├«n pia╚¢─â, precum ╚Öi sprijinirea activit─â╚¢ii de cercetare aferente domeniului de activitate;...g)tarifele de transport ╚Öi de distribu╚¢ie sunt nediscriminatorii ╚Öi reflect─â costurile justificate ale operatorilor, ╚¢in├ónd cont ╚Öi de costurile de re╚¢ea marginale evitate pe termen lung, ca urmare a producerii distribuite ╚Öi a m─âsurilor de gestionare a cererii.......(5)...Este interzis─â subven╚¢ia ├«ncruci╚Öat─â ├«ntre activit─â╚¢ile reglementate, precum ╚Öi ├«ntre activit─â╚¢ile reglementate ╚Öi cele nereglementate ale unui operator economic. ├Än cazul producerii de energie electric─â ╚Öi termic─â ├«n cogenerare de ├«nalt─â eficien╚¢─â sau din surse regenerabile de energie care beneficiaz─â de scheme de sprijin instituite la nivel na╚¢ional, subven╚¢ia ├«ncruci╚Öat─â se analizeaz─â ├«ntre activitatea de producere ├«n cogenerare sau din surse regenerabile de energie care beneficiaz─â de schema de sprijin ╚Öi restul activit─â╚¢ilor desf─â╚Öurate de operatorul economic....(6)...Furnizorului de ultim─â instan╚¢─â i se recunosc integral ├«n tarife costurile de achizi╚¢ie a energiei electrice ╚Öi costurile asociate, precum ╚Öi o cot─â rezonabil─â de profit....(7)...Principiile care stau la baza stabilirii pre╚¢urilor reglementate pentru energia electric─â ╚Öi termic─â produs─â ├«n centrale de cogenerare de ├«nalt─â eficien╚¢─â sau din surse regenerabile ce beneficiaz─â de scheme de sprijin au ├«n vedere urm─âtoarele:a)pentru energia electric─â ╚Öi termic─â produs─â ├«n cogenerare de ├«nalt─â eficien╚¢─â sau din surse regenerabile se aplic─â prevederile schemelor de sprijin, aprobate prin hot─âr├óre a Guvernului;...b)pentru energia termic─â produs─â ├«n capacit─â╚¢i de producere termice amplasate ├«ntr-o central─â electric─â de cogenerare ce beneficiaz─â de scheme de sprijin, pre╚¢urile se stabilesc pe baza metodologiei aprobate de autoritatea competent─â....', NULL, 79, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1456, 5, '81', 'Articolul 81', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Recuperarea costurilor...(1)...├Än cazul ├«n care este impus─â respectarea unor condi╚¢ii legale de ├«ndeplinire a obliga╚¢iilor de serviciu public ╚Öi de serviciu universal, participan╚¢ii la pia╚¢a de energie electric─â au dreptul de a-╚Öi recupera costurile rezultate din aplicarea acestor condi╚¢ii....(2)...Recuperarea acestor costuri se va face conform reglement─ârilor emise de autoritatea competent─â sau prin hot─âr├óre a Guvernului, ├«n func╚¢ie de modalitatea de impunere a obliga╚¢iilor de serviciu public, conform art. 16 alin. (2).', NULL, 81, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1457, 5, '82', 'Articolul 82', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Eviden╚¢ierea costurilor ├«n producerea combinat─â a energiei electrice ╚Öi termice...Operatorii economici care produc combinat energie electric─â ╚Öi termic─â ╚Öi care comercializeaz─â cel pu╚¢in una dintre acestea vor repartiza costurile ├«ntre cele dou─â forme de energie, potrivit reglement─ârilor aprobate de autoritatea competent─â.', NULL, 82, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1458, 5, '83', 'Articolul 83', NULL, 'Energia electric─â', NULL, 'Pre╚¢uri ╚Öi tarife Tipurile de pre╚¢uri ╚Öi tarife.', NULL, NULL, NULL, NULL, 'Serviciile conexe...Serviciile prestate ter╚¢ilor de c─âtre operatorii economici care produc energie hidroelectric─â ╚Öi care au ├«n administrare, concesiune sau ├«nchiriere baraje, diguri ╚Öi lacuri de acumulare se realizeaz─â pe baza contractelor ├«ncheiate cu beneficiarii.', NULL, 83, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1459, 5, '84', 'Articolul 84', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Dispunerea efectu─ârii investiga╚¢iilor...Pre╚Öedintele autorit─â╚¢ii competente dispune, prin decizie, efectuarea de investiga╚¢ii, potrivit prevederilor art. 86, de c─âtre personalul propriu ├«mputernicit ├«n acest sens, din oficiu sau ca r─âspuns la o pl├óngere ├«nregistrat─â la autoritatea competent─â, formulat─â de c─âtre o persoan─â fizic─â ori juridic─â afectat─â ├«n mod real ╚Öi direct de o poten╚¢ial─â ├«nc─âlcare a prevederilor prezentului titlu, numai ├«n domeniile ├«n care ANRE are competen╚¢a de investigare potrivit legii.', NULL, 84, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1460, 5, '85', 'Articolul 85', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Solicitarea informa╚¢iilor ╚Öi documentelor...├Än realizarea investiga╚¢iilor, precum ╚Öi a competen╚¢elor conferite ├«n baza prezentului titlu, autoritatea competent─â poate solicita operatorilor economici informa╚¢iile ╚Öi documentele care sunt necesare, men╚¢ion├ónd baza legal─â ╚Öi scopul solicit─ârii, ╚Öi poate stabili termene p├ón─â la care aceste informa╚¢ii ╚Öi documente s─â ├«i fie furnizate.', NULL, 85, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1461, 5, '86', 'Articolul 86', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Drepturi de investigare...(1)...Pentru investigarea ├«nc─âlc─ârii prevederilor prezentului titlu ├«n condi╚¢iile art. 84, personalul ANRE ├«mputernicit ├«n acest sens are urm─âtoarele drepturi:a)s─â ├«ntre ├«n spa╚¢iile, terenurile sau mijloacele de transport pe care operatorii economici le de╚¢in legal;...b)s─â examineze orice documente, registre, acte financiar-contabile ╚Öi comerciale sau alte eviden╚¢e legate de activitatea operatorilor economici, indiferent de locul ├«n care sunt depozitate;...c)s─â cear─â oric─ârui reprezentant sau angajat al operatorului economic explica╚¢ii cu privire la faptele ori documentele legate de obiectul ╚Öi scopul investiga╚¢iei ╚Öi s─â consemneze sau s─â ├«nregistreze r─âspunsurile acestora;...d)s─â ridice sau s─â ob╚¢in─â, ├«n orice form─â, copii ori extrase din orice documente, registre, acte financiar-contabile ╚Öi comerciale sau din alte eviden╚¢e legate de activitatea operatorului economic;...e)s─â sigileze orice amplasament destinat activit─â╚¢ilor operatorului economic ╚Öi orice documente, registre, acte financiar-contabile ╚Öi comerciale sau alte eviden╚¢e legate de activitatea operatorului economic, pe durata ╚Öi ├«n m─âsura necesar─â investig─ârii.......(2)...Autoritatea competent─â va proceda la ac╚¢iunile prev─âzute la alin. (1) numai dac─â exist─â indicii c─â pot fi g─âsite documente sau pot fi ob╚¢inute informa╚¢ii considerate necesare pentru ├«ndeplinirea competen╚¢elor sale, iar rezultatul investiga╚¢iei va fi consemnat ├«ntr-un proces-verbal de constatare ╚Öi inventariere....(3)...Autoritatea competent─â poate face inspec╚¢ii inopinate ╚Öi poate solicita orice fel de informa╚¢ii sau justific─âri legate de ├«ndeplinirea competen╚¢elor de investigare, at├ót la fa╚¢a locului, c├ót ╚Öi prin convocare la sediul autorit─â╚¢ii competente.', NULL, 86, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1462, 5, '87', 'Articolul 87', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Autorizarea judiciar─â a investiga╚¢iilor...├Än baza autoriz─ârii judiciare date prin ├«ncheiere, conform art. 88, personalul ANRE, ├«mputernicit ├«n condi╚¢iile art. 84, poate efectua inspec╚¢ii ├«n orice alte spa╚¢ii, inclusiv domiciliul, terenuri sau mijloace de transport apar╚¢in├ónd conduc─âtorilor, administratorilor, directorilor ╚Öi altor angaja╚¢i ai operatorilor economici ori asocia╚¢iilor de operatori economici supu╚Öi investiga╚¢iei.', NULL, 87, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1463, 5, '88', 'Articolul 88', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Ob╚¢inerea autoriz─ârii judiciare...(1)...Personalul ANRE efectueaz─â inspec╚¢ii, ├«n conformitate cu prevederile art. 87, numai ├«n baza deciziei de ├«mputernicire emise de c─âtre pre╚Öedintele autorit─â╚¢ii competente ╚Öi cu autorizarea judiciar─â dat─â prin ├«ncheiere de c─âtre pre╚Öedintele Cur╚¢ii de Apel Bucure╚Öti sau de c─âtre un judec─âtor delegat de acesta. O copie certificat─â a deciziei de ├«mputernicire ╚Öi a autoriza╚¢iei judiciare se comunic─â obligatoriu persoanei supuse inspec╚¢iei, ├«nainte de ├«nceperea acesteia....(2)...Cererea de autorizare se judec─â ├«n camera de consiliu, f─âr─â citarea p─âr╚¢ilor. Judec─âtorul se pronun╚¢─â asupra cererii de autorizare ├«n termen de cel mult 48 de ore de la data ├«nregistr─ârii cererii. ├Äncheierea se motiveaz─â ╚Öi se comunic─â autorit─â╚¢ii competente, ├«n termen de cel mult 48 de ore de la pronun╚¢are....(3)...├Än cazul ├«n care inspec╚¢ia trebuie desf─â╚Öurat─â simultan ├«n mai multe spa╚¢ii dintre cele prev─âzute la art. 87, autoritatea competent─â va introduce o singur─â cerere, instan╚¢a pronun╚¢├óndu-se printr-o ├«ncheiere ├«n care se vor indica spa╚¢iile ├«n care urmeaz─â s─â se desf─â╚Öoare inspec╚¢ia....(4)...Cererea de autorizare trebuie s─â cuprind─â toate informa╚¢iile de natur─â s─â justifice inspec╚¢ia, iar judec─âtorul sesizat este ╚¢inut s─â verifice dac─â cererea este ├«ntemeiat─â....(5)...Oricare ar fi ├«mprejur─ârile, inspec╚¢ia se desf─â╚Öoar─â ├«ntre orele 8,00 ╚Öi 18,00 ╚Öi trebuie efectuat─â ├«n prezen╚¢a persoanei la care se efectueaz─â inspec╚¢ia sau a reprezentantului s─âu. Inspec╚¢ia poate continua ╚Öi dup─â ora 18,00 numai cu acordul persoanei la care se efectueaz─â inspec╚¢ia sau a reprezentantului s─âu....(6)...Inventarele ╚Öi punerile de sigilii se fac conform dispozi╚¢iilor Codului de procedur─â penal─â....(7)...├Äncheierea prev─âzut─â la alin. (1) poate fi atacat─â cu recurs la ├Änalta Curte de Casa╚¢ie ╚Öi Justi╚¢ie, ├«n termen de 48 de ore. Termenul de recurs pentru autoritatea competent─â curge de la momentul comunic─ârii ├«ncheierii, potrivit prevederilor alin. (2). ├Än ceea ce prive╚Öte persoana supus─â inspec╚¢iei, termenul de recurs curge de la momentul comunic─ârii ├«ncheierii, potrivit prevederilor alin. (1). Recursul nu este suspensiv de executare....(8)...Pre╚Öedintele Cur╚¢ii de Apel Bucure╚Öti sau judec─âtorul delegat de acesta are competen╚¢a s─â emit─â autoriza╚¢ia judiciar─â ├«n vederea efectu─ârii inspec╚¢iei, potrivit art. 87. Instan╚¢a de judecat─â verific─â dac─â decizia de ├«mputernicire emis─â de c─âtre pre╚Öedintele ANRE este autentic─â ╚Öi dac─â m─âsurile coercitive preconizate nu sunt nici arbitrare ╚Öi nici excesive, av├ónd ├«n vedere, ├«n special, gravitatea ├«nc─âlc─ârii suspectate, importan╚¢a elementelor de prob─â c─âutate, implicarea ├«ntreprinderii ├«n cauz─â ╚Öi probabilitatea rezonabil─â ca registrele ╚Öi documentele privind activitatea, care au leg─âtur─â cu obiectul inspec╚¢iei, s─â fie p─âstrate ├«n incintele pentru care se solicit─â autoriza╚¢ia. Instan╚¢a de judecat─â poate solicita ANRE explica╚¢ii detaliate privind elementele care ├«i sunt necesare pentru a-i permite s─â verifice propor╚¢ionalitatea m─âsurilor coercitive preconizate.', NULL, 88, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1464, 5, '89', 'Articolul 89', NULL, 'Energia electric─â', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Accesul la documente ╚Öi informa╚¢ii...(1)...Organele administra╚¢iei publice centrale ╚Öi locale, precum ╚Öi orice alte institu╚¢ii ╚Öi autorit─â╚¢i publice sunt obligate s─â permit─â autorit─â╚¢ii competente accesul la documentele, datele ╚Öi informa╚¢iile de╚¢inute de acestea, cu respectarea dispozi╚¢iilor legale....(2)...Autoritatea competent─â, primind acces la documentele, datele ╚Öi informa╚¢iile men╚¢ionate la alin. (1), are obliga╚¢ia de a respecta caracterul datelor ╚Öi informa╚¢iilor clasificate atribuit legal respectivelor documente, date ╚Öi informa╚¢ii.', NULL, 89, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1467, 5, '92', 'Articolul 92', NULL, 'Energia electric─â', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Infrac╚¢iuni...(1)...Deteriorarea, modificarea f─âr─â drept sau blocarea func╚¢ion─ârii echipamentului de m─âsurare a energiei electrice livrate ori modificarea f─âr─â drept a componentelor instala╚¢iilor energetice constituie infrac╚¢iune ╚Öi se pedepse╚Öte cu ├«nchisoare de la 3 luni la 2 ani sau cu amend─â....(2)...Executarea sau folosirea de instala╚¢ii clandestine ├«n scopul racord─ârii directe la re╚¢ea sau pentru ocolirea echipamentelor de m─âsurare constituie infrac╚¢iune ╚Öi se pedepse╚Öte cu ├«nchisoare de la 6 luni la 3 ani sau cu amend─â....(3)...├Än cazul ├«n care infrac╚¢iunile prev─âzute la alin. (1) ╚Öi (2) sunt s─âv├ór╚Öite de un salariat al unui titular de licen╚¢─â, limitele speciale se majoreaz─â cu jum─âtate....(4)...Tentativa la infrac╚¢iunile prev─âzute la alin. (1) ╚Öi (2) se pedepse╚Öte.', NULL, 92, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1468, 5, '93', 'Articolul 93', NULL, 'Energia electric─â', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Contraven╚¢ii...(1)...Constituie contraven╚¢ii urm─âtoarele fapte:...1. desf─â╚Öurarea de c─âtre persoane fizice ori juridice de activit─â╚¢i ╚Öi/sau prest─âri de servicii ├«n sectorul energiei electrice f─âr─â a de╚¢ine licen╚¢─â ori autoriza╚¢ie de ├«nfiin╚¢are conform prevederilor prezentei legi ╚Öi ale regulamentului prev─âzut la art. 9 alin. (4);...2. proiectarea, executarea ╚Öi verificarea instala╚¢iilor electrice f─âr─â atestat ╚Öi/sau autoriza╚¢ie;...3. nerespectarea normelor tehnice emise de c─âtre ANRE;...4. nerespectarea condi╚¢iilor de valabilitate asociate licen╚¢elor/atestatelor/autoriza╚¢iilor emise de ANRE;...5. ├«nc─âlcarea de c─âtre participan╚¢ii la pia╚¢a de energie electric─â a reglement─ârilor tehnice ╚Öi/sau comerciale emise de ANRE;...6. refuzul de a permite efectuarea de ac╚¢iuni de control dispuse de ANRE, precum ╚Öi obstruc╚¢ionarea acesteia ├«n ├«ndeplinirea atribu╚¢iilor sale;...7. nefurnizarea/neprezentarea datelor, documentelor ╚Öi/sau a informa╚¢iilor solicitate ├«n termenele stabilite de ANRE sau furnizarea/prezentarea incomplet─â ori eronat─â a acestora, precum ╚Öi/sau nerealizarea m─âsurilor dispuse de aceasta;...8. refuzul nejustificat de a acorda acces oric─ârui solicitant la re╚¢ele de interes public;...9. furnizarea de c─âtre operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem a unor informa╚¢ii incomplete ori eronate necesare pentru accesul la re╚¢ea;...10. neasigurarea de c─âtre operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem a unui tratament nediscriminatoriu ├«ntre utilizatorii re╚¢elei electrice;...11. ├«nt├órzierea nejustificat─â ├«n racordarea/alimentarea cu energie electric─â a clien╚¢ilor sau ├«n realimentarea acestora dup─â ├«ntrerupere, precum ╚Öi propunerea unor solu╚¢ii de racordare, altele dec├ót cele optime din punct de vedere tehnic ╚Öi cu costuri minime, conform actelor normative ├«n vigoare ╚Öi reglement─ârilor emise de ANRE;...12. punerea sub tensiune a instala╚¢iilor electrice f─âr─â a exista ├«ncheiat contract de furnizare a energiei electrice, precum ╚Öi/sau f─âr─â a exista montat un grup de m─âsurare a energiei electrice, dup─â caz;...13. necomunicarea unei oferte privind condi╚¢iile de furnizare a energiei electrice solicitan╚¢ilor, conform prevederilor art. 58 alin. (1);...14. sistarea nejustificat─â a aliment─ârii cu energie electric─â a clien╚¢ilor;...15. nerespectarea prevederilor standardelor de performan╚¢─â referitoare la serviciile publice de transport ╚Öi distribu╚¢ie, precum ╚Öi la serviciul universal din sectorul energiei electrice;...16. nerespectarea prevederilor clauzelor obligatorii din contractele-cadru emise de ANRE;...17. nerespectarea prevederilor legale privind schimbarea furnizorului de energie electric─â;...18. divulgarea informa╚¢iilor sensibile din punct de vedere comercial privind opera╚¢iunile comerciale de c─âtre operatorul de transport ╚Öi de sistem, de operatorii de distribu╚¢ie sau de operatorul pie╚¢ei de energie electric─â, inclusiv a celor privind toate tranzac╚¢iile din cadrul contractelorde furnizare de energie electric─â ╚Öi/sau al instrumentelor financiare derivate din sectorul energiei electrice, cu excep╚¢ia cazului ├«n care acestea sunt solicitate de autorit─â╚¢ile competente;...19. nerespectarea cerin╚¢elor de independen╚¢─â a operatorului de distribu╚¢ie ori a operatorului de transport ╚Öi de sistem ╚Öi/sau a reglement─ârilor privind controlul direct ori indirect asupra unor ├«ntreprinderi din sectorul producerii, furniz─ârii, distribu╚¢iei sau transportului de energie electric─â;...20. nerespectarea de c─âtre furnizorul de ultim─â instan╚¢─â a obliga╚¢iei de a asigura serviciul universal clien╚¢ilor, conform dispozi╚¢iilor prezentului titlu;...21. nerespectarea de c─âtre produc─âtori a obliga╚¢iilor care le revin potrivit art. 6 lit. p) ╚Öi art. 28;...22. nerespectarea de c─âtre operatorul de transport ╚Öi de sistem a reglement─ârilor privind ordinea de merit ├«n pia╚¢a de echilibrare, privind dispecerizarea instala╚¢iilor de producere ╚Öi/sau utilizarea interconexiunilor cu celelalte sisteme;...23. neprezentarea de c─âtre operatorul de transport ╚Öi de sistem, ├«n vederea aprob─ârii de c─âtre ANRE, a planului de dezvoltare a re╚¢elei de transport, la termenele stabilite prin reglement─ârile ├«n vigoare;...24. practicarea de pre╚¢uri sau tarife neaprobate de c─âtre ANRE, pentru activit─â╚¢ile reglementate;...25. nerespectarea reglement─ârilor emise de ANRE privind stabilirea pre╚¢urilor ╚Öi tarifelor;...26. nerespectarea reglement─ârilor privind separarea contabil─â a activit─â╚¢ilor desf─â╚Öurate de c─âtre operatorii economici titulari de licen╚¢─â ├«n sectorul energiei electrice;...27. nepublicarea ╚Öi neactualizarea, pe pagina proprie de web, a datelor tehnice ╚Öi comerciale prev─âzute de reglement─âri de c─âtre participan╚¢ii la pia╚¢a de energie electric─â;...28. refuzul clien╚¢ilor finali de a permite accesul la grupurile de m─âsurare, precum ╚Öi la instala╚¢iile aflate ├«n patrimoniu ╚Öi/sau ├«n gestiunea operatorului de distribu╚¢ie ori a operatorului de transport ╚Öi de sistem;...29. executarea de s─âp─âturi sau de lucr─âri de orice fel ├«n zonele de protec╚¢ie a instala╚¢iilor f─âr─â consim╚¢─âm├óntul prealabil al titularului acestora;...30. interzicerea/├«mpiedicarea de c─âtre persoane fizice sau juridice a exercit─ârii drepturilor prev─âzute de prezenta lege, de acces, uz ╚Öi servitute ori de restr├óngere a unor activit─â╚¢i, cu ocazia efectu─ârii lucr─ârilor de retehnologizare, repara╚